css:
@-moz-document url-prefix("https://www.facebook.com/") {
/* Default Santa Style */  
body>#blueBar       {background: none !important;}
.jewel, .jewelBox   {border: none !important; }
.jewelBox           {background-color: rgba(255,255,255,0.9) !important;}
#headNavOut         {background: none !important; border: none !important;}
#pageNav a          {color: #111 !important;}
#pageNav a:hover    {color: #333 !important; background: none !important;}
#navAccount         {border: none !important;}
#navAccount ul      {background-color: rgba(255,255,255,0.9) !important;  border: none !important;}
#contentCol,#left_column,#right_column                  {background-color: rgba(255,255,255,0.4) !important;  border: none !important;}
.inputtext,.add_comment_text,.uiMorePager, .box_header  {background-color: rgba(255,255,255,0.5) !important;}
.ufi_section, .photo_table, .photo_container            {background: none !important; border: none !important;}
#sidebar_ads, #pagelet_adbox, #ego                      {display: none !important;}
body {background: url('http://goo.gl/mE82us') bottom left #000000  repeat fixed !important;background-size: contain !important}
div#userNavigationLabel, div#privacyFlyoutLabel, div._5ahz {-webkit-filter: invert(50%);}
  div#u_0_1n, div#u_0_2h, div#u_0_1o        {color : rgb(0,0,255); background: rgba(255,255,255, 0.5);}
  div._5rzs, div#leftCol                    {background : rgba(255,255,255, 0.4);}
  div._5qqe, div#pagelet_canvas_nav_content {background : #333 ; opacity : 0.1;}  
  div._5rzs, div._4-u2.mbm._5v6e, div#u_0_0 {background : rgba(255,255,255, 0.4);}
#blueBarHolder #blueBar, #blueBarNAXAnchor        {background: #222 !important;}
div#blueBarNAXAnchor._4f7n._xxp.fixed_elem::after {background: #333 !important;}
.fbDockChatTabFlyout .fbNubFlyoutBody {background: url("http://goo.gl/j2OKQb") top left no-repeat #000000!important;    background-size: 240px 100% !important;    border-color: #000000 !important;
}.uiSideNav .sideNavItem .hasCount:hover,
.uiSideNav .sideNavItem .noCount:hover{
   text-shadow: 2px 2px 2px rgba(57, 255, 20, 0.75) !important;
   color: #ff1414 !important;
}.fbbody  {color: blue;}
/* Default Santa Style */  
.fbbody a  {color: blue;}

}
