css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("joy.reactor.cc") {
  #container {
    background-image: none;
    background-color: #ffc438;
    padding-top: 30px;
  }
  
  #page {
    margin: 0 auto;
  }
  
  .topbar_wr, .topbar_wr a {
    line-height: 30px;
    height: 30px;
    background-color: #392f1e;
  }
  
  .swf_switcher {
    padding: 0 20px;
    text-transform: uppercase;
  }
  
  .sswither {
    margin: 4px 10px;
    width: 40px;
    height: 22px;
    border-radius: 11px;
    overflow: hidden;
  }
  
  .sswither span {
    width: 12px;
    height: 12px;
  }
  
  .icon_mail {
    background-position: center center;
    vertical-align: top;
  }
  
  #header {
    height: 92px;
  }
  
  #header .logo {
    padding: 10px 0 0;
  }
  
  #header .description {
    top: 59px;
  }
  
  #navcontainer, #searchBar, #pageinner {
    padding: 10px 20px;
    height: auto;
  }
  
  #navcontainer ul, #navcontainer ul li, #navcontainer ul li a, .post_random a {
    height: 30px;
    line-height: 30px;
  }
  
  #navcontainer ul li.current_page_item a, #navcontainer ul li.current_page_ancestor a, #navcontainer ul li#konkurs.current_page_ancestor a {
    border-radius: 15px;
  }
  
  #navcontainer ul li a, .post_random a {
    padding: 0 18px;
  }
  
  #searchmenu {
    height: auto;
    padding: 0 10px;
  }
  
  #s, #searchsubmit {
    height: 26px;
  }
  
  #content {
    width: 900px;
    margin-bottom: 0;
  }
  
  .addpost_label {
    font-weight: bold;
    font-size: 20px;
  }
  
  input[type="submit"], input[type="button"], button {
    line-height: 30px;
    border-radius: 15px;
  }
  
  #add_post textarea {
    height: 100px;
  }
  
  #add_post > div > div.add_post_footer {
    margin-bottom: 20px;
  }
  
  .article {
    margin: 0 0 20px;
  }
  
  .article .post_content_expand, #tagArticle .post_content_expand {
    height: auto;
  }
  
  .article .post_content_expand span, #tagArticle .post_content_expand span {
    line-height: 30px;
  }
  
  .article .uhead {
    height: auto;
    margin-bottom: 6px;
  }
  
  .article .uhead .avatar {
    width: 30px;
    height: 30px;
  }
  
  .taglist a {
    font-size: 0.8em;
    font-weight: normal;
    margin-right: 6px;
  }
  
  .commentnum {
    height: 30px;
    line-height: 26px !important;
  }
  
  .article .ufoot span.comments {
    margin-right: 20px;
  }
  
  .article .ufoot .ufoot_first {
    line-height: inherit;
  }
  
  .comment {
    padding: 5px;
    margin-bottom: 5px;
  }
  
  .comments_bottom {
    margin-top: 6px;
  }
  
  .comments_bottom .avatar {
    width: 30px;
    height: 30px;
  }
  
  .article .ufoot span, .article .ufoot span a, .article .ufoot span a.response, .article .ufoot .txt span.comment_rating {
    line-height: 30px;
  }
  
  .pagination {
    line-height: 30px;
  }
  
  .pagination_main > * {
    line-height: 30px;
    border-radius: 15px;
  }
  
  .pagination a, .pagination span {
    margin-bottom: 0;
  }
}
