@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("joyreactor.cc") {

/*Замена заднего фона
------------------------------------------------------------------------------*/
#container {
 	background: url("http://www.lirent.net/wp-content/uploads/2014/10/Android-Lollipop-wallpapers-p.png");
  	background-size: 100% !important;
  	background-attachment: fixed !important;
} 

}

@-moz-document domain("joyreactor.cc"), domain("reactor.cc") {

/*Уменьшение масштаба сайта на 5%, если не нужно закомментируйте или удалите
------------------------------------------------------------------------------*/

#container:before,
#container:after {
 	background: none !important; 
}

/*Изменение стиля поста и формы 'новый пост'
------------------------------------------------------------------------------*/
#contentinner form {
    padding: 20px;
}

.postContainer {
    padding: 10px;
    border-bottom: 1px solid #ccc;
}

#post_list .postContainer:last-child {
  border: 0;
}

.postContainer,
#contentinner > form {
    background: #fff !important;
            box-shadow: 0 1px 3px 0 rgba(0,0,0,0.12),0 1px 2px 0 rgba(0,0,0,0.24);    
    -webkit-box-shadow: 0 1px 3px 0 rgba(0,0,0,0.12),0 1px 2px 0 rgba(0,0,0,0.24);
       -moz-box-shadow: 0 1px 3px 0 rgba(0,0,0,0.12),0 1px 2px 0 rgba(0,0,0,0.24);
}

#contentinner > form {
    margin-bottom: 20px;
}

#blogManagementLinks {
	background: none !important;  
}
  
.article,
.add_post_footer {
    margin-bottom: 0;  
}

#add_post>div>div.add_post_footer {
    margin: 10px 0 20px !important;
    overflow: visible !important;
}

/*Изменение цвета контейнера 
------------------------------------------------------------------------------*/
#pageinner {
    background: #eee !important; 
}

.week_top,
.user.week_top .userposition,
.user.week_top a,
.user .weekrating {
    background-image: none !important; 
    background: none !important; 
}

/*Изменение стиля кнопок навигации
------------------------------------------------------------------------------*/
.pagination .current,
.pagination .next,
.pagination .prev,
#submit_button,
.addcomment .submit {
            box-shadow: 0 1px 3px 0 rgba(0,0,0,0.12),0 1px 2px 0 rgba(0,0,0,0.24);
    -webkit-box-shadow: 0 1px 3px 0 rgba(0,0,0,0.12),0 1px 2px 0 rgba(0,0,0,0.24);
       -moz-box-shadow: 0 1px 3px 0 rgba(0,0,0,0.12),0 1px 2px 0 rgba(0,0,0,0.24);  
}

