.vote-plus:hover {
    background-color: #9fd831 !important;         
}
    
.vote-minus:hover {
    background-color: #f03f28 !important;         
}

.comment_rating div.vote-plus,
.comment_rating div.vote-minus {
    background-color: #cecece !important;     
}

.comment_rating div.vote-plus:hover,
.comment_rating div.vote-minus:hover {
    background-color: #b6b6b6 !important;     
}

/*Стили поля для поиска
------------------------------------------------------------------------------*/
#searchmenu {
    padding: 0 10px;
    border-radius: 0;
    border: none;
    border-bottom: 2px solid #857456;
}

input#searchsubmit {
    background-image: none !important;
    width: 17px;
    -webkit-mask: url(data:image/svg+xml;base64,) 50% 50% / 23px 30px no-repeat;
         -o-mask: url(data:image/svg+xml;base64,) 50% 50% / 23px 30px no-repeat;
        -ms-mask: url(data:image/svg+xml;base64,) 50% 50% / 23px 30px no-repeat;
    background-color: #F5F5F5;
}

#page {
  margin-bottom: 0;
}
div.pp_overlay {
    width: 100% !important;
}

/*Стили подменю шапки
------------------------------------------------------------------------------*/
.submenuitem.active {
	position: relative;
}

#submenu div.submenuitem.active a {
  color: #000;
  font-size: 15px;
  margin-bottom: 9px;
  background: none;
}

#pageinner {
  position: initial;
}

#submenu a, #submenu b {
  color: #717171;
}

.submenuitem.active:after {
  content: " ";
  border-left: 13px solid transparent;
  border-right: 13px solid transparent;
  border-top: 13px solid #fdda97;
  width: 0;
  height: 0;
  position: absolute;
  left: 35%;
  top: 98%;
}

.pagination {
    margin-top: 20px;
}

/*Иконка приватных сообщений
------------------------------------------------------------------------------*/
.icon_mail {
    width: 21px !important;
    background-image: url(https://a-v2.sndcdn.com/assets/images/header/messages-f517d0.svg) !important;
}

/*Скрыть меню внизу страницы
------------------------------------------------------------------------------*/
#tagList {
    display: none;
}

/*Спрятать баннер и рекламу снизу
------------------------------------------------------------------------------*/
#pageinner > :nth-child(6),
li > .sidebarContent,
#pageinner > :nth-child(4),
#post_list > :last-child {
	display: none;    
}
  
}
