css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("https?:\/\/instasynch?\.com(?:\/(?:r|pages)(?:\/.*|\/)?|\/)?") {
.navbar {
    position: absolute !important;
}
a {
    color: #36C;
}
a:hover, a:active {
    color: #36C;
}
.fa, .room-container .room-component-row .video-controls .nav-tabs li a, .glyphicon {
    color: #b9b9b9;
}
.fa:hover, .fa:active, .glyphicon:hover, .glyphicon:active {
    color: #ccc;
}
body{
    color:#000 !important;
    background-color:#fff !important;
}   
.chat #tabs_chat_content .chat-messages {
    background-color: #fff !important;
}
body.dim .navbar-default, .navbar-default {
    background-image: linear-gradient(to bottom, rgba(68, 68, 68, 0.6) 0%, rgba(34, 34, 34, 0.6) 100%);
    background-color: transparent !important;
}
body.dim, body {
    background-image: url(http://i.imgur.com/3De82yp.jpg);
    background-color: transparent;
}
.navbar-default .navbar-brand {
    color: transparent !important;
    text-shadow: none !important;
    background-image: url(http://i.imgur.com/AfmxaI2.png);
    background-size: contain;
    background-repeat: no-repeat;
    background-position: center
}
.nav > li > a:hover, .nav > li > a:focus {
    text-decoration: none;
}
.navbar-nav > li > a {
    background-color: #2284B5;
    margin-left: 5px;
    color: #fff !important;
    padding: 5px 20px;
}
.navbar-default .navbar-nav > li > a {
    background-color: #2284B5 !important;
}
.navbar-nav > li > .dropdown-menu {
    margin-left: 5px;
}    
.navbar-nav > li > a:hover, .navbar-nav > li > a:active {
    transition: background-color 0.2s ease-in-out 0s;
    background-color: #196F9A !important;
}
.navbar-default .navbar-nav > .open > a, .navbar-default .navbar-nav > .open > a:hover, .navbar-default .navbar-nav > .open > a:focus {
    background-color: #196F9A !important;
}
.navbar-nav > li > a {
    text-shadow: none !important;
    margin-top: 7px;
}

.container > .navbar-header, .container-fluid > .navbar-header, .container > .navbar-collapse, .container-fluid > .navbar-collapse {
    margin-left: 15px;
    margin-right: 15px;
}

.navbar {
    border-bottom-color: #545454;
}    
.text-info {
    color: #7882BF;
}    
.room-container .room-component-row .video-controls {
    border-right: 1px solid #444 !important;
}
.room-container .room-component-row .chat .chat-tabs li a {
    border-color: #444;
    border-bottom-color: transparent;
}
.room-container .room-component-row .chat #tabs_chat_content .user-list {
    background-color: rgba(255,255,255, 0.8);
}
.room-container .room-component-row .chat #tabs_chat_content .user-list li:hover{
    background-color: #D1E1FA;
    text-decoration: none;
}
.room-container .room-component-row .chat #tabs_chat_content .user-list li.mod:active {
    color: #C9421D;
}
.room-container .room-component-row .chat .chat-tabs li.tabs-users.visible, .room-container .room-component-row .chat .chat-tabs li.tabs-users:hover {
    background-color: #555;
}
.room-container .room-component-row .video-controls .tab-content #tabs_playlist .playlist-stats div {
    color: #BBB;
}
.nav-tabs {
    background-color: #444 !important;
    border-bottom-width: 2px !important;
}
.nav-tabs > li.active > a, .nav-tabs > li.active > a:hover, .nav-tabs > li.active > a:focus, .nav-tabs > li > a, .nav-tabs > li > a:hover, .nav-tabs > li > a:active {
    border: none !important;
}
.nav-tabs > li.active > a, .nav-tabs > li.active > a:hover, .nav-tabs > li.active > a:focus {
    background-color: transparent !important;
}
.nav > li > a:hover, .nav > li > a:focus {
    background-color: transparent;
}
.nav-tabs > li {
    margin-bottom: 0px !important;
}
.nav-tabs > li.active > a, .nav-tabs > li.active > a:hover, .nav-tabs > li.active > a:focus, body.dim .room-container .room-component-row .video-controls .tab-content, .room-container .room-component-row .video-controls .tab-content #tabs_playlist .playlist li, .nav .open > a, .nav .open > a:hover, .nav .open > a:focus, .room-container .room-component-row .video-controls .tab-content #tabs_playlist_history .playlist li {
    background-color: #444 !important;
}
.room-container .room-component-row .video-controls .tab-content #tabs_playlist .playlist li, .room-container .room-component-row .video-controls .tab-content #tabs_playlist_history .playlist li {
    border-radius: 0px !important;
    border-color: #808080 !important;
}    
.room-container .room-component-row .video-controls .nav-tabs li.skip-controls #skip_counter {
    color: #FFF;
}
.room-container .room-component-row .video-controls .tab-content #tabs_playlist .playlist, .room-container .room-component-row .video-controls .tab-content #tabs_playlist_history .playlist {
    background-color: #444 !important;
}
.room-container .room-component-row .video-controls .tab-content #tabs_playlist .playlist li .title, .room-container .room-component-row .video-controls .tab-content #tabs_playlist_history .playlist li .title {
    color: rgb(232,232,232) !important;
}
.room-container .room-component-row .video-controls .tab-content #tabs_playlist .playlist li.active, .room-container .room-component-row .video-controls .tab-content #tabs_playlist .playlist li.active .title, .room-container .room-component-row .video-controls .tab-content #tabs_playlist .playlist li.active:hover, .room-container .room-component-row .video-controls .tab-content #tabs_playlist .playlist li.active .pl-video-info .duration {
    background-color: #D1E1FA !important;
    color: #222 !important;
}
.room-container .room-component-row .video-controls .tab-content #tabs_playlist .playlist li:hover, .room-container .room-component-row .video-controls .tab-content #tabs_playlist_history .playlist li:hover {
    background-color: #555 !important;
}
.room-container .room-component-row .video-controls .tab-content #tabs_playlist .playlist li .pl-video-info .duration, .room-container .room-component-row .video-controls .tab-content #tabs_playlist_history .playlist li .pl-video-info .duration {
    color: #BBB !important;
}
.room-container .room-component-row .video-controls .tab-content #tabs_playlist .playlist li.active .pl-video-info .duration {
    color: rgb(34,34,34);
}   
.room-container .room-component-row .video-controls .tab-content #tabs_playlist .playlist li.active .buttons i {
    color: rgb(34,34,34) !important;
}    
.no-list-style, .room-container .room-component-row .chat #tabs_chat_content .user-list, .room-container .room-component-row .video-controls .tab-content #tabs_playlist .playlist, .room-container .room-component-row .video-controls .tab-content #tabs_playlist_history .playlist {
    border-width: 2px !important;
}
#poll_tab .poll.active, #poll_column .poll.active {
    box-shadow: none;
    background-color: #DFDFDF;
}
#poll_tab .poll, #poll_column .poll {
    border: 1px solid rgba(0, 0, 0, 0.25);
    background-color: #444;
}
.room-container .room-component-row {
    background-color: transparent !important;
}
.room-container .room-component-row {
    box-shadow: none;    
}
body.dim .footer {
    background-image: none;
}
.footer {
    background-color: rgba(25, 25, 25, 0.6);
}
.footer .footer-nav a {
    color: #C8C8C8;
    text-decoration: underline;
}
.footer .footer-nav a:hover, .footer .footer-nav a:active, .footer .footer-nav span {
    color: #fff;
}
.room-container .room-info-row {
  border: 1px solid #AAA;
  border-radius: 4px;
  background:none;
  background-color: #fff;
  margin: 20px auto;
  width: 70%;
  position: relative;
  left: 15%;
}
.room-container .room-component-row .video-controls .nav-tabs {
    padding: 0px 0px 0px;
}
.room-container .room-component-row .video-controls .tab-content #tabs_playlist .playlist {
    height: 279px !important;
}
#poll_column {
    padding-top: 8px;
}
#create_poll_btn_column {
    margin-bottom: 0px !important;
    margin-top: 4px !important;
}
.open > a {
    text-decoration: none !important;
}
.room-container .room-info-row {
    margin-bottom: 0px;
}
.navbar-nav > li > .dropdown-menu, .nav-tabs .dropdown-menu {
    border-radius: 4px !important;
}
.navbar-default .navbar-nav > .active > a {
    background-image: none;
    background-color: #2284B5;
}
.page-container {
    background-color: rgba(255, 255, 255, 0.65);
    margin-bottom: 5px;
}
.page-container .rooms .room {
    background-color: rgba(255, 255, 255, 0.5)!important;
    box-shadow: 2px 2px rgba(0, 0, 0, 0.3);
    margin: 2px;
    border-radius: 4px;
    padding-top: 16px;
    height: 260px;
    width: 189px;
}
.page-container .home {
    border-color: #999;
}
#poll_column .poll.active .poll-options .poll-option .poll-votes:hover {
    border: 1px solid rgba(0, 0, 0, 1.0);
}
}
@-moz-document regexp("https?:\/\/instasynch?\.com\/r\/[bB][iI][bB][bB][yY].*") {
.navbar-default .navbar-brand {
    background-image: url(http://i.imgur.com/4AyXQt0.png);
    background-size: contain;
    background-position: center 40%;
}    
#poll_column .poll.active {
    background-image: url(http://i.imgur.com/YR9hGSt.png);
    background-size: contain;
    background-position: center center;
    background-repeat: no-repeat    
}
}
