css:
@-moz-document domain("play.pokemonshowdown.com") {
  body {
    background:url("http://i.imgur.com/caq5CVE.jpg?1") no-repeat !important;
    background-color: #ED3F41 !important;
  }
  
  .header {
    background: rgba(24, 34, 44, 0.9);
  }

  .mainmenufooter small:after{
    content:"Made by: Charles Paroissien"; 
    font-size:9pt; 
    color:#cccccc; 
    text-shadow:0 1px black; 
    position:absolute; 
    size:6px; 
    left:230px; 
    top:21px; 
    width: 300px;
  }
  
  a {
    color: #ED3F41;
  }
  
  a:hover {
    color: #D63A3B;
  }
  
  a:visited {
    color: #BC6060;
  }
  
  .maintabbarbottom {
    background: rgba(24, 34, 44, 0.9);
    border: none;
    box-shadow: none;
  }
  
  .tabbar a.button.cur, .tabbar a.button.cur:hover {
    border-radius: 15px;
    background: #ED3F41;
    border: none;
    text-shadow: none;
    color: #182130;
  }
  
  .tabbar li:last-child a.button {
    border-top-right-radius: 15px;
  }
  
  .tabbar li:first-child a.button {
      border-top-left-radius: 15px;
  }
  
  .tabbar a.button {
    border-radius: 15px;
    background: #D63A3B;
    text-shadow: none;
    border: none;
    margin-left: 20px;
  } 
  
  .tabbar a.button:hover {
    border-radius: 15px;
    background: #ED3F41;
  } 
  
  .ps-room.ps-room-light.scrollable {
    background: url("http://i.imgur.com/2EBXsBy.jpg?1") no-repeat right;
    background-color: #D63A3B;
    opacity: .95;
    border: 1px solid #3B3A4A;
    color: #FFF;
  }
  
  .mainmenufooter {
    color: #000;
    text-shadow: none;
  }
  
  .mainmenufooter a {
    color: #000;
    text-shadow: none;
    font-weight: bold;
  }
  
  .mainmenufooter a:hover {
    color: #1A212B;
  }
  
  .mainmenu .menugroup:nth-child(1) .button, .mainmenu .menugroup:nth-child(2) p:nth-child(1) .button, .mainmenu .menugroup:nth-child(2) p:nth-child(2) .button, .mainmenu .menugroup:nth-child(2) p:nth-child(3) .button, .mainmenu .menugroup:nth-child(3) p:nth-child(1) .button, .mainmenu .menugroup:nth-child(3) p:nth-child(2) .button {
    border: 1px solid #000;
    background: rgba(214, 58, 59, 0.9);
    background: -moz-linear-gradient(top, rgba(214, 58, 59, 0.9) 0%, rgba(214, 58, 59, 0.9) 0%, rgba(214, 58, 59, 0.9) 10%, rgba(213, 117, 117, 0.9) 89%, rgba(213, 117, 117, 0.9) 100%);
  }
  
  .mainmenu .menugroup:nth-child(1) .button:hover, .mainmenu .menugroup:nth-child(2) p:nth-child(1) .button:hover, .mainmenu .menugroup:nth-child(2) p:nth-child(2) .button:hover, .mainmenu .menugroup:nth-child(2) p:nth-child(3) .button:hover, .mainmenu .menugroup:nth-child(3) p:nth-child(1) .button:hover, .mainmenu .menugroup:nth-child(3) p:nth-child(2) .button:hover {
    border: 1px solid #000;
    background: rgba(213, 117, 117, 0.9);
    background: -moz-linear-gradient(top, rgba(213, 117, 117, 0.9) 0%, rgba(213, 117, 117, 0.9) 0%, rgba(213, 117, 117, 0.9) 10%, rgba(214, 58, 59, 0.9) 89%, rgba(214, 58, 59, 0.9) 100%);
  }
  
  .menugroup {
    background: rgba(0, 0, 0, 0);
  }
  
  .battleform label {
    background: url("http://i.imgur.com/yFWeHx0.png") no-repeat scroll left center transparent !important;
    padding: 4px;
    color: #FFF;
  }
  
  .select:disabled, .select.teamselect {
    padding-top: 3px;
  }
  
  .select {
    border-top-left-radius: 0px;
    background: rgba(190, 190, 190, 0.9);
    background: -moz-linear-gradient(top, rgba(190, 190, 190, 0.9) 0%, rgba(190, 190, 190, 0.9) 0%, rgba(190, 190, 190, 0.9) 10%, rgba(255, 255, 255, 0.9) 89%, rgba(255, 255, 255, 0.9) 100%);
  }
  
  .select:hover {
    background: rgba(255, 255, 255, 0.9);
  }
 
  .header .logo {
    opacity: 0;
  }
  
  .closebutton, .minimizebutton, .tabbar .closebutton {
    color: #000;
  }
  
  .closebutton:hover, .minimizebutton:hover {
    background: transparent; 
    border: none;
  }
  
  .closebutton:hover, .tabbar .closebutton:hover {
    color: #5B1F1F;
  }
  
  .ps-popup {
    background: rgba(27, 31, 43, 0.98);
    color: #FFF;
    box-shadow: 1px 1px 0px #000 inset, -1px -1px 0px #101010 inset, 2px 2px 3px rgba(0, 0, 0, 0.2);
    border: 1px solid #202020;
  }
  
  .popupmenu h3 {
    color: #ED3F41;
  }
  
  .popupmenu button {
    color: #FFF;
  }
  
  .popupmenu button:hover {
    color: #FFF;
    background: rgba(27, 31, 43, 0.6);
    border: 1px solid #000;
  }
  
  .popupmenu button.sel:hover {
    background: rgba(27, 31, 43, 0.8);
  }
  
  .roomlist a.ilink {
    font-size:14px;
    font-family: Tahoma, Verdana, Segoe, sans-serif;
    font-weight:bold;
    opacity:0.9;
    color:#fbfbfb;  
    text-transform: uppercase;
    text-align:left;
    text-shadow: 1px 1px 1px #4d4d4d,
    1px 0px 1px #4d4d4d;
    background: url(http://i.imgur.com/DqmewkB.png) no-repeat;
    box-shadow:none; 
    border-radius:0px; 
    border-width:0; 
    width:642px;
    padding-left:20px;
    padding-top:10px;
    padding-bottom:12px;
  }
  
  .roomlist a.ilink:hover {
    font-size:14px;
    opacity:0.9;
    font-family: Tahoma, Verdana, Segoe, sans-serif;
    font-weight:bold;
    color:#fbfbfb;  
    text-transform: uppercase;
    text-shadow: 1px 1px 1px #4d4d4d,
    1px 0px 1px #4d4d4d;
    background: url(http://i.imgur.com/UoUnGNL.png);
    box-shadow:none; 
    border-radius:0px; 
    border-width:0; 
    width:642px;
    padding-left:20px;
    padding-top:10px;
    padding-bottom:12px;
  }
  
  .pm-window h3 {
    border: 1px solid #000;
    background: rgba(213, 117, 117, 0.9);
    background: -moz-linear-gradient(top, rgba(213, 117, 117, 0.9) 0%, rgba(213, 117, 117, 0.9) 0%, rgba(213, 117, 117, 0.9) 10%, rgba(214, 58, 59, 0.9) 89%, rgba(214, 58, 59, 0.9) 100%);
    color: #FFF;
  }
  
  .pm-window h3:hover {
    border: 1px solid #000;
    background: rgba(213, 117, 117, 0.9);
    background: -moz-linear-gradient(top, rgba(213, 117, 117, 0.9) 0%, rgba(213, 117, 117, 0.9) 0%, rgba(213, 117, 117, 0.9) 10%, rgba(214, 58, 59, 0.9) 89%, rgba(214, 58, 59, 0.9) 100%);
    color: #000;
  }
  
  .pm-window.focused h3, .pm-window.focused h3:hover {
    border: 1px solid #000;
    background: rgba(213, 117, 117, 0.9);
    background: -moz-linear-gradient(top, rgba(213, 117, 117, 0.9) 0%, rgba(213, 117, 117, 0.9) 0%, rgba(213, 117, 117, 0.9) 10%, rgba(214, 58, 59, 0.9) 89%, rgba(214, 58, 59, 0.9) 100%);
    color: #000;
  }
  
  /*button {
    border-radius: 5px;
    border: 1px solid #000;
    background: #ED3F41;
  }
  
  button:hover {
    border-radius: 5px;
    border: 1px solid #000;
    background: #EF2525;
  }
  
  .userbar button.icon {
    height: 21px;
    width: 23px;
    font-size: 14px;
    text-align: center;
    padding: 0px 0px 2px 0px;
  }
  
  .chat-log {
    background: rgba(0, 0, 0, 0.9);
    color: #FFF;
  }
  
  .username {
    text-shadow: 1px 1px 1px #000;
  }
  
  .pm-log {
    background: rgba(0, 0, 0, 0.9);
    color: #FFF;
    border: 1px solid #000;
    border-top: none;
    border-bottom: none;
  }
  
  .pm-window.focused .pm-log {
    border: 1px solid #000;
    border-top: none;
    border-bottom: none;
  }
  
  .pm-log-add {
    background: rgba(0, 0, 0, 0.9);
    border: none;
  }
  
  .pm-window h3 small {
    color: #000;
  }
  
  .pxInta, .pxInta:focus, .pxInta:link, .pxInta:visited, .pxInta:active, .pxInta:hover {
    border-width: 0px !important;
    border-style: none none solid !important;
    border-color: #FFF !important;
    -moz-border-top-colors: none !important;
    -moz-border-right-colors: none !important;
    -moz-border-bottom-colors: none !important;
    -moz-border-left-colors: none !important;
    border-image: none !important;
    background-color: transparent !important;
    color: #FFF !important;
    font-weight: normal !important;
    text-decoration: none !important;
    padding-bottom: 0px !important;
    display: inline !important; 
  }
  
  .setchart {
    background: rgba(0, 0, 0, 0.95);
    box-shadow: none;
    border: 1px solid #000;
  }
  
  .setchart-nickname {
    background: rgba(0, 0, 0, 0.95);
    border: 1px solid #000;
  }
  
  .setchart label, .setchart-nickname label {
    color: #FFF;
    text-shadow: 1px 1px 1px #303030;
  }*/

  .ps-room .battle-controls {
    background: transparent !important;
  }
 
}
