css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("eldarya.com") {



/*Ezarel en familier*/  

 

  /*Images - pictures*/

 #pet-headerPortrait-base {

   background: url("http://img15.hostingpics.net/pics/245602502051Ezarel2.png") -10px top no-repeat !important;

  background-size : auto 125px !important ;

  }



  #pet-portrait {

    background : url("http://img15.hostingpics.net/pics/245602502051Ezarel2.png") center no-repeat !important ;

    background-size : auto 450px !important ;

    height : 450px !important ;

  }



  #pet-portrait > img[src*="/static/img/pet/icon/"] {display : none !important ;}



    /*Infos*/



  #pet-info-name:before {

    content : "Ezarel ♥" !important ;

    font-size : 20px !important ;

  }

  

  #pet-info-name {

    font-size : 0 !important ;

  }  

  

  #pet-info-evolve > .on {font-size : 0 !important ;}

  

  .pet-info-evolve-icon {font-size : 13px !important ;}

  

  .pet-info-evolve-icon:after {

    content : " Ezarel est épuisé. N'espère pas plus !";

  }

  

  

  #pet-info-racegender:before {

    content : "Elfe" !important ;

    font-size : 16px !important ;

    line-height : 20px !important ;

  } 

  

  #pet-info-racegender {

    font-size : 0 !important ;

    margin-top : 5px ;

  }  

  

  img[src*="/static/img/f.png"] {

    background : url("http://www.eldarya.com/static/img/m.png") !important ;

    padding-left : 14px !important ;

    padding-top : 15px !important ;

    width : 0 !important ;

    height : 0 !important ; 

    margin-bottom : 5px !important ;

  }

 

 /*Nourriture - food*/

  

 #pet-info-food > img {

  background : url("http://nsm08.casimages.com/img/2015/04/02//15040204041710339313132918.png") !important ;

  background-size : 60px 60px !important ;

  margin-top : -5px !important ;

  padding-left : 60px !important ;

  padding-top : 60px !important ;

  width : 0 !important ;

  height : 0 !important ;

} 

  

  #pet-info-food {font-size : 0 !important ;}

  

  #pet-info-food:before {

    content : "Gaufres au miel " !important ;

    font-size : 16px !important ;

  }

  

  #pet-info-food > span {font-size : 16px !important ;}

}
