css:
@-moz-document domain("tweakers.net") {
.reactieBody {
border: 1px solid #dddede;
padding: 0px 15px 0px;
margin-bottom: -0.5px;
}

#reacties {
width: 100%;
}

#commentColumn {
width: 80%;
}
  
.reactieHeader {
max-height: 20px;
  padding-top: 3px;
}

.reactieHeader br {
display:none;
}
 
.scoreButton.large{width:22px;height:15px;line-height:15px;font-size:10px;font-weight:400;border-radius:1px}

.thumb.usericonsmall {
width: 20px;
height: 20px;
}

.thumb.usericonsmall.empty {
width: 20px;
height: 20px;
}

.reactieContent {
padding: 10px 50px 0px 0px;
}

.reactieFooter {
padding: 0;
margin-top: -15px;
text-align: right;
}

.commentLink {color:#616464;}

a{color:#616464;text-decoration:none}
a:hover,a.highlightlink{color:#b9133c;text-decoration:underline}

.karmaCards {
    position: relative;
    margin-top: 0px;
    float: right;
}
}
