/* Dark Web of Science by siliconworm */
@-moz-document url-prefix(http://apps.webofknowledge.com),
url-prefix(https://apps.webofknowledge.com),
domain("cel.webofknowledge.com"),
domain("login.webofknowledge.com")
{

* {
background-color: #14181F!important;
}

.block-search-input {
color: #96FFB8!important;
}
.search-criteria-input {
color: #96FFB8;
}
h1, h2, h3.title2, h3.title3, h3.title4, h4 {
color: #E2D48B;
}
.button1, .button2, .button3, .button4 {
background: transparent !important;
}
.goToPageNumber-input {
color: #8CE49D!important;
}
.j-custom-select a span {
color: #9FAEB6;
}
.hitHilite {
color: #FF0!important;
}
.NEWFRside_rec {
color: #B3B3B3;
}
.NEWwokErrorContainer P, .NEWwokErrorContainer LI {
color: #70AAAF;
}
option {
color: #8FE4E4!important;
}
select {
color: #FF62E6!important;
}
input{
color: #58E77A!important;
}
.ui-dialog-quickoutput .ui-dialog-title {
color: #A2C062;
}
.SignInRightColumn H3, .SignInLeftColumn H3 {
color: #C98F8F;
}
.fr_address_row2 a:hover, .fr_address_row2 a, a[id^="address"], a[id^="address"]:hover {
color: #438CB6;
}
.paginationBar .pagination span {
color: #E4BEBE;
}
.button8, .button9 {
background: transparent !important;
}
.select2-container .select2-choice {
background: transparent !important;
}
.select2-dropdown-open .select2-choice {
-webkit-box-shadow: 0 1px 0 #4F5068 inset;
box-shadow: 0 1px 0 #515257 inset;
background-image: -webkit-gradient(linear,left bottom,left top,color-stop(0,rgb(200, 219, 62)),color-stop(0.5,#BEC25C));
}
    

/* defaults for all links */
a:link, .link, a font[color], .mn-hd-txt, 
    a.fl:link, .fl a, .flt, a.flt, .gl a:link, a.mblink, .mblink b, ._Hg ._qA, ._Hg ._Pn, ._Hg ._Uq, #gt-feedback, #gb a  { 
        color: #7C9EDC !important; 
        transition: color .4s, text-shadow .4s !important;
    }
                    
a:visited { 
    color: #F92672 !important; 
}

/* hovers */
.menuitem:hover, a:hover, a span:hover, .actbar-btn:hover, #gt-feedback:hover, #gb a:hover {
color: #E5E5E5 !important; text-shadow: #ffffff 0px 0px 2px !important;
transition: color .4s, text-shadow .4s !important;
}

/* big link visited */
#res .g a:visited { opacity: 0.5 !important; }

/* URL text */
.a, cite, .cite, .cite:link, cite b, #resultStats { color: #999 !important; }

/* dashes between Cached and Similar links */
.gl { color: #999 !important; }

/* disable dashed outline on focused elements */
:focus { outline: none; }

/* image thumbnails */
#res .g img, #ImgContent td img {
  border: none !important;
box-shadow: 4px 4px 10px rgba(0,0,0,0.8) !important;
}

/* Main text color */
div, td, font[color="#000000"], .qbtbha .qbtbtxt.qbclr, .gt-rw-span, .gt-syn-span, .gt-card-ttl-txt, #contribute-target, #source { color: #C0C0C0 !important; }

/* fix for google suggest */
.aAutoComplete .cAutoComplete, .google-ac-a .google-ac-c, .gac_a > .gac_c { color: #CCCCCC !important; }
.aAutoComplete .dAutoComplete, .google-ac-a .google-ac-d, .gac_a > .gac_d { color: white !important; }
.google-ac-e td, .gac_e { color: #380000 !important; }

/* make sure web forms (and google suggest) don't get their colors changed */
input *, textarea *, .aAutoComplete {
    background: -moz-Field !important;
    color: #999999 !important;
}

}
/* Dark Web of Science by siliconworm */
