css:
@namespace url(http://www.w3.org/1999/xhtml);

/* FF14速報 */
@-moz-document domain("ff14net.2chblog.jp") {
  div[style="margin: 0px 0px 60px 5px; line-height: 180%; font-size: 17px; font-weight: bold;"] {
    font-size: 90% !important;
    font-weight: normal !important;
    line-height: 140% !important;
    margin: 0 0 45px 5px !important;
  }
  .article-title {
    margin: 0;
  }
  .article-sub-category ul {
    font-size: 14px;
  }
  .comment-body {
    margin-bottom: 35px;
    color: #333;
    font-size: 14px;
    font-weight: normal;
  }
  
  #header2,
  #blog-header-image,
  .rss-blogroll,
  .left-column-inner > .plugin-memo:nth-of-type(1),
  .main-column-inner > .listWithImage,
  .listWithImage+div[align="center"],
  .article-social-btn,
  h2#heading4,
  a[href^="http://gamealpha.warotamaker.com"],
  a[href^="http://nullpoantenna.com/"],
  a[href^="http://ff14matome.warotamaker.com/"],
  a[href^="http://2ch-c.net/"],
  a[href^="http://chomatome.warotamaker.com/"],
  div[style^="line-height: 140%; font-size: 15px; font-weight: bold;"],
  div[style="line-height: 140%; font-size: 17px; font-weight: bold; border-top:1px dashed #CCCCCC; padding:5px"],/* x */
  div[style="line-height: 140%; font-size: 15px; font-weight: bold; border-top:1px dashed #ff0000; padding:5px"],
  .listWithImage > div[id^="popularArticlesWithImageTag"],
  .article-tags,
  .right-column-inner > :nth-of-type(1),/* リンク */
  .right-column-inner > :nth-of-type(2)/* カテゴリ別アーカイブ */{
    display: none;
  }
}

/* 馬鳥速報 */
/* 1段階縮小して表示を推奨 */
@-moz-document url-prefix("http://blog.livedoor.jp/umadori0726/") {
  .t_h {
    font-size: 85% !important;
  }
  .t_b {
    font-size: 100% !important;
    font-weight: normal !important;
    line-height: 140%!important;
  }
  
  #header,
  .naviarea,
  #leftside > .plugin-memo:nth-of-type(2),
  #leftside > .plugin-memo:nth-of-type(3),
  #headline,
  .innerbanner,
  br+br+br+br,
  #adwbox,
  #entrywbox,
  #bottombtn,
  h3#rssheader,
  #rssinner {
    display: none;
  }
}
  
/* FF14@2chまとめ ff14sc */
@-moz-document domain("ff14sc.com") {
  #header,
  #navi,
  #sns-buttons,
  div[style="padding:20px 0px 0px 0px;"],
  .sns-pages,
  #text-12 {
    display: none;
  }
}
