css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("zaadoptujfaceta.pl") {
    body {
        background: #fff;
    }
    .navbar .overview a {
        color: #DADADA;
    }
    #my-popularity #ma-popu span {
        color: #E7E7E7;
    }
    .logo {
        display: none;
    }
    .welcome {
        visibility: hidden;
        margin: 20px 0px 36px 29px;
    }
    #version-switcher {
        display: none;
    }
    #main {
        border: none;
        box-shadow: none;
    }
    #main-head {
        background-image: url("http://wallpapers.wallhaven.cc/wallpapers/full/wallhaven-103929.png");
        background-position: center -315px;
        background-size: 894px;
    }
    #main-foot {
        display: none;
    }
    .logged #content {
        background: none;
    }
    .navbar {
        background: none;
        border: none;
    }
    .navbar .overview {
        border: none;
    }
    .navbar .shortcuts li {
        border: none
    }
    .cart {
        margin: 11px 15px 0px 0px;
    }
    .logtools {
        display: none;
    }
    .logged #contentwrapper {
        background: none;
    }
    .panel {
        box-shadow: none;
    }
    .action {
        filter: hue-rotate(80deg);
    }
    #pop-bar .pop-container span {
        filter: hue-rotate(80deg);
    }
    .cart .nb-products {
        color: #C6C6C6;
    }
    .cart .basket {
        color: #818181;
    }
    .person.suggestion .city,
    .person.mailbox .time {
        color: #BDBDB8;
    }
    #mailbox #mailbox-content .sent-from-phone {
        filter: hue-rotate(80deg);
        opacity: 0.5;
    }
    .smiley,
    .emote {
        filter: hue-rotate(65deg);
    }
    #mailbox #mailbox-content .message-content .status {
        filter: hue-rotate(80deg);
    }
    #mailbox .mailbox-basics {
        border-bottom: 1px solid #E9E9E9;
    }
    #mailbox .mailbox-basics.bottom {
        border-top: 1px solid #E9E9E9;
    }
    h2 {
        border-bottom: 1px solid rgba(0, 0, 0, 0.05);
    }
}
