css:
/* i really want this to be global */
.bodytopbg, div.header.clickable-cell,
    .bmlist-options > a, .icon-comments
    {
        background: rgb(10, 10, 10) !important;
    }
    .beatmapListing > tbody > tr > th,
    .bmlist-options,
    .content-infoline, #playstats,
    .historyp,
    .beatmapListing th,
    #tablist a,
    .tablebg.forum_table > tbody > tr > td.row1, .tablebg.forum_table > tbody > tr > td.row2,
    .frozen-header,
    div#mw-head-base, div#mw-page-base, .profileStatLine, .profileStatHeader
    {
        background: rgb(40, 40, 40) !important;
    }
    
    .beatmap, .beatmap:hover, .tablebg .row1:hover, .tablebg .row2:hover, .profileGameModes a, .profile-tabs td
    {
        background: rgb(40, 40, 40) !important;
        border: 1px solid rgb(60, 60, 60) !important;
    }
    
    .content-with-bg, .footer, .pack-msg,
    #tablist A:hover,
    .sf-menu a:hover, .sf-menu a:focus, .sf-menu li.sfHover
    {
        background: rgb(40, 40, 40) !important;
    }
    
    .dialog-area, .peppynote, .reasonpanels > li, .reasonpanels > li:hover > i,
    .changelog-text,
    .row1p,
    li.sfHover > ul > li > a,
    .login-dropdown,
    .blurb, .news-text, .row1,
     .postRight, .postbody, .postTime, .tablebgside > tbody > tr > td, .quotecontent, .sectionContents, .playstyle-container
    {background: rgb(60, 60, 60) !important}
    
    .final_footer,
    .row2p,
    div#tablist, table#songinfo, #songinfo td.colour, .posttext,
    .row2,
    .postLeft,
    div.mw-panel, .prof-beatmap
    {
        background: rgb(60, 60, 60) !important;
    }
    
    .beatmapListing tr.row1p:hover, .beatmapListing tr.row2p:hover 
    {
        background: rgb(60, 60, 60) !important
    }
    
    #toc, table.wikitable, .wikitable th
    {background-color: rgb(60, 60, 60) !important; background: rgb(60, 60, 60) !important}
    
    .beatmap .tags a {background: rgb(60, 60, 60) !important; color:black; border: rgb(60, 60, 60) !important;}

    
    .row1p > td > a, .row2p > td > a, .row1p > td > a:hover, .row2p > td > a:hover
    {color: rgb(200, 200, 200) !important;}
    
    #songinfo td.colour, #songinfo td.colour > a, #songinfo > tbody > tr > td > a,
    .login-dropdown > form > div.smaller > a,
    .blurb, .news-text, .row1 > td > a, .row2 > td > a,
    .postTime > a,
    div.mw-panel div.portal h5, div.mw-panel div.portal div.body ul li a
    {color: black;}
    
    #songinfo td{border-bottom: 1px solid rgb(80, 80, 80) !important}
    
    #tablist A.active:link, #tablist A.active:visited {background: rgb(20, 20, 20) !important}
    
    sf-menu a{color:#b478fa;}
    
    a, .sf-menu a, #general > font
    {color:rgb(150, 120, 250) !important;}
    
    .blurb, .news-text, .main-box{border: 1px solid rgb(60,60,60) !important}

    .blog-post{background:none}
   	
	.final_footer{display:none} .content-with-bg{min-height:0px;} .top_button{display:none}
	
	.content-with-bg > h1, .content-with-bg > h2, .login-open-button, .login-open-button:hover, .new-user-button:hover,
    .ntitle,
    .row1, .row2,
    .forum_posts_table tr:hover, h1, .prof-beatmap .pp-display, h2, .profileGameModes a:hover
    {text-shadow: none}

	div.avatar-holder
	{
  		background: rgb(40,40,40) !important;
  		border: rgb(40,40,40) !important;
	}

	div#userpage
	{
  		background: rgb(150,150,150) !important;
	}

	div.spoiler_body
	{
  		background: rgb(250,150,0) !important;
	}
	
	div.spoiler_head
	{
    	border-color: rgb(250,150,0) !important;
	}

	input
	{
  		background-color: rgb(150,150,150) !important;
	}

	a, .path-fill
	{color:black;}

	td#border, tbody
	{
  		background: rgb(150,150,150) !important;
	}

	#bodyContent > table, .toccolours, .collapsible, .collapsed {display: none;}
	#bodyContent > div.visualClear, div.thumbinner {background: rgb(150,150,150) !important;}
	
	div.changelog_area {display: none;}
	td.nav {background: rgb(40,40,40) !important;}
	tbody > tr > td {background: rgb(40,40,40) !important;}
	
	div#pagecontent
	{
  		background: rgb(40,40,40) !important;
    }

	a.stream
	{
  		background: rgb(150,150,150) !important;
  		border: rgb(40,40,40) !important;
	}

	div#playstats {color: rgb(150,120,250) !important;}
        div#coolheader {display: none;}
	div#google_translate_element {display: none;}
	div#p-namespaces {display: none;}
	div#p-views {display: none;}
        form#cse-search-box {display: none;}
