css:
@-moz-document domain(netvibes.com) {
/* @import url(http://fonts.googleapis.com/css?family=FiraSans:400,500,700);
@import url(http://fonts.googleapis.com/css?family=FiraSans:400,500,700); */

/* ---------------------- INTRODUCTION  ---------------------- 

Works with following Mode : 
- Condensed mode
- List with images

Better render with Fira Sans (https://fonts.google.com/specimen/Fira+Sans)
Only works with "Condensed" display 
Hasn't been tested with "Feed mode"

Some useless functionnalities have been remove : 
- Collapse all
- Header background
- Header title
- Charts icon (Premium option)



*/

body {
    background-color: #fff !important;
    overflow-x: hidden;
    text-rendering: optimizeLegibility;
}

body { /* Modify this line to change the Font */ 
    font-family: "Fira Sans", FiraSans, Fira, Roboto, Helvetica, Arial, sans-serif !important;
}

#application { /* Modify these lines to change font-size */ 
   font-size: 1.4em;
   line-height: 1.6em!important; 
}



/* ---------------------- HEADER & FOOTER ---------------------- */
#header {
    background-image:none !important;
    background-color:#eee !important; 
    
}
#pageTitle, .header-collapse-expand-content,
#header-middle-content, #savingStatus, .topbar-profile-picture /* removing dumb custom header */
{
    padding:0;    
    display:none !important;   

}
#topLinks {
    color: #fff;
    height: 0;
    padding: 0;
}
#top {
    margin: 0!important;
    padding: 0!important;
}
.topbar {
    height:50px;
}
.topbar-switch-widgets .text, .topbar-switch-reader .text {
    display:none !important;   
}
#top .universal-zorch {
    border-radius:0!important;   
    background-color:transparent;
    border:1px solid #7a7a7a;
}
#top .zorch-launch {
     background-color:transparent !important;
    
}
.topbar-center {
    margin-top:10px;
}
#addContentLink,
#addContent {
    height: 24px!important;
    line-height: 1.4em!important;
    font-size: 1em!important;
    width: 33px!important;
    overflow: hidden;
    margin-left: 4px;
}
#aNewTab {
    color: #fff !important;
    text-shadow: none;
}

.nv-footer-toggle {
 display:none !important;
    
}
.topbar-button {
    background-color:transparent;
}

/* ---------------------- TOP TABS ---------------------- */


.tabs {
    border:0 !important;
}
.tab, #divTabs .tabs li.tab {
    background-color:transparent !important;
    border:0 !important;
}
#divTabs  {
    border-bottom:0px !important;
}
#divTabs .tabs li {
    background-color: #eee!important;
    border: 1px solid #ccc!important;
    border-bottom: 0!important;
    border-right: 1px solid #fff !important;
    border-top: 0 !important;
    margin: 0!important;
    padding: 1.4em 1.4em !important;
    font-weight:normal;
}
.actions-tab-options {
    margin-left:1em;
}

#divTabs .tabs li.tab.selected{
    background-color: #fff !important;
    background-image: none !important;
    position: relative;
    border-bottom: 1px solid #fff !important;
    font-weight:700;
    z-index:888;
    border-top:0;
}
#divTabsOuter {
    border: 0;
}
#divTabs .tabs .icon {
    display: none!important;
}
#divTabs .unread,
#divTabs .iconText {
    font-size: 0.8em!important;
}
#divTabs[overflow=true]>.nv-tabs-left, #divTabs[overflow=true]>.nv-tabs-right {
    background-color:transparent
}
.nv-tabs-right .btn-separator {
    display:none !important;
}
.nv-tabs-right {

background-image:none !important;
    position:absolute!important;
    width:100px!important;

    
    }


.nv-tabs-right .btn, .nv-tabs-left .btn {
 height:30px !important;
    margin-top:1em;
}

#menuTabs .tab-visible {
    background-color:#5b5b5b !important;
    padding:0.8em!important;
    border:0 !important;
    border-bottom: 1px solid #888 !important;

}
.nv-options,
.nv-options-root,
.widget-view-root,
.tabOptions {
    z-index: 4 !important;
    position: relative !important;
}


/* ---------------------- FEEDS  ---------------------- */

#maintable {
    margin-top:30px;
    padding:0 6em;
}
 .nv-condensed-item-list-renderer {
    height:280px !important; 
}
.module {
    background-image: none!important;
padding:0 1em;
    letter-spacing: .01em;
    z-index: 0;
    margin-bottom:1.5em !important;
 
}
.moduleFrame {
        border-radius:0 !important;   
    border:0 !important;
        background-image: none!important;

}
.module:hover {
   /* border-top: 2px solid #000; 
    box-shadow: 0px 0px 40px rgba(0, 0, 0, 0.2);*/
    /*left: -10%;
    position: relative;
    width: 120%;
    z-index: 999;*/    
    -webkit-transition: box-shadow .4s ease-in;
    transition: box-shadow .4s ease-in;
}
.moduleHeader {
    background-color: #fff;
    background-image: none !important;
    padding-top: 12px!important;
}
.moduleHeader .iconText {
    font-size: 0.8em !important;
    text-transform: uppercase;
}
.moduleHeader .title a {
    color: #111!important;
    text-decoration: none!important;
}
.moduleContent {
    padding: 6px 0;
}
.unread {
    color: #a1a1a1 !important;
}

.nv-condensed-item-renderer .item-time {
    box-shadow:0 0 0 rgba(0,0,0,0) !important;
    -webkit-box-shadow:0 0 0 rgba(0,0,0,0)  !important;
}
.uwa-themed-scroller-content {

/*       overflow-x: hidden !important;  Hide horizontal scrollbar */
    
}
.tooltip,
.nv-compactToolTip {
    background: gray!important;
    
}
.module .next,
.module .prev {
    font-size: .8em;
    text-transform: uppercase;
    padding: 0;
    margin: 0
}
.item {
    padding: 0!important;
    margin: 0;
}
.nv-condensed-item-renderer {
    padding:0.3em 0;
}
.charts {
    display:none !important;
}
/*
.nv-feedList {
    margin: 0!important;
}
.nv-feedList li {
    background-image: none;
    line-height: 1.4em;
    padding: 0;
    border-top: 1px solid #eee!important;
}
.nv-feedList li a {
    padding: 4px 4px !important;
}
.nv-feedList li a:hover {
    border: 0 !important
}
.nv-feedList li .main-link {
    border: 0;
    height: 16px;
    overflow: hidden;
}
*/

/* ---------------------- CONTENT READER ---------------------- */

.feed-feed-head .title {
     font-size:3em;
    line-height:1.25em;
}

.feed-feed-infos {
       font-size:1.4em;
    line-height:1.6em;
}
.nv-item-renderer-content-wrapper{
    max-width:800px
}
.nv-item-renderer-article-text a:not(.uwa-button), .nv-item-renderer-article-text a:not(.uwa-button):link, .nv-item-renderer-article-text a:not(.uwa-button):visited {
   color:#3646ee;
}
.no-touch .nv-item-renderer-feed a:not(.uwa-button):hover {
     color:#3646ee;
 
}
.nv-modal-box .nv-item-list-renderer {
     font-size:1.4em;
   
}
.feed-article-text p {
    font-size:1.4em;
    line-height:1.6em;
    margin:1em 0 0 0;
}
.feed-article-text h2 {
    font-size:1.8em;
    line-height:1.6em;
    margin-bottom:0em !important;
    margin-top:2em;
}

/* ---------------------- HIDE FACEBOOK LIKE BUTTON ---------------------- */
#FBLike-chrome,
.nv-facebookLike,
.nv-facebookLike-iframe,
.iframe-webview,
.iframe-container {
    position: absolute;
    background: none!important;
    height: 0px!important;
    line-height: 0;
    padding: 0;
    display: none;
}

/* ---------------------- SCROLLBARS ---------------------- */
::-webkit-scrollbar {
    width: 12px;
}

::-webkit-scrollbar-track {
    border-radius: 10px;
    background-color: #ddd;
}
::-webkit-scrollbar-thumb {
    -webkit-border-radius: 10px;
    border-radius: 10px;
    background: rgba(0, 0, 0, 0.2);
}
::-webkit-scrollbar-thumb:window-inactive {
    background: rgba(0, 0, 0, 0.2);
}

}
