css:
@-moz-document url-prefix("https://www.tumblr.com"), url-prefix("http://www.tumblr.com") {
.popover--account-popover.popover--base.bottom {
  display: inline-block!important;
  position: absolute!important;
  top: 0!important;
  left: 0!important;
  transform: none!Important;
  width: 100%!important;
  z-index: 50!important;
} 
.popover.popover_menu.popover_gradient {
  margin: 0 auto!important;
  width: 900px!important;
  padding: 60px 30px 0!important;
  top: 0;
  background: transparent!important;
  -webkit-transition: none!Important;
  -moz-transition: none!Important;
  transition: none!Important;
  -webkit-transform: none!Important;
  -moz-transform: none!Important;
  -ms-transform: none!Important;
  -o-transform: none!Important;
  transform: none!Important;
  box-shadow: none!important;
  opacity: 1!important;
}
.popover_inner {
  margin-left: 664px!important;
}
.loader-container {
  display: none!important;
}


.l-container.l-container--two-column .right_column, .l-container.l-container--two-column-dashboard .right_column {
  margin-top: 820px!important;
}

      
      
.popover_header {
  background: transparent!important;
    color: rgba(255,255,255,.3)!important;}
        
        

.popover_gradient>.popover_inner {
  background: transparent!important;
  color: rgba(255,255,255,.5)!important;
box-shadow:none!important;
}
.popover--account-popover .popover .popover_menu_item .popover_menu_item_anchor {padding: 0 10px;
  color: rgba(255,255,255,.5)!important;}

.popover_menu>.popover_inner .popover_item_suffix a {
  color: rgba(255,255,255,.5)!important;}

.popover_menu>.popover_inner .popover_item_suffix {
  color: rgba(255,255,255,.5)!important;}


.popover--account-popover .popover .popover_menu_item_blog.popover_menu_item_blog {
  background: transparent;
  color: rgba(255,255,255,.5)!important;
    border-top: 2px solid rgba(255,255,255,.13);
  border-collapse: collapse!important;
}

.popover--account-popover .popover .popover_menu_item_blog:hover:not(:first-child) {
  border-top-color: rgba(255,255,255,.13);
}

.popover_menu_item a {
  color: rgba(255,255,255,.5)!important;
}

.popover_menu_item:hover a, .popover_menu_item.keyboard_focus a, .share_options .popover_menu_item:hover a.external:after, .share_options .popover_menu_item.keyboard_focus a.external:after {
  color: rgba(255,255,255,.5)!important;
}

.popover--account-popover .popover_menu_sublist .popover_menu_item:hover {
  background: none!important;
}




.popover--account-popover .popover .popover_menu_item:hover {
  background: none!important;
}
.blog-list-item.blog-list-item--on-light:hover {
  background: rgba(0,0,0,0.15)!important;
}


.blog-list-item.blog-list-item--on-light:hover .blog-list-item-info-name, .blog-list-item.blog-list-item--on-light:hover .blog-list-item-icon-private {
  color: rgba(255,255,255,.5)!important;
}

.popover--account-popover .popover .popover_menu_item_blog_details{padding:0!important;}

.blog-sub-nav-details .blog-sub-nav-item {
  padding: 4px 20px!important;
  margin-top: 1px!important;
}

.blog-sub-nav-details .blog-sub-nav-item:hover, .blog-sub-nav-details .blog-sub-nav-item.selected, .blog-sub-nav-details .blog-sub-nav-item.currentItem {
  box-shadow: none;
  background: rgba(0,0,0,0.15)!important;
}

.blog-sub-nav-details .blog-sub-nav-item:hover .blog-sub-nav-item-label, .blog-sub-nav-details .blog-sub-nav-item.selected .blog-sub-nav-item-label, .blog-sub-nav-details .blog-sub-nav-item.currentItem .blog-sub-nav-item-label {
  color: rgba(255,255,255,.5)!important;
}

.nav-redesign .popover--account-popover .popover .popover_menu_item:not(.popover_menu_item_blog):active, .nav-redesign .popover--account-popover .popover .blog-list-item:active, .nav-redesign .popover--account-popover .popover .blog-sub-nav-item:active {
  background-color: rgba(0,0,0,0.15)!important;
}
.blog-sub-nav-details .blog-sub-nav-item .blog-sub-nav-item-data {
        color: rgba(255,255,255,.5)!important;}

      
.nav-redesign .popover--account-popover .popover .popover_menu_item:not(.popover_menu_item_blog):active a, .nav-redesign .popover--account-popover .popover .blog-list-item:active a, .nav-redesign .popover--account-popover .popover .blog-sub-nav-item:active a, .nav-redesign .popover--account-popover .popover .popover_menu_item:not(.popover_menu_item_blog):active a div, .nav-redesign .popover--account-popover .popover .blog-list-item:active a div, .nav-redesign .popover--account-popover .popover .blog-sub-nav-item:active a div {
  color: rgba(255,255,255,.5)!important;
}

li.blog-sub-nav-item:hover a div {
  color: rgba(255,255,255,.5)!important;
}

.blog-list-item .blog-list-item-info .blog-list-item-info-title {
        color: rgba(255,255,255,.3)!important;}


#popover_search {
  margin: 0!important;
  background: white!important;
  width: auto!important;
  padding: 0!important;
  max-width: 300px!important;
}

#popover_search .popover_inner {
  margin: 0!important;
  width: auto!important;
}
#popover_search .popover_menu_item a {
  color: #444!important;
}
}
