css:
@-moz-document domain('tumblr.com') {

.tab-bar-container {
  position: relative;
  height: 100%;
  float: right;
  width: auto;
  margin: 0;
  right: 210px!important;
  top: 0;
}

.ui_search, .ui_search .search_form_row {
  position: fixed;
  right: 30px;
  width: 200px;
}

.compose-button {
  color: #36465d;
  border-color: #529ecc;
  background: #529ecc;
  position: relative;
  font-size: 30px;
  height: 32px;
  width: 45px;
  margin-left: 20px;
  margin-top: 10px;
  float: right;
  display: none;
}
  
.popover popover_menu popover_gradient search_popover {
  position: absolute;
  top: 48px!important;
  right: 3px!important;
  width: 215px;
}

.popover--account-popover .popover .popover_menu_item_blog_details {
  padding-left: 15px;
  overflow: hidden;
  height: 0;
  padding-right: 10px;
}



.identity-refresh .tab_notice .tab_notice_value {
color: #35455c;
}

.identity-refresh .selected .tab_notice, .identity-refresh .new_post_notice_container, .l-header-container--refresh .tab_notice, .identity-refresh .inbox_notice_container
{
background: #D95E3F;
}

.l-header-container--refresh.header-redesign.has-3d-logo .search_popover {
  top: 48px!important;
  left: auto!important;
}
.l-header-container--refresh.header-redesign .search_popover {
  top: 48px!important;
  right: 0px!important;
  left: auto!important;
}

.header-redesign .ui_search, .ui_search {
  height: 30px !important;
  float: right !important;
  margin-top: 11px !important;
  margin-right: 20px !important;
}

.ui_search, .ui_search .search_form_row {
  position: fixed;
  right: -3px;
  top: 0px;
  width: 200px;}

.search_popover {
  left:0!important;
  right:0!important;
  margin-left:calc(100% - 183px - 12px);
  max-width:183px;
}

}
