css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("culture.hu-berlin.de") {

body {font-family:'dejavu sans','bitstream vera sans',sans-serif !important;line-height:1.6em !important;font-size:.9em !important}
#page_container {width:98% !important;max-width:1400px !important}
#page_header,
#page_header_table,
#page_body {width:100% !important;margin:0 auto !important}
p {line-height:1.6 !important;max-width:44em !important;}
em {font-style:normal !important;font-weight:700 !important}
td.content_table_twocols_left_cell {width:37% !important}
td.content_table_twocols_right_cell {width:63% !important}
#page_header img[src*="logo"] {width:42px !important;height:42px !important}
#kuwi_header_title_div h1,
#kuwi_header_title_div span {display:inline !important;padding-right:1em !important}
#kuwi_header_title_div {width:100% !important;padding:1em 0 !important;text-align:left !important}

}
