css:
@-moz-document domain("www.tumblr.com") {
body {
  background-image: url(https://36.media.tumblr.com/fc439139b20d4b1d07b3ec9fd4fa0885/tumblr_nmiwenbTeq1s60yyoo1_500.png) !important;
  background-repeat: no-repeat !important;
  background-attachment: fixed !important;
  background-position: right bottom !important;
  background-size: auto !important;
  background-color:#8babca!important;
}

.glass.blue, #hello_glass.blue, #glass_overlay.blue {background: rgba(255,255,255,1)!important}

DIV.l-header-container { 
	background-color: transparent!important;
	box-shadow: none!important;
        position: absolute !important;
	}
.post_avatar{background-color:transparent!important}

div.l-content {
background-color: rgba(0,0,0,0) !important;
}
  .l-container.l-container--two-column-dashboard .left_column{
background-color: rgba(0,0,0,0) !important;
}

.mceContentBody {
  background: rgb(255, 255, 255) !important; 
  color: rgb(68, 68, 68) !important; 
}
.elevator{color: #FFFFFF !important;} #search_actions_search .elevator, #discover_actions_discover .elevator{background-color:transparent!important}

#right_column .controls_section li a,
#right_column .identity-refresh .controls_section a .count .identity .controls_section li.account_header, .identity .controls_section li.editors_header, .identity .controls_section li.contributors_header, .identity .controls_section li.section_header,.small_links A, .count{color:#FFFFFF!important;}
.identity-refresh .controls_section li:hover{background:#FFFFFF!important; border-radius:10px!important}

#tumblr_radar {display: none !important;}

.identity-refresh .controls_section.follow_list.recommended_tumblelogs, .identity-refresh .controls_section.follow_list.trending_tumblelogs{display: none !important;}

.identity-refresh .l-container.l-container--two-column .right_column, .identity-refresh .l-container.l-container--two-column-dashboard .right_column{background-color:transparent!important}

.discover-controls-wrapper{background-color:rgba(255,255,255,1)!important; position:absolute!important;}

div#right_column,

div#right_column:after {

  background: none !important;

}

.identity-refresh .controls_section li .posts .hide_overflow, .identity-refresh .controls_section li .followers .hide_overflow, .identity-refresh .controls_section li .reach .hide_overflow, .identity-refresh .controls_section li .members .hide_overflow, .identity-refresh .controls_section li .messages .hide_overflow, .identity-refresh .controls_section li .drafts .hide_overflow, .identity-refresh .controls_section li .processing .hide_overflow, .identity-refresh .controls_section li .queue .hide_overflow .controls_section li a, .controls_section li .hide_overflow {margin-left: 25px!important;}
}

@-moz-document url-prefix("http://www.tumblr.com/dashboard/iframe"), url-prefix("https://www.tumblr.com/dashboard/iframe") {
body {background:none !important;}
}
