css:
@-moz-document url-prefix("https://www.tumblr.com/"), url-prefix("http://www.tumblr.com/") {
#search_query{background:#616F85; margin-top: 3px; border-radius:20px;}
.ui_search .search_query { border: 1px solid rgba(255,255,255,.1);}
#search_results_container .search_results_container popover_scroll {max-height: 400px;}

.ui_search.tab.active .search_query, .ui_search.tab.active .search_form_field:before {border-color: rgba(255,255,255,.1);}
.elevator {font-size: 40px;}
.l-header-container--refresh .tab_bar .iconic .tab_anchor:before {font-size: 20px;}
.identity-refresh .tab_notice .tab_notice_value {color: white; font-size:11px;}
.tab_anchor .selected .tab_notice {background:#D75651!important;}
.identity-refresh .selected .tab_notice {background: #D75651!important;}
.selected .tab_notice {background: #D75651 !important;}
.identity-refresh .tab_notice {background: #D75651;}

.l-header-container--refresh .tab-bar-container {right: 50%; margin-right: -190px; position:fixed;}
.l-header-container--refresh .l-header {left: 50%; margin-left: -445px; padding: 0 0px 0 0px;}

.l-header-container--refresh .tab_bar .tab.ui_search {padding-left: 30px;}
input, textarea {line-height: 22px;font-size: 12px;}
.ui_search .search_query {height: auto;}
.ui_search .search_form_field:before {font-size: 16px;top: 5px; left:10px}
.l-header-container--refresh .l-header .compose-button {right: 50%; margin-right: -500px; position:fixed;}


#search_field{ position: fixed;  margin-right: -447px;  right: 50%;}

.identity-refresh .sidebar_nav {margin: 15px 20px 10px;}

.icon_user_settings:before {
content: "\f153";
font-size: 20px!important;} 


.logo.logo-redesign .logo-anchor {
  background-image: url('https://www.tumblr.com/images/logo/logo_small.png?v=7148d4cebdca57896e8bfaf58012c7b9');
  background-repeat: no-repeat;
  background-position: center;
  background-size: auto 31px;
  text-indent: -999em;
  overflow: hidden;
  display: block;
  height: 100%;
  width: 100%;
}
      
      .logo {
        width: 150px!important;}
        
        .header-redesign .ui_search .search_query {
  border: 0px solid rgba(255,255,255,.5);
  color: rgba(255,255,255,.5);
  border-radius: 28px;
  padding-right: 10px;
  padding-left: 32px;
  background: 0 0;
  font-size: 12px;
  line-height: 14px;
  font-weight: 400;
  width: 220px!important;
  height: 28px;
}
              .header-redesign .ui_search .search_form_field:before {
font-size: 16px;
            left:11px;
           color: rgba(255,255,255,.5);
                  top:7px;}
          
          .ui_search, .ui_search .search_form_row {
  position: relative;
  margin-top: 6px;
}
      .l-header-container--refresh.header-redesign.has-3d-logo .search_popover {
  top: 48px!important;
  left: 638px!important;
}

.search_popover{
    left: 637px!important;}
}

@-moz-document url-prefix("https://www.tumblr.com/"), url-prefix("http://www.tumblr.com/") {
.l-header-container--refresh .l-header {left: 50%; margin-left: -425px; padding: 0 0px 0 0px;}
#search_field{ position: fixed;  margin-right: -437px;  right: 50%;}
.l-header-container--refresh .l-header .compose-button {right: 50%; margin-right: -490px; position:fixed;}
.l-header-container--refresh .tab-bar-container {right: 50%; margin-right: -180px; position:fixed;}

}
