css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.netresearch.de") {
   body {
     background: #f6f6f6;
   }
  
  #footer {
    background: #333;
  }
  
  #header {
    height: 70px;
  }
  
  #header .lightdot {
    background: none;
  }
  
  #header .pagetitle h1, #header .pagetitle {
    color: #424236;
    font-family: "Raleway",sans-serif;
    text-transform: uppercase;
  } 
    
  #meta {
    background: none repeat scroll 0px 0px #0b747c;
  }
  
  body {
    font-family: "Calibri",  sans-serif;
  }
  
  body, div, p, ul, li, table, h1, h2, h3, h4, h5, h6 {
    font-size: 15px;
  }
    
  .box_content_title {
    border-top: none;
    border-bottom: none;
    background: none;
    text-align: left;
    text-transform: uppercase;
    font-family: "Raleway";
    font-size: 22px;
    color: #439da1;
  }
  
    .box_content_title h1, .box_content_title_icon h1, .box_content_title h2, .box_content_title h3, .box_content_title h4, .box_content_title h5, .box_content_title h6 {
    font-family: "Raleway";
    font-size: 22px;
    text-align: left;
    text-transform: uppercase;
    color: #439da1;
  }
      
  .bg-grey .box_content_wrap {
    background: none repeat scroll 0% 0% #f6f6f6;
  }
  
  .logobox {
    background: none;
  }
      
  #content li a, #content a {
    color: #3098A1;
    text-decoration: none;
    font-weight: unset;
  }
  
  div.rte-content p {
    font-size: 15px;
  }
  
  div.buehne_shadow_top {
    background-position: 0px 0px;
    width: 0px;
    height: 0px;
}
  
  div.buehne_shadow_bottom {
    background-position: 0px 0px;
    width: 0px;
    height: 0px;
}
  
  .footer_spalte_inner a {
    font-size: 15px;
  }
  
  .footer_headline {
    font-family: "Raleway",sans-serif;
    font-size: 22px;
    text-transform: uppercase;
    color: #E6E6E6;
    padding: 0px 0px 0px 20px;
    border-top: none;
    border-bottom: none;
}
  
}
