css:
@-moz-document url-prefix(https://www.tumblr.com/dashboard), url-prefix(https://www.tumblr.com/blog), url-prefix(https://www.tumblr.com/settings){
 
body {
background-colour: /*[[bgc]]*/!important;
background-image: url('/*[[bgi]]*/')!Important;
background-attachment: fixed!important;
background-size: cover!important;
}

.l-content,.right_column,.right_column:after,#left_column {
background:transparent!important;
}
.l-header-container {
background-color:rgba(255,255,255,0.1);
border-bottom: 1px solid rgba(255,255,255,0.2);
}

.post_permalink {
border-color:transparent transparent!important;
background: /*[[bgc]]*/!important;
transition: .5s!important;
}

.tab_notice {
background-color: transparent!important;
border-color:transparent;
border-radius:100px;
}

.tab_notice * {
color:#fff!important;
}

.compose-button {
background-color:transparent;
border-color:transparent;
color: #fff;
margin-left:0px;
}

.logo {
opacity:0;
}

.ui_search {
width: 542px!important;
margin-left: 315px!important;
}

#search_query {
font-size:14px!important;
}
#popover_search {
margin-left: 224px;
margin-top: 20px;
width: 250px;
border-color:transparent!important;
}

.post_avatar.post-avatar--fixed {
position: absolute !important;
top: 0 !important;
left: -85px !important;
}

.post_avatar.post-avatar--absolute {
position: absolute;
top: 0 !important;
left: -85px !important;
bottom: inherit !important;
}

.post_avatar.post-avatar--sticky .avatar-wrapper {
position: absolute !important;
top: 0px !important;
height: auto;
width: auto;
}
.post_avatar.post-avatar--sticky {
height: 64px !important;
}

.controls_item:hover,controls_section_item:hover {
background:linear-gradient(transparent,#fff 200%)!important;
}

.item {
background:transparent!Important;
}

.item *:before,.radar_footer *:before {
background:transparent!Important;
border: 0px solid!important;
}

.item:hover,#tumblr_radar:hover {
background:linear-gradient(transparent,#fff 200%)!important;
}

.plus-follow-button {
color: salmon;
}
.plus-follow-button:hover {
background-color: /*[[acc]]*/!important;
}

.settings_group_wrapper {
background:#fff;
}

.post-forms-glass {
background-color: /*[[bgc]]*/;
background-image: url('/*[[bgi]]*/')!important;
background-attachment: fixed!important;
background-size: cover!important;
background-position: top right!important;
}
}
