css:
@-moz-document url(http://play.pokemonshowdown.com) {
body {
color: white;
font-size: 11pt;
font-family: Verdana,Helvetica,Arial,sans-serif;
background: #ffffff url(http://i.imgur.com/gK3978p.jpg) no-repeat left bottom fixed;
}

button {
  background: rgb(225,20,20);
  color: #fff;
  border: 1px solid #000;
}

button:hover {
  background: rgba(0,0,0,0.9);
  color: #fff;
  border: 1px solid #e11414;
}

.username {
  cursor: pointer;
  text-shadow: 1px 1px 0 rgba(255,255,255,0.1);
}

.header .logo {
    display: none;
}

#header {
    /*background: url(http://i.imgur.com/q79MNuF.png) no-repeat;*/
  background: url(http://i.imgur.com/N0fv8Ab.png);
}

.header {
    background: url(http://i.imgur.com/y0sUNIg.png);
}


.ps-room.ps-room-light.scrollable {
    background:url(http://i.imgur.com/9787QS1.jpg) no-repeat right bottom;
    background-color: rgba(217,217,217);
    box-shadow: -10px 0px 15px -5px #202020;
  }

.roomlist a.ilink {
  border: 1px solid rgb(225,20,20);
  background: #fff;
  color: #000;
}

  .roomlist a.ilink:hover {
  border: 1px solid #000;
  background: rgb(225,20,20);
  color: #fff;
  text-shadow: none;
}
      
h2 {
  color: rgb(225,20,20);
  text-shadow: 1px 1px 0 #000, 1px -1px 0 #000, -1px 1px 0 #000, -1px -1px 0 #000;
}

.menugroup .button {
  color: #FFF;
  border: solid 1px rgb(225,20,20);
  background: #000;
}

.menugroup .button:hover {
  color: #FFF;
  border: solid 1px #000;
  background: rgb(225,20,20);
}

.menugroup .button:active {
  color: #FFF;
  border: solid 1px #000;
  background: rgb(225,20,20);
}

.label {
  color: #000;
  text-shadow: 1px 1px 0 rgba(70,70,70,0.3);
}

.menugroup {
  background: rgba(215,215,225,0.25);
  text-align: center;
}

.mainmenufooter a {
  text-decoration: underline;
  color: rgb(225,20,20);
}

.mainmenufooter a:hover {
  text-decoration: underline;
  color: rgb(240,72,72);
}

.mainmenufooter small {
  color: rgb(200,200,200);
  font-size: 9pt;
}
    
.popupmenu h3 {
  margin: 5px 0 0 -2px;
  padding: 1px 0;
  font-size: 8pt;
  color: rgb(225,20,20);
}

.ps-popup {
  position: absolute;
  top: auto;
  right: auto;
  left: auto;
  bottom: auto;
  text-align: left;
  background: rgba(255,255,255,0.95);
  color: black;
  border: 2px solid #000;
  padding: 2px 10px;
  border-radius: 6px;
  box-shadow: inset 1px 1px 0 #ffffff,inset -1px -1px 0 #DDDDDD,2px 2px 3px rgba(0,0,0,.2);
  z-index: 20;
}

.popupmenu button.sel {
  padding: 0 3px;
  border: 1px solid #AAAAAA;
}

.popupmenu button.sel:hover {
  padding: 0 3px;
  border: 1px solid #AAAAAA;
  background: rgba(0,0,0,0.1);
}


.popupmenu button {
  display: block;
  cursor: pointer;
  font-size: 8pt;
  font-family: Verdana,Helvetica,Arial,sans-serif;
  margin: 0 0 0 6px;
  padding: 0 4px;
  border: 0;
  border-radius: 2px;
  background: transparent;
  color: black;
  width: 204px;
  height: 19px;
  text-align: left;
  box-sizing: border-box;
}

.popupmenu button:hover {
  display: block;
  cursor: pointer;
  font-size: 8pt;
  font-family: Verdana,Helvetica,Arial,sans-serif;
  margin: 0 0 0 6px;
  padding: 0 4px;
  border: 0;
  border-radius: 2px;
  background: rgba(0,0,0,0.2);
  color: black;
}
    
.pm-window h3 {
  background: rgba(255,255,255,0.8);
  color: #000;
  margin: 0;
  padding: 2px 5px;
  font-size: 11pt;
  border: 1px solid #e11414;
  border-top-left-radius: 5px;
  border-top-right-radius: 5px;
  cursor: pointer;
}
  
.pm-window h3:hover {
  background: rgba(245,245,245,0.85);
  color: rgb(225,20,20);
  margin: 0;
  padding: 2px 5px;
  font-size: 11pt;
  border: 1px solid #e11414;
  border-top-left-radius: 5px;
  border-top-right-radius: 5px;
  cursor: pointer;
} 


.pm-window.focused h3 {
background: rgba(0,0,0,0.6);
color: white;
border: 1px solid #e11414;
}

.pm-window.focused h3:hover {
background: rgba(0,0,0,0.65);
color: #e11414;
border: 1px solid #e11414;
}

.pm-window h3.pm-notifying {
    border-color: #d61530;
    background: black;
  color: white;
}

.pm-window h3.pm-notifying:hover {
    border-color: #0096ff;
    background: black;
    color: rgb(200,200,200)
}

.pm-window h3 small {
color: rgb(150,150,150);
}

.pm-window.focused h3,.pm-window.focused .pm-log,.pm-window.focused .pm-log-add {
    border-color: #e11414;
}
  
.pm-log {
  min-height: 100px;
  max-height: 300px;
  background: rgba(0,0,0,0.55);
  color: white;
  word-wrap: break-word;
  border: 1px solid #e11414;
  border-top: 0;
  border-bottom: 0;
  overflow: auto;
  -webkit-overflow-scrolling: touch;
  overflow-scrolling: touch;
}

.pm-log-add {
  border: 1px solid #e11414;
  border-bottom-left-radius: 5px;
  border-bottom-right-radius: 5px;
  background: rgba(0,0,0,0.55);
}

.challenge {
    margin-top: -1px;
    background: rgba(225,20,20,0.8);
    border: 0;
    color: white;
    padding: 1px 5px;
}

.maintabbarbottom {
  content: "";
  display: block;
  position: absolute;
  left: 0;
  right: 0;
  bottom: -6px;
  height: 6px;
  background: #fff;
  border: solid 1px #e11414;
  border-left: 0;
  margin: -1px 0 0 0;
  -webkit-box-shadow: 0 1px 2px rgba(0,0,0,.2);
  -moz-box-shadow: 0 1px 2px rgba(0,0,0,.2);
  box-shadow: 0 1px 2px rgba(0,0,0,.2);
}

.message-pm {
  color: #e11414;
}

.tabbar a.button {
    color: #fff;
      border: solid 1px #e11414;
  	background: #000;
    text-shadow: none;
}

.tabbar a.button:hover {
    background: #e11414;
  	color: #fff;
  border: solid 1px #000;
}

.tabbar a.button.cur, .tabbar a.button.cur:hover {
      background: #fff;  
  	color: #000;
    border-color: #e11414;
}
  

.tabbar a.button:active {
    background: #b21010;
}

.tabbar a.button.notifying {
    border: solid 1px black;
  border-bottom: solid 1px #e11414;
    background: #e11414;
   color: #fff;
}

.tabbar a.button.notifying:hover {
    border: solid 1px #e11414;
  border-bottom: solid 1px #e11414;
    background: #000;
  color: #fff;
}


.closebutton, .minimizebutton {
color: rgb(175,175,175);
background: none;
border: none;
}

.closebutton:hover, .minimizebutton:hover {
color: #e11414;
  background: none;
border: none;
}

.closebutton:active,.minimizebutton:active {
    color: #b00a0a;
  background: none;
border: none;
}

.select:hover {
  color: black;
}

.ladder th {
text-align: left;
font-size: 9pt;
background: #e11414;
  color: #fff;
} 

.ladder table, .ladder td, .ladder th {
border-collapse: collapse;
border: 1px solid #000;
}

.chat-log {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 40px;
  font-size: 10pt;
  background: url(http://i.imgur.com/P2fKuk0.png) no-repeat top right fixed;
  color: black;
  word-wrap: break-word;
  overflow: auto;
  overflow-x: hidden;
  -webkit-overflow-scrolling: touch;
  overflow-scrolling: touch;
}

.chat-log-add {
  position: absolute;
  bottom: 0;
  left: 0;
  right: 0;
  min-height: 36px;
  padding: 3px 4px 0 4px;
  border-top: 1px solid #AAAAAA;
  background: url(http://i.imgur.com/vC7j0Wn.png);
}

.ps-room.ps-room-light {
  /*background: rgba(242,247,250,.85);*/
    background: url(http://i.imgur.com/8ZQK4dh.png);
  color: black;
}

.userlist li {
  border-bottom: 1px solid rgb(70,70,70);
  height: 19px;
  background: url(http://i.imgur.com/vC7j0Wn.png);
  color: #e11414;
  font: 10pt Verdana,sans-serif;
  white-space: nowrap;
}

.userlist li button {
  box-sizing: border-box;
  display: block;
  height: auto;
  background: url(http://i.imgur.com/vC7j0Wn.png);
  border: 0;
  padding: 1px 0;
  margin: 0;
  height: 19px;
  width: 100%;
  white-space: nowrap;
  font: 9pt Verdana,sans-serif;
  text-align: left;
  cursor: pointer;
}

.userlist li button:hover {
  box-sizing: border-box;
  display: block;
  height: auto;
  background: rgba(255,255,255,0.1);
  border: 0;
  padding: 1px 0;
  margin: 0;
  height: 19px;
  width: 100%;
  white-space: nowrap;
  font: 9pt Verdana,sans-serif;
  text-align: left;
  cursor: pointer;
}

.setchart {
  border: 1px solid rgb(50,50,50);
  background: url(http://i.imgur.com/ftyEyEs.png);
}

.setchart label, .setchart-nickname label {
  display: block;
  font-size: 8pt;
  font-weight: bold;
  color: #FFFFFF;
  padding-left: 2px;
  text-shadow: none;
}

.setchart-nickname {
  position: absolute;
  left: -5px;
  top: 5px;
  border: 1px solid rgb(40,40,40);
  background: url(http://i.imgur.com/ftyEyEs.png);
  border-radius: 5px;
  padding: 1px 0 0 0;
  width: 110px;
  box-shadow: inset 1px 1px 0 rgba(255,255,255,.6);
}

.setmenu button {
  cursor: pointer;
  color: #000;
  background: transparent;
  border: 0;
  border-radius: 4px;
  padding: 3px 6px;
}

.setmenu button:hover {
  cursor: pointer;
  color: rgb(215, 30, 30);
  background: transparent;
  border: 0;
  border-radius: 4px;
  padding: 3px 6px;
}
    
    .setchart .setdetails label {
  float: none;
  color: #000;
  display: block;
  font-weight: normal;
  font-size: 7pt;
  text-align: center;
  padding: 0;
}
  
  .setdetails .detailcell {
  border-left: 1px solid #CCCCCC;
  padding: 2px 4px;
  display: block;
  float: left;
  font-size: 9pt;
  color: #000;
  text-align: center;
}
  
  .setchart .setstats label {
  float: left;
  width: 20px;
  height: 5px;
  text-align: right;
  font-weight: normal;
  font-size: 7pt;
  padding: 0;
  color: #000;
}
  
.setstats em {
  float: left;
  font-style: normal;
  width: 22px;
  height: 5px;
  color: #000;
  text-align: right;
}

.utilichart .typecol img {
  opacity: 0.75;
  margin-right: 1px;
}  
  
.utilichart .itemdesccol, .utilichart .abilitydesccol, .utilichart .movedesccol {
  color: #272727;
}
      
.utilichart .labelcol em, .utilichart .widelabelcol em, .utilichart .pplabelcol em, .utilichart .statcol em, .utilichart .bstcol em {
  color: #696969;
  font-size: 7pt;
  font-style: normal;
}

.teambuilder-results .firstresult a,.teambuilder-results .firstresult a:hover {
    padding: 0px 0px 0px 4px;
        border: 1px solid rgba(0,0,0,0);
    background: rgba(255,255,255,0.6);
}
        
.teambuilder-results .result a:hover {
  padding: 0px 0px 0px 4px;
        border: 1px solid rgba(0,0,0,0);
    background: rgba(255,255,255,0.6);
}

.teambuilder-results h3, .teambuilder-results .utilichart h3 {
  background: rgba(0,0,0,0.85);
  color: #fff;
  margin: 6px 0;
  padding: 2px 5px;
  font-family: Verdana,sans-serif;
  font-size: 9pt;
  text-align: left;
  border: 1px solid #e11414;
  border-left: 0;
  border-right: 0;
  text-shadow: none;
  box-shadow: none;
}
     
.jslider .jslider-label {
  color: #000;
  opacity: 0.9;
}

.utilichart .col {
  color: #000000;
}
      
.utilichart .numcol {
  width: 24px;
  padding-right: 5px;
  text-align: right;
  color: #e11414;
  padding-top: 7px;
  font-size: 8pt;
}

.teambar button {
  display: block;
  background: rgba(255,255,255,0.8);
  color: #000;
  border: 1px solid #e11414;
  padding: 0;
  margin: -1px 2px 0 0;
  height: 42px;
  float: left;
  width: 88px;
  cursor: pointer;
  white-space: pre;
  font-size: 8pt;
  font-family: Verdana,Arial,sans-serif;
}
      
.teambar button:hover {
  display: block;
  background: rgba(255,255,255,0.4);
  color: #000;
  border: 1px solid #e11414;
  padding: 0;
  margin: -1px 2px 0 0;
  height: 42px;
  float: left;
  width: 88px;
  cursor: pointer;
  white-space: pre;
  font-size: 8pt;
  font-family: Verdana,Arial,sans-serif;
}
          
.credits h2 {
  font-size: 11pt;
  font-style: italic;
  margin-bottom: 0;
  color: #e11414;
}
        
.credits ul {
  padding-left: 1.5em;
  margin-top: 0;
  margin-bottom: 0;
  color: #e11414;
}
        
a.subtle:hover {
  text-decoration: underline;
  color: #e11414;
}
          
.rightbar {
position: absolute;
top: 0;
right: 0;
height: 450px;
width: 100px;
background-color: #CCCCCC;
background-color: rgba(0,0,0,0.5);
border-left: none;
color: rgba(255,255,255,0.95);
}

.leftbar {
position: absolute;
top: 0;
right: 0;
height: 450px;
width: 100px;
background-color: #CCCCCC;
background-color: rgba(0,0,0,0.5);
border-right: none;
color: rgba(255,255,255,0.95);
}

.tournament-box {
  border-bottom: 1px #aaa solid;
  border-right: 1px #000 solid;
  background-color: rgba(20,20,20,0.85);
}

a.ilink {
  text-decoration: none;
  color: #e11414;
}

a.ilink:hover {
  text-decoration: underline;
  color: #ff5454;
}

.tournament-status {
  position: absolute;
  top: 0;
  padding: 0 0.5em;
  font-weight: normal;
  font-size: 80%;
  background-color: rgba(225,20,20,0.85);
  border-right: 1px #000 solid;
  color: #fff;
}
  
.tournament-toggle {
  position: absolute;
  top: 0;
  padding: 0 0.5em;
  font-weight: normal;
  font-size: 80%;
  background-color: rgba(225,20,20,0.85);
  border-left: 1px #000 solid;
  border-right: 1px solid #000;
  color: #fff;
}

.spoiler:hover {
  color: #000;
  background: transparent;
  border: 0;
  padding: 0px 3px;
}
    
.message-announce {
  background: #e11414;
    color: #fff;
}
  
.highlighted {
  background-color: rgba(255,30,30,0.5);
} 

.broadcast-blue {
background-color: #000;
color: white;
padding: 2px 4px;
}

.battle-log h2 {
  margin: 0.5em -8px;
  padding: 4px 8px;
  border: 1px solid #000000;
  background: rgba(0,0,0,0.2);
  border-left: 0;
  border-right: 0;
  font-family: Verdana,sans-serif;
  font-size: 13pt;
}

.battle-log {
  position: absolute;
  text-align: left;
  border: 1px solid #AAAAAA;
  background: url(http://i.imgur.com/thyfpo9.png);
  color: #000;
  top: 9px;
  left: 640px;
  right: -1px;
  height: 360px;
  font-family: Verdana,sans-serif;
  font-size: 10pt;
  overflow: auto;
  overflow-y: scroll;
  -webkit-overflow-scrolling: touch;
  overflow-scrolling: touch;
}

.ps-room.ps-room-opaque {
  background: url(http://i.imgur.com/TiYGq6E.png);
  color: #000;
}

.movemenu button:hover {
  float: left;
  display: block;
  width: 153px;
  margin-right: 6px;
  height: 40px;
  font: 10pt/100% Verdana,sans-serif;
  position: relative;
  padding: 6px 4px 0 4px;
  color: #000;
}
    
.moveselect button {
  color: #e11414;
  cursor: default;
}
  
.moveselect button:hover {
  color: #e11414;
  background: none;
  border: none;
  cursor: default;
}
      
.switchselect button {
  color: #445588;
  cursor: default;
}
    
.switchselect button:hover {
  color: #445588;
  cursor: default;
  background: none;
  border: none;        
}
        
.switchmenu button:hover {
  position: relative;
  display: block;
  float: left;
  width: 100px;
  margin-right: 6px;
  font: 9pt Verdana,sans-serif;
  padding: 5px 5px 5px 0;
  white-space: pre;
  overflow: hidden;
  color: #000;
}
}
