css:
@-moz-document domain(typeracer.com) {
  
  body {
    /* Main bg gray */
    background: #efefef;

  }


  .newNorthWidget {
    display: none;
  }
  .adblockWarning {
    display: none;
  }

  input:focus,
  select:focus,
  textarea:focus,
  button:focus {
      outline: none;
  }
  .title h1 {
    /* Moves title out of the way for bigger logo */
    margin-left: 25px !important;
  }

  .sectionHeaderRow {
    /* The header in the edit profile boxes were dark blue */
    color: #f2f2f2 !important;
  }
  
  /* Header container gets a deep blue, also set logo*/
  .container {
    background-color: #047AC6;
    /* http://ballicons.net/ Ballicons 2, TypeWriter icon */
    background-image: url(http://i.imgur.com/keiD24N.png);
    background-size: 92px 92px;
    background-repeat: no-repeat;
  }

  .themeHeader {
    /* Line below header */
    border-bottom: 4px solid #7DB6DB;
  }

  .main {
    /* Gray bg */
    background: #efefef;
  }

  .footer {
    /* colorful footer */
    background: #2893D8;
    color: white;
  }

  /* Top right user info, light headings */
  .mainUserInfoBox {
    color: #efefef;
  }

  a:hover, a {
    text-decoration: none;
  }

  .mainUserInfoBox a:hover {
    color: #7DB6DB;
  }
  .mainUserInfoBox a {
    color: white;
  }

  /* Simple edit button */
  .ImageButton {
    /* http://www.iconsdb.com/black-icons/edit-icon.html */
    background: url(http://i.imgur.com/RtREzsC.png) !important;
    background-size: 16px 16px !important;
    background-repeat: no-repeat !important;
    height: 16px !important;
    width: 16px !important;

  }
  
  .view {
    background: white;
    border: 1px solid #c3c3c3;
  }
  
  .view a {
    color: #047AC6 !important;
    text-decoration: none !important;
  }
  .rankPanelWpm {
    font-family: "Anonymous Pro", "Lucida Console", Monaco, monospace !important;
  }
  
  .gwt-TabBar .gwt-TabBarItem {
    color: #047AC6 !important;
    text-decoration: none;
  }
  
  .progressBar {
    border-bottom: 1px solid #047AC6 !important;
  }
  .view .ImageButton {
    display: none;
  }
  
  .playerInfoPopup, .PopupWithIcon, .editUserPopup, .countdownPopup {
    background-image: none;
    background-color: #047AC6 !important;
    color: white !important;
    border: 2px solid #7DB6DB;
  }
  
  
  .popupContent a, .playerInfoPopup a {
    color: white;
  }
  

  
  .countdownPopup img {
    display: none;
  }

  tr[height], td[height="5"], td[width="5"], td[width="14"] {
    display: none;
  }
  .chrome_m {
    background: none;
  }
  .room {
    background: none;
  }
  .room-title {
    background: white;
    	font-family: Arial, "Helvetica Neue", Helvetica, sans-serif;
	color: #047AC6;
	font-size: 22px;
	padding: 5px;
    font-weight: lighter !important;
	font-style: italic;
	text-transform: uppercase;
  }
  
  .gameStatusLabel, .arrowPopupText {
    color: #047AC6 !important;
    opacity: 1;
        transition: opacity .4s ease-in-out;
   -moz-transition: opacity .4s ease-in-out;
   -webkit-transition: opacity .4s ease-in-out;
  }
  .gameStatusLabel-flashOn, .arrowPopupText-flashOn {
    opacity: 0.66;
    transition: opacity .4s ease-in-out;
   -moz-transition: opacity .4s ease-in-out;
   -webkit-transition: opacity .4s ease-in-out;
  }
  
  .nonHideableWords, .hideableWords, .txtInput {
    font-family: "Anonymous Pro", "Lucida Console", Monaco, monospace !important;
  }
  
  .inputPanel {
    padding: 10px;
    border: 1px solid #c3c3c3;
  }
  
  .txtInput {
    background: none !important;
    border: none !important;
    border-bottom: 2px solid #047AC6 !important;
  }
  .txtInput-error {
    background: none !important;
    border-bottom: 2px solid red !important;
  }
  
  [style="text-decoration: underline; color: rgb(153, 204, 0);"], [style="color: rgb(153, 204, 0);"] {
    color: #047AC6 !important;
    text-decoration: none !important;
  }
  
  .view {
    width: 700px;
  }
  
  .scoreboard, .statsView, .mainMenu, .textInfoView, .TextStatsView {
    width: 100%;
    margin-top: 1em;
    margin-bottom: 1em;
    padding: 10px;
    border: 1px solid #c3c3c3;
  }
  
  .mainMenu {
    width: 622px !important;
  }
  .textInfoView {
    border-bottom: none;
    margin-bottom: 0;
  }
  .TextStatsView {
    width: 622px !important;
    border-top: none;
    margin: 0;
    padding: 10px;
  }
  
  .statsView {
    width: 622px !important;
  }
  
  .view > tbody:nth-child(1) > tr:nth-child(2) > td:nth-child(1), .view > tbody:nth-child(1) > tr:nth-child(2) > td:nth-child(3) {
    display: none;
  }
    
  #tr_textHeader h1, #tr_textHeader h2 {
    color: #047AC6 !important;
  }
  
  .newSouthWidget {
    display: none;
  }
 
  /* Ad removal */
  .mainMenuAdUnit {
    display: none;
  }

  
}
