css:
@-moz-document
regexp("(http|https)://www.gamefaqs.com.*"),
regexp("(http|https)://gamefaqs.gamespot.com.*")
    {
      body
      {
        background: #1C1C1C !important;
      }
      
      h2 a
      {
        color: #0973BF !important;
      }
      
      .name, .friend_info
      {
        color: #FAFAFA !important;
      }
      
      .masthead a, .content_nav > li > a, .masthead_user a
      {
        color: #FAFAFA !important;
      }
      
      .no-touch .masthead a:hover
      {
        color: #FAFAFA !important;
        text-decoration-line: underline;
      }
      
      .masthead_user a:visited, .crumb::before
      {
        color: #FAFAFA !important;
      }
      
      .page-header .page-title
      {
        text-shadow: none !important;
      }
      
      .pod_topgames li .count, .pod_topgames li .sub
      {
        color: #FAFAFA !important;
      }
      
      .pod_topgames li
      {
        background: #161616 !important;
      }
      
      .pod_featured_top10 div.slide
      {
        background: #161616 !important;
      }
      
      .pod_popular_board li
      {
        background: #131313 !important;
      }
      
      .footer a
      {
        color: #FAFAFA !important;
      }
      
      s
      {
        background: #131313 !important;
        color: #131313 !important;
      }
      
      s:hover
      {
        background: #131313 !important;
        color: #FAFAFA !important;
      }
      
      th a
      {
        color: #FAFAFA !important;
      }
      
      .paginate > li
      {
        color: #CCCCCC !important;
      }
      
      .content_nav
      {
        box-shadow: none !important;
        border: solid white 1px !important;
      }
      
      .amzn
      {
        background: linear-gradient(#3A3A3A, #303030) !important;
      }
      
      .amzn_txt
      {
        color: #FAFAFA !important;
      }
      
      .amzn_price
      {
        text-shadow: none !important;
      }
      
      .btn
      {
        background: linear-gradient(#3A3A3A, #303030) !important;
        color: #FAFAFA !important;
        text-shadow: none !important;
      }
      
      .paginate > li > a
      {
        background: linear-gradient(#3A3A3A, #303030) !important;
        color: #FAFAFA !important;
        text-shadow: none !important;
      }
      
      .user_subnav, .content_subnav
      {
        background: #0A0A0A !important;
        color: #FAFAFA !important;
      }
      
      .ffaq pre.faqtext, .board_poll, .board.message .msg_infobox:not(.deleted), .tinynav, .reg_dialog
      {
        background: #0A0A0A !important;
        color: #FAFAFA !important;
      }
      
      .board.message td, .board.message:not(.newbeta) tr:nth-child(2n+1) td.msg, .board.message.newbeta tr:nth-child(2n-1) td {
        background: #1C1C1C !important;
        color: #FAFAFA !important;
      }
      
      .pod_gs, ul.systems li:nth-child(2n+1), .body.settings div.row:nth-child(2n+1)
      {
        background: #0A0A0A !important;
      }
      
      .pod_gb
      {
        background: #0A0A0A !important;
      }
      
      .pod_topten li.showcase a
      {
        background: #0A0A0A !important;
      }
      
      .mygames_section
      {
        background: #0A0A0A !important;
      }
      
      h3, .subsection-title
      {
        color: #FAFAFA !important;
      }
      
      .pod_gameinfo li.esrb, .board.message .msg_infobox .post_time
      {
        color: #FAFAFA !important;
      }
      
      .pod_topten .body a span, .pod_topten .body a span.count
      {
        color: #FAFAFA !important;
      }
      
      .pod_popular_board span a
      {
        color: #CCCCCC !important;
      }
      
      .pod_gameinfo li.metacritic
      {
        background: #0A0A0A !important;
      }
      
      .pod_related p
      {
        color: #FAFAFA !important;
      }
      
      .pod_topten li:nth-child(2n+1)
      {
        background: #0A0A0A !important;
      }
      
      .pod_popular_board li:nth-child(2n+1)
      {
        background: #0A0A0A !important;
      }
      
      .board.message tr.left:nth-child(2n+1) td.msg
      {
        background: #1C1C1C !important;
        color: #FAFAFA !important;
      }
      
      .pod_poll .fields
      {
        background: #0A0A0A !important;
      }
      
      .board.message .author
      {
        background: #0A0A0A !important;
        color: #FAFAFA !important;
      }
      
      .forum_anon
      {
        background: #2C2C2C !important;
      }
      
      .wrapper
      {
        background: #1C1C1C !important;
      }
      
      #content
      {
        background: #1C1C1C !important;
        color: #FAFAFA !important;
      }
      
      tr:nth-child(2n+1) td
      {
       background: #1C1C1C !important;
        color: #FAFAFA !important;
      }
      
      html
      {
        background: #1C1C1C !important;
      }
      
      a, a:visited, a:hover, a:active, .tinynav a
      {
        color: #0973BF !important;
      }
      
      .board.topics span:not(.pglist) a
      {
        color: #0973BF !important;
      }
      
      .post_author a
      {
        color: #0973BF !important;
      }
      
      h1 a
      {
        color: #FAFAFA !important;
      }
      
      h1 a:hover
      {
        color: #FAFAFA !important;
      }
      
      .ffaq_page
      {
        background: #1C1C1C !important;
      }
      
      span[id^="faqspan"]
      {
        color: #FAFAFA !important;
      }
    }
