css:
@-moz-document regexp(".*whirlpool.net.au.*") {
body { font-family: Verdana, 'Helvetica Neue', Helvetica, Arial, sans-serif }

/* thread listings */
#threads table tbody tr td.title a.title { font: bold 14px 'Helvetica Neue', Helvetica, Arial, sans-serif }

/* thread listings group tag */
#threads table tbody tr td.title a.group { font: normal 10px 'Helvetica Neue', Helvetica, Arial, sans-serif; }

/* thread listings replies and unread */
#threads table tbody tr td.reps, #threads table tbody tr td.reads, #threads table tbody tr td.unread { font: normal 14px 'Helvetica Neue', Helvetica, Arial, sans-serif; }
  
/* thread view - posters' names */
#replylist div.reply div.replymeta div.replyuser span.bu_name { font: bold 16px 'Helvetica Neue', Helvetica, Arial, sans-serif }

/* thread view - body text */
#replylist div.reply div.replytext { font: normal 13px Verdana, 'Helvetica Neue', Helvetica, Arial, sans-serif }

/* thread pagination & last post times */
#content ul.pagination { font: normal 16px Georgia, serif; }

/* LHS navigation - category headings */
#left ul#menu > li > a { font: normal 22px Georgia serif; }

/* search results - thread titles */
#searchresults ol.results { font: 22px 'Helvetica Neue', Helvetica, Arial, sans-serif; }

/* navigation - breadcrumbs */
#upperbar ul.breadcrumb { font: normal 22px Georgia, serif; }

#forumindex table td.title div.title a { font: bold 16px 'Helvetica Neue', Helvetica, Arial, sans-serif }
}
