css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wikipedia.org") {

    /* Hiding options */
    #centralNotice, #n-randompage, #n-contact, #n-alphindex, #n-thema, #p-Contribuer, #p-tb, #footer-info-copyright, #footer-places, #p-interaction, #p-navigation, #p-wikibase-otherprojects, .after-portlet-lang, #p-coll-print_export, .portlet, #mw-panel h3, .interlanguage-link, #toctitle, #p-lang .uls-settings-trigger, #p-cactions-label, #ca-ve-edit, #pt-mytalk, #pt-betafeatures, #pt-watchlist, #ca-watch, #footer-icons  {display:none !important;}

    /* Selection of 2 LANGUAGES MAXIMUM - CHECK ALSO THE COMMA */
       /*.interwiki-ar Arabic,*/
       /*.interwiki-bn Bengali,*/
       /*.interwiki-zh Chinese,*/
       .interwiki-fr /*French*/,
       .interwiki-en /*English*/,
       /*.interwiki-de German,*/
       /*.interwiki-hi Hindi,*/
       /*.interwiki-id Indonesian,*/
       /*.interwiki-it Italiano,*/
       /*.interwiki-jp Japanese,*/
       /*.interwiki-pt Portuguese,*/
       /*.interwiki-ru Russian,*/
       .interwiki-es /*Spanish*/
       /*.interwiki-tr Turkish,*/
                {display: inline !important;margin-right:10px !important;}
        #p-lang {padding: 0px !important;}

    /* Table of content */
       #content, #left-navigation {margin-left: 180px !important;}
       #content .toc {
          display: block !important;
          position: fixed ;
          top: 70px; left: 0px ; bottom:0px!important;
          width: 180px !important;
          text-indent: 0 ;
          overflow-y: auto;
          padding: .6em !important;
          padding: 0px!important;
          border: none !important; }
       #content #toc a  {color: #0645AD !important; }
       #toc .toctoggle {display: none !important;} 
       #toc .toclevel-1 {
          padding-left: 3px !important;
          font-size: 12px !important;
          line-height: 15px !important;
          padding-bottom: 8px !important;
          font-weight: bold !important;}
       #toc .toclevel-2 {
          font-size: 10px !important;
          line-height: 12px !important;
          padding-bottom: 3px !important;
          font-weight: normal !important;
          margin-left: -14px !important;}
       #toc .toclevel-3 {display: none !important;}

    /* Left side */
       #mw-panel {
          top:30px !important;
          height:0px !important;
          padding-top: 0px !important;}
       #mw-panel #p-logo {
          height: 25px !important;
          width:25px !important;
          top:-30px !important;}
       #mw-panel #p-logo a {
          height: 30px !important;
          background-size: 100% !important;
          background-position: 50% 50% !important; }
}
