css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.outdoorforum.cz") {
  
#ff-iridium{ font-family: "Roboto", "Lucida Grande",Tahoma,Verdana,Helvetica,sans-serif;  }
div.wrapper { max-width: 85%; min-width: 970px; width: 100%;  }
html{background: #669748;}
  
body, body.f-default, .content {  line-height: 1.5em; }



#horiz-menu div.wrapper { max-width: 100%;  width: 100%;  }
.menutop li.root > .item{ font-size: 1em; }
ul.topics li { font-size: 0.9em; }
li.row, li.row:hover  { background: white none; }
li.row.bg2, li.row.bg2:hover { background: #f8f8f8 none; }
/*li.row:hover, li.row.bg2:hover{background: #ececec;}*/
a, #top-right ul li a:hover, .abstract-menu li a:hover, #main-content .cart_totals div, #roksearch_results .roksearch_odd-hover h3, #roksearch_results .roksearch_even-hover h3, #horiz-menu.splitmenu li:hover .item span, #horiz-menu.splitmenu li.active .item span, #horiz-menu.splitmenu li.active:hover .item span { color: #0069b4; }
.icon a { color: #5f5f5f; }
.icon .topictitle, .topiclist  .forumtitle { color: #0069b4; font-size: 1.1em; }
dl.icon dt {padding-left: 10px; }
ul.topiclist li.row dl { background: none repeat scroll 0 0 rgba(0, 0, 0, 0) !important; }
.pohora-and-banner{ display: none; }
#top-pohora-menu, #breadcrumbs, #pohora-and-banner, .colright, .bottom-menu, .banner-6{ display: none; }
#pohora-menu{background: transparent none; padding-top: 5px;}
  #pohora-menu .logo img{width: 100%; height: auto; display: block;}
#horiz-menu { background: none repeat scroll 0 0 rgba(255, 255, 255, 0.85); margin-top: 0; moz-border-radius: 5px; border-radius: 5px; -webkit-border-radius: 5px; margin: 0 0 5px 0;}
.postprofile { font-size: 0.8em; }
#page .post {border: 0; border-top: 1px solid #C4CACE; background: #f2f2f2; background: -moz-linear-gradient(top,  #f2f2f2 0%, #ffffff 17%); background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#f2f2f2), color-stop(17%,#ffffff)); background: -webkit-linear-gradient(top,  #f2f2f2 0%,#ffffff 17%); background: -o-linear-gradient(top,  #f2f2f2 0%,#ffffff 17%); background: -ms-linear-gradient(top,  #f2f2f2 0%,#ffffff 17%); background: linear-gradient(to bottom,  #f2f2f2 0%,#ffffff 17%); }
.postbody h3 a{ color: #646464;  }
.postbody h3 { font-weight: 400; }
.pagination span a, .pagination span strong{ padding: 0 !important; line-height: 2em !important; min-width: 2em; text-align: center; display: inline-block; }
.pagination span strong, .pagination span a:hover{background: #0069b4 !important;; border-color: #0069b4; color: white !important;; font-weight: bold;}
.reply-icon span { display: none; }
.buttons div.reply-icon { height: auto; width: 150px; padding-bottom: 5px; }
.buttons .reply-icon a { line-height: 25px; height: auto; background: none repeat scroll 0 0 #0069b4; background-position: 0 0; color: white; display: block; font-size: 1.1em; height: 100%; overflow: hidden; padding: 7px 0; position: relative; text-align: center; width: 100%; -moz-border-radius: 4px; border-radius: 4px; -webkit-border-radius: 4px; }
.buttons .reply-icon a:hover{ color: white; background: gray; text-decoration: none; }
.topic-actions { height: auto; min-height: 48px; clear: both; }
#top-right-surround { margin: 5px 10px -60px 0; max-width: 180px; position: relative; z-index: 20; }
.section-index #top-right-surround { margin: 5px 10px 0 0; }
#page-body > h2:first-child { margin-top: 0; margin-right: 200px; }
.x-c-s .col3 { border: 1px solid #ddd; box-shadow: none; }
#main-body .x-c-s .col3 ul.menu li a { color: black; }
#searchmod-surround .inputbox { border: 1px solid #c2c2c2; box-shadow: 2px 2px 2px #ddd inset; outline: medium none; padding: 10px; width: 90%; -moz-border-radius: 5px; border-radius: 5px; -webkit-border-radius: 5px; }
#topic-search input{ padding: 8px;  }
#topic-search #search_keywords{ background-position: 5px center; padding-left: 30px; color: black }
.postbody h3 {  font-size: 1.3em; }
#page-body h2:first-child~p { float: left; }
li.header dl.icon dt { padding-right: 0; }
blockquote { background-color: #f8f8f8; border-color: #e3e3e3; -moz-border-radius: 5px; border-radius: 5px; -webkit-border-radius: 5px; font-size: .9em;  line-height: 1.3em; color: #434343; margin: 1em 0; }
ul.profile-icons { padding-top: 3px; }
.postprofile dd:nth-of-type(2){ display: none; }
.postprofile .pm-icon > a {margin: 0; padding: 0; line-height: 1.2em; text-align: center; width: auto; height: auto; background: none repeat scroll 0 0 white; border: 1px solid silver; color: #646464; font-size: 1.2em; font-weight: bold;  -moz-border-radius: 2px; border-radius: 2px; -webkit-border-radius: 2px;}
.postprofile .pm-icon > a:after{ content: "SZ";  }
.postprofile .pm-icon > a:hover{ background: #0069b4; border-color: #0069b4; color: white; text-decoration: none; }
.postprofile .pm-icon > a span{ display: none; }
ul.profile-icons li.pm-icon { height: auto; }
.postprofile  dt { font-size: 1.2em; }
.postprofile{ -moz-border-radius: 5px; border-radius: 5px; -webkit-border-radius: 5px; }
.topics .lastpost > span {    line-height: 1.3em;}
.row .pagination {    background-image: none;}
.postprofile dt img{width: auto; height: auto; max-height: 70px; max-width: 50px;}
.postprofile dd img{width: 50px; height: auto;}
.signature {   font-size: 0.85em;    font-weight: normal !important;    line-height: 140%;    margin-top: 1.5em; opacity: .75;}
.inner {    padding: 10px 0;}
.inner .corners-top, .margin-fix{display: none;}
  a.button1, input.button1, a.button2, input.button2, input.button3 { border-color: silver; color: #383838; -moz-border-radius: 5px; border-radius: 5px; -webkit-border-radius: 5px; background: #f8f8f8; box-shadow: -1px -1px 1px rgba(0,0,0,.1) inset; }
a.button1:hover, input.button1:hover, a.button2:hover, input.button2:hover, input.button3:hover { border-color: #0069b4; color: white; background: #0069b4; }
body.full #bg-main { background-position: 0 -130px; background-attachment: fixed; }
/*#postingbox fieldset.fields1 dd input { width: 100% !important; }*/
#main-trans, #main-trans-top, .colmask, #mainmodules7 { background: white; -moz-border-radius: 5px; border-radius: 5px; -webkit-border-radius: 5px; }
a, input{ -webkit-transition: color, background-color, border-color, border-color  0.4s; -ms-transition: color, background-color, border-color  0.4s; -moz-transition: color, background-color, border-color  .4s; -o-transition: color, background-color, border-color  .4s; transition: color, border-color, background-color 0.4s; }
#div-gpt-ad-1411633631558-1{display: none;}
#postingbox fieldset.fields1 dd input{width: 55% !important;}
  

/*na web*/
.section-index #pohora-and-banner, .section-search #pohora-and-banner{display: block; -webkit-box-sizing: border-box; -moz-box-sizing: border-box; box-sizing: border-box;  -webkit-background-clip: padding-box;  -moz-background-clip: padding;  background-clip: padding-box;}
.banner-4, .banner-8, #pohora-and-banner h2, .banner-1{display: none;}
#pohora-rss .item{float: left; width: 50%; margin: 0; border: 0; font-size: .9em; background: transparent none; padding: 10px;}
#pohora-rss .item+.item{border-left: 1px solid silver;}
#pohora-rss-items, #pohora-rss-items .item{-webkit-box-sizing: border-box; -moz-box-sizing: border-box; box-sizing: border-box;  -webkit-background-clip: padding-box;  -moz-background-clip: padding;  background-clip: padding-box;}
#pohora-rss h3 { font-size: 1.2em;  margin: 0.5em 0 0.5em 10px;    padding: 0; margin: 0; line-height: 1.2em;}
#pohora-rss .item img{width: 100%; height: auto; }
#pohora-rss .item a.image{width: 25%; padding: 0 10px 0 0;}
#pohora-rss .text .desc{margin: 0;}
#pohora-menu a.logo{display: block; float: left; background: rgba(255,255,255,.85); padding: 8px 10px;  moz-border-radius: 5px; border-radius: 5px; -webkit-border-radius: 5px; margin: 0 5px 5px 0; width: 7%;}
#pohora-menu a.logo+a.logo{width: 20%;}
.blockContent {
    display: none;
}
  


}
