css:
@-moz-document domain("sbb.ch") {
  html {
    background: #000 !important;
  }
  body {
    background-color: #000;
    color: #707070 !important;
    background: #121212 !important;
  }
  .richtext p, p.base, ul.base, ol.base {
    color: #707070;
  }
  .layoutHd, .layoutFt {
    background-color: #000;
  }
  .rightCol, .modNhTile {
    display: none !important;
  }
  .head {
    background-color: #000 !important;
  }
  .modMainNavigation a.toplevel {
    color: #707070;
  }
  .newhome .sbb-1col {
    margin-left: 0%;
  }
  .modNhFahrplan.skinNhFahrplanFahrplan .fahrplanBox  {
    background-color: #000;
    color: #707070;
  }
  .modNhFahrplan.skinNhFahrplanFahrplan .boxHeadline, .modNhFahrplan.skinNhFahrplanFahrplan .nhInputfield label.tiny {
    color: #707070;
  }
  .js .expandableElmt .expandableHead h2, .expandableElmt .expandableHead h2, .expandableElmt .expandableHead h2 a, .expandableElmt .expandableHead h2 a:visited {
    background-color: #111111;
  }
  .borderRadiusSmall {
    border-radius: 0px;
  }
  .modNhFahrplan.skinNhFahrplanFahrplan .inputFieldTextStyle, .modNhFahrplan.skinNhFahrplanFahrplan .inputFieldDateStyle {
    border: none;
  }
  .foot {
    background: #000 !important;
  }
  .layout-timetable .layoutBd, .layout-timetable .layoutHd, sbb-col-inner {
    background-color: #000;
  }
  div.hac_greybox {
    background-color: #000;
  }
  .hafas a, .hafas a:link, h1.base {
    color: #707070;
  }
  .bg-corner-topleft, .bg-corner-topright, .bg-corner-bottomleft, .bg-corner-bottomright {
    filter: invert(100%);
    -webkit-filter: invert(100%);
  }
  .sbb-col-middle .layoutFt, inner {
    background: #000;
  }
  .layout-timetable .layoutBd, .sbb-col-middle .layoutBd {
    background-color: #000;
    background-image: none;
  }
  tr.zebra-row-0 td, tr.zebra-row-1 td {
    background-color: #000;
  }
  table.hfs_trainsearch th, table.hfs_traininfo th, table.hfs_detail th, table.hfs_stboard th, table.hfs_overview th {
    background-color: #000;
    color: #707070;
  }
  table.hfs_overview tr.dateHint td {
    background-color: #000;
  }
  table.hfs_overview tr.overview td {
    border-bottom: 2px solid #707070;
    background-color: #000;
  }
  div.browse {
    background-color: #000;
  }
  table.hfs_detail tr.details td {
    background-color: #121212;
  }
  table.hfs_detail td.navigation {
    background-color: #121212 !important;
  }
  table.hfs_detail tr.details_disinherit td.details {
    background-color: #121212;
  }
  table.hfs_overview div.buttonHolder, table.hfs_overview div.detailsFares, table.hfs_overview div.baimInformation {
    background-color: #121212;
  }
  div.detailsContent {
    background-color: #121212;
  }
  td.additionalContent {
    background-color: #121212;
  }
  div.detailsContent {
    border-bottom: 0px;
  }
  .megadropdown_shadow .hd, .megadropdown_shadow_top_left,
  .megadropdown_shadow_top_right, .megadropdown_shadow_bottom_left,
  .megadropdown_shadow_bottom_right, .megadropdown_shadow .ft,
  .megadropdown .megadd_inner .bd ul.submenu,
  .megadropdown_shadow_middle_left, .megadropdown_shadow_middle_right {
    background: transparent;
  }
  .megadropdown ul a.secondlevel strong {
    color: #000;
  }
  .megadropdown ul a strong {
    color: #000;
  }
  .megadropdown .megadd_inner .bd ul.submenu li {
    background: repeat-x scroll left top #039;
  }
  .modSubNavigation a, .modSubNavigation a:link, .modSubNavigation a:visited {
    color: #707070;
  }
  .leftCol .layoutBd, .rightCol .layoutBd, .mainBd .layoutBd {
    background-color: #000;
  }
  .modSubNavigation a.icon, .modSubNavigation a.icon:link, .modSubNavigation a.icon:active, .modSubNavigation a.icon:visited, .modSubNavigation a.icon:focus {
    background: #000;
  }
  .modLogo .bd {
    opacity: 0.6;
  }
  .modNhFahrplan.skinNhFahrplanFahrplan .fieldsetFahrplansuche li.string input, input, .hafas .textarea, .hafas .select, .hafas .input {
    color: #fff;
  }
  #suggestion{
    background-color: #121212;
    border: 0px;
  }
  #suggestion li.infoLabel, #suggestion li.infoLabel {
    border:0px;
    background-color: #000;
  }
  #suggestion .selected{
    background-color: #000;
  }
  #suggestionMenu a {
    color: #039;
  }
  .modTabs .tabInner {
    background: none repeat scroll 0% 0% #000;
  }
  #sbb_timestamp {
    color: #707070;
    background-color: #000;
  }
  .acc-item {
    background-color: #000;
    border: 1px solid #707070;
  }
  .acc-body {
    background-color: #121212;
    color: #707070;
  }
  * {
    color: #707070;
  }
  div.hfs_whitebox {
    background-color: #000;
  }
  div.header {
    background: #000;
  }
  .hafas .textarea, .hafas .select, .hafas .input {
    border: 1px solid #303030;
  }
  .accordion {
    background-color: #000;
    margin-top: -2px;
  }
  .modTabs .tabInner .modAccordion {
    background-color: #000;
    color: #707070;
  }
  .skinTableLines table thead th {
    background: #121212;
  }
  .expandableElmt .expandableBody {
    background-color: #000;
  }
  .megadropdown_shadow_top_right {
    background-position: 0px -200px;
    right: -93px;
    top: 0px;
  }
  .megadropdown_shadow_bottom_right {
    right: -7px;
    top: -102px;
  }
  .megadropdown_shadow_bottom_left {
    top: -102px;
  }
  .expandableElmt .expandableHead h2, .js .expandableElmt .expandableHead h2.expanded {
    color: #707070;
  }
  .modMosaic h2 a, .modMosaic h2 a:link, .modMosaic h2 a:visited {
    background-color: #121212;
  }
  .skinTableLines table th .bl, .skinTableLines table th .br, .skinTableLines table th .tl, .skinTableLines table th .tr {
    background-image: none;
  }
  .textrouteView {
    background-color: #121212;
  }
  .nhIcon {
    -webkit-filter: invert(100%);
  }
  .modNhFahrplan.skinNhFahrplanFahrplan .fieldsetFahrplansuche li.string input, input, .hafas .textarea, .hafas .select, .hafas .input {
  color: #fff;
  background-color: #121212;
  }
}
