css:
/* AGENT_SHEET */
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("zip.4chan.org"),domain("img.4chan.org"),domain("orz.4chan.org"),domain("cgi.4chan.org"),domain("status.4chan.org"),domain("bin.4chan.org") {
body {
 background: #2C2C2C !important;
 color: #eee !important;
}

a {
 color: #efefef !important;
}

hr {
 display: none !important;
}

#navtop {
 background: #000000 !important;
 width: 99.6% !important;
 text-align: center !important;
 color: #eee !important;
 padding: .4em !important;
 top: 0;
 z-index: 1;
}

#navtop a:hover {
 background: #555 !important;
}

.logo,.logo font b span{
 color: #fff !important;
}

form {
 margin: 1em !important;
}

.inputtext, textarea {
 width: 295px !important;
}

.rules{
 display: none !important;
}

td.postblock {
 background: #444 !important;
 border: 1px solid #666 !important;
 color: #aaa !important;
}

td.reply {
background-color:#333 !important;
width: 100% !important;
padding: .2em !important;
border: 1px solid #555 !important;
color: #eee !important;
-moz-border-radius:3px !important;
}

td.replyhl {
background-color:#4B4B4B !important;
width: 100% !important;
padding: .2em !important;
border: 1px solid #4B4B4B !important;
color: #eee !important;
-moz-border-radius:3px !important;
}

.postername, .commentpostername, .omittedposts, .abbr {
 color: #fff !important;
}

.filetitle, .replytitle {
color: #e15014 !important;
font-size: large;
}

.doubledash {
visibility: hidden !important;
}

th {
 background: #222 !important;
 padding: .2em !important;
 font-size: small;
 width: 200px !important;
}

#hd {
display: none;
}

.boxbar {
background: #444 !important;
color: #eee !important;
}

.box-outer {
border: none !important;
background: #333 !important;
color: #eee !important;
}

.box-inner {
 background: #333 !important;
color: #eee !important;
}

#filter-button, #option-button {
display: none;
}

#ft{
display: none;
}

.pages {
 background: #444 !important;
 border: none !important;
}

.pages td, .pages td b {
color: #eee !important;
}

#recent-images li{
 background: #333 !important;
}
pre, textarea {
   color: inherit !important;
   background: #333333 !important;
   font-size: 12pt ! important;
   border-left-color: gray !important;
   border-right-color: gray !important;
   border-top-color: gray !important;
   border-bottom-color: gray !important;
}
input:not([type="button"]):not([type="checkbox"])
:not([type="submit"]):not([type="reset"]), 
select {
    color: inherit !important;
    background: #333333 !important;
    border-left-color: gray !important;
    border-right-color: gray !important;
    border-top-color: gray !important;
    border-bottom-color: gray !important;
} 
.postarea input[type="file"]{
opacity:0.13 !important;
}

}
