css:
@-moz-document domain("gamedev.ru") {
table.r td b a:link, a:link {
    color: #7196BB;
}

table.r td b a:visited, a:visited {
    color: #1E4E8C;
}

#container, #main {
    background: #000;
}

#header > *:not(#sitename) {
    display: none;
}

#path {
    background: #222;
    border: 1px solid #333;
    text-shadow: 0 1px #222;
}

#sitename {
    color: #789;
    font-style: normal;
}

div.docs {
    border: 2px solid #333;
    padding: 0;
}

div.docs .menuco {
    border: 0;
    border-bottom: 2px solid #333;
    margin: 0;
}

ul.menu li {
    background: #111;
}

ul.menu li ul {
    background: #111;
}

ul.menu li.sel {
    background: #222;
}

ul.menu li ul li {
    background: #111;
}

ul.menu li ul li.sel {
    background: #222;
}

ul.menu a {
    color: #99F;
}

div.menuco {
    background: #111;
}

table.mes th {
    background: #111;
}

table.mes th.red {
    background: #221111;
}

table.mes .level, td.co, div.menuitem {
    background: #222;
}

table.mes .center {
    background: #111;
}

table.mes td {
    background: #111;
}

table.mes th a:link, table.mes th a:visited {
    color: #007ECC;
}

div.mes img {
    filter: grayscale(100%) brightness(25%);
    transition: filter 0.5s;
}

div.mes img:hover {
    filter: none;
}

div.quote {
    background: #333;
    border: 0;
    border-left: 5px solid #555;
}

.spoiler {
    border: 1px solid #333;
}

.thinbut, #_gdr_preview, #_gdr_post {
    background: #222;
    border: 1px solid #333;
    color: #AAA;
}

.thinbut:hover, #_gdr_preview:hover, #_gdr_post:hover {
    background: #333;
    border: 1px solid #444;
    color: #BBB;
}

a:link {
    color: #99F;
}

a:visited {
    color: #999;
}

body {
    background: #111;
}

div {
    color: #AAA;
}

div#ad {
    display: none;
}

div.list {
    background: #111;
}

div.mes {
    border: 2px solid #333;
    padding: 0;
}

div.title {
    background: #111;
}

h1.title {
    background: #111;
    color: #AAA;
}

table.mes {
    border-bottom: 2px solid #333;
    border-spacing: 0;
}

table.r {
    border: 1px solid #333;
}

table.r td {
    background: #111;
}

table.r th {
    background: #111;
    border-bottom: 2px solid #333;
}

table.r tr.red td {
	background-color: #221111;
}

table.r tr.sec td {
    background: #222;
}

ul.menu li ul li.sel, ul.menu li ul li {
    border-top: 0;
}

textarea.gdr {
    background: #111;
    border: 1px solid #333;
    color: #AAA;
}

#footer {
    border: 1px solid #333;
    text-shadow: 0 1px #111;
}

div[style^='background: #f2f2f2'] {
    -webkit-filter: invert(1.0);
    filter: invert(1.0);
}

div.offtop {
    border: 2px solid #333;
}

.pages a {
    background-color: #335;
}

.pages a:hover {
    background-color: #446;
}

div#main_body div.text {
    background-color: #111;
}

div.menu {
    border: 1px solid #333;
    padding: 0;
}

div.menu > * {
    padding: 0;
    border: 0;
}

div.menucode {
    background-color: #222;
}

span.splink {
    color: #99F;
    border: 0;
}
}
