css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document  regexp("(https|http)://(cytu.be|ssl.cytu.be)/?/(?!blog).*") {

  body { color: #C8FFB7; background-color: #06090C; line-height: 1.4; font-size:13px; -webkit-font-smoothing: antialiased; -moz-osx-font-smoothing: grayscale; }
  .fluid { overflow-x: hidden; overflow-y: auto;}
  #messagebuffer  { background-color:#06090C; overflow-x: hidden; overflow-y: auto; }
  .channel-emote { background-color: transparent !important; max-height: 75px !important; max-width: 60px !important;}
  .nick-hover {background-color: #1D1D21 ;}
  #userlist { background-color: #15141B; overflow-x: hidden; overflow-y: auto;}
  .userlist-op { color: #7392FF !important; }
  #chatheader {text-align: left !important;}
  #userlisttoggle {margin-left: 7px; margin-top: 2px;}
  .form-control {color: #9FABFF !important;}
  #motdwrap {background-color: #1C1E22;}
  #queue {overflow-y: auto; }
  table {background-color: #000711;}
  .userlist_op { color: #7392FF !important; }
  .panel-primary .panel-heading {background-color: #377BBF; }
  .pm-panel, .pm-panel-placeholder { width: 300px; }
  #motdrow { display: none; }
  #sitefooter { display: none; }
  #footer { display: none; }
  .pm-buffer { height: 270px !important;}
  #voteskip[disabled] { background-color: rgb(110, 0, 0); }
  .navbar { min-height: 20px; height:37px; }
  .navbar-header .navbar-brand { height: 35px; padding-top: 7px; padding-bottom: 0px; }
  .navbar-nav > li >a { height: 35px; padding-top: 8px; padding-bottom: 0px }
  #logoutform { height:35px; padding-top: 8px; padding-bottom: 0px }
  .navbar-text { margin-top: 0px; margin-bottom: 0px;}
  navbar, navbar-inverse, navbar-fixed-top { min-height: 20px; height:37px; }
  .navbar-form { margin: 0px; height:35px;}
  .navbar-form .form-control { height:35px; margin-bottom:1px; }
  #login { padding: 5px 10px; }
  #controlsrow { margin-top: 0; }
  #mainpage { padding-top: 45px; }
  .queue_entry { padding: 0px 3px 0px 0px; }
}
