css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("www.taschenlampen-forum.de") {
    /****************************************Hintergrund************************************
/Wer den gestreifen Hintergrund nicht mag muss folgende Datei mit dem Werbeblocker
/unterbinden: http://www.taschenlampen-forum.de/images/backgrounds/Page-BgTexture.jpg
/**************************************************************************************/
    /**********Links sind in einheitlicher Farbe (nicht grau)********/
    span.lastThreadTitle > a,
    a.username,
    a.PreviewTooltip,
    div.messageInfo > div.messageContent > div.title > a,
    div.funbox_below_content > div > table > tbody > tr > td > strong > a,
    div.funbox_below_content > div > table > tbody > tr > td > a {
        color: #2868be !important;
        /*#4993c5*/
        font-weight: bold;
    }
    div.unread > h4 > a,
    div.nodeInfo.forumNodeInfo.unread > div.nodeText > h3 > a,
    span.nodeIcon.hasGlyph {
        color: #2868be !important;
    }
    div.uix_nodeTitle_status {
        background-color: #e84d0e;
    }
    div.nodeLastPost.secondaryContent {
        width: 350px;
    }
    .pageContent.defaultBorderRadius > div#logo > a > h2,
    div.footerLegal,
    .funbox,
    .nodeControls,
    .baseHtml.noticeContent {
        display: none !important;
    }
    div#searchBar.hasSearchButton {
        margin-right: 20px;
    }
    /*************Feste Forenbreite***********/
    div.pageWidth {
        max-width: 93% !important;
    }
    /***************hellgraue MenÃ¼s etwas kontrastreicher******/
    ul.secondaryContent.blockLinksList > li > a,
    .tabLinks.xengalleryTabLinks > ul.secondaryContent.blockLinksList.xengallery > li > a,
    ul.secondaryContent.blockLinksList.xengallery > ul > li.tablinkIndent > a,
    .tabLinks.xengalleryTabLinks > ul.secondaryContent.blockLinksList.xengallery > li > div.Popup > a,
    ul.col1.blockLinksList > li > a,
    ul.col2.blockLinksList > li > a,
    ul.col1.blockLinksList > li > form > label,
    ul > li.section > ul > li > a,
    div.pageNavLinkGroup > div.linkGroup.SelectionCountContainer > a,
    span.crust > a.crumb,
    ul.footerLinks > li > a {
        color: #111111 !important;
    }
    ul.secondaryContent.blockLinksList > li > a:hover,
    .tabLinks.xengalleryTabLinks > ul.secondaryContent.blockLinksList.xengallery > li > a:hover,
    ul.secondaryContent.blockLinksList.xengallery > ul > li.tablinkIndent > a:hover,
    .tabLinks.xengalleryTabLinks > ul.secondaryContent.blockLinksList.xengallery > li > div.Popup > a:hover,
    ul.col1.blockLinksList > li > a:hover,
    ul.col2.blockLinksList > li > a:hover,
    ul.col1.blockLinksList > li > form > label:hover,
    ul > li.section > ul > li > a:hover,
    /*div.pageNavLinkGroup > div.linkGroup.SelectionCountContainer > a:hover,*/
    span.crust > a.crumb:hover,
    ul.footerLinks > li > a:hover {
        color: #4993c5 !important;
        background-color: #f0f0f0 !important;
    }
    /**************erst Details, dann Content ************/
    div.messageInfo.primaryContent {
        position: relative;
    }
    div.messageDetails {
        position: absolute;
        top: 0;
        left: 0;
    	width: calc(100% - 10px);
        background-image: linear-gradient(0deg, rgb(200,224,247), rgb(118,182,245));
        padding: 5px;
        color: white !important;
        border-top-left-radius: 2px;
        border-top-right-radius: 2px;
    }
    div.messageUserInfo {
    	position: absolute;
        }
    div.messageInfo.primaryContent > div.messageContent {
    	margin-left: 165px;
        min-height: 270px !important;
    }
    li.sectionMain.message {
    	padding: 0px;
    }
	div.messageMeta.ToggleTriggerAnchor {
    	padding-left: 10px;
        padding-right: 10px;
	}
    div.messageInfo.primaryContent {
    	margin: 0px !important;
    }
    div.messageUserInfo {
    	margin-left: 15px;
        margin-top: 40px;
    }
    div.messageContent {
        padding-top: 37px !important;
    }
    div.section.threadList > div.secondaryContent > ul > li > div.messageInfo > div.messageContent {
        padding-top: 0px !important;
    }
    /************************ "Danke" ohne Daumen ******************************/
    a.LikeLink.item.control.like:before,
    a.LikeLink.item.control.unlike:before {
        display: none;
    }
    /******************************************** Button mit 3D Effekt ***********************************/
    div.discussionList.sectionMain > div.sectionFooter > a,
    div.pageNavLinkGroup > div.linkGroup.SelectionCountContainer > a {
        padding: 4px;
    }
    div.privateControls > a,
    div.publicControls > a,
    div.discussionList.sectionMain > div.sectionFooter > a,
    div.pageNavLinkGroup > div.linkGroup.SelectionCountContainer > a,
    div.PageNav > nav > a,
    div.overlayScroll.pollResultsOverlay > div.buttons > a,
    dl.ctrlUnit.submitUnit > dd > input,
    dd > a.redactor_modal_btn.redactor_btn_modal_close.button,
    span.buttonContainer > input,
    div.pageNavLinkGroup.afterDiscussionListHandle > div.linkGroup > a > span,
    div.buttonGroup > input,
    span.scrollable > span.items > a,
    a.button.moreOptions.Tooltip,
    a.button.NoPopupGadget.Tooltip.PopupControl.PopupOpen.BottomControl,
    a.text.unreadLink,
    input.button.primary,
    input.PreviewButton,
    input.button.DisableOnSubmit {
        background-position: 0% 0%;
        background-color: transparent !important;
        color: black !important;
        border-radius: 5px !important;
        border-color: #707070 !important;
        border-width: 1px;
        border-style: solid;
        background-image: linear-gradient(180deg, #f2f2f2, #d2d2d2);
    }
    a.currentPage {
        background-image: linear-gradient(180deg, #e8f6fd, #acdcf7) !important;
    }
    div.privateControls > a:hover,
    div.publicControls > a:hover,
    div.discussionList.sectionMain > div.sectionFooter > a:hover,
    div.pageNavLinkGroup > div.linkGroup.SelectionCountContainer > a:hover,
    div.PageNav > nav > a:hover,
    div.overlayScroll.pollResultsOverlay > div.buttons > a:hover,
    dl.ctrlUnit.submitUnit > dd > input:hover,
    span.buttonContainer > input:hover,
    dd > a.redactor_modal_btn.redactor_btn_modal_close.button:hover,
    div.pageNavLinkGroup.afterDiscussionListHandle > div.linkGroup > a > span:hover,
    div.buttonGroup > input:hover,
    span.scrollable > span.items > a:hover,
    a.button.moreOptions.Tooltip:hover,
    a.button.NoPopupGadget.Tooltip.PopupControl.PopupOpen.BottomControl:hover,
    a.text.unreadLink:hover,
    input.button.primary:hover,
    input.PreviewButton:hover,
    input.button.DisableOnSubmit:hover {
        background-image: linear-gradient(180deg, #e8f6fd, #acdcf7);
        text-decoration: none;
    }
    /*********************Suchfeld****************/
    div#uix_searchMinimal {
        width: 300px !important;
    }
}
