css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

#navigator-throbber {
	list-style-image:    url("chrome://global/skin/throbber/Throbber-small.png") !important;
}
#navigator-throbber[busy="true"] {
	list-style-image:    url("chrome://global/skin/throbber/Throbber-small.gif") !important;
}
toolbar[mode="text"] #navigator-throbber {
	list-style-image:    url("chrome://global/skin/throbber/Throbber-small.png") !important;
}
toolbar[mode="text"] #navigator-throbber[busy="true"] {
	list-style-image:    url("chrome://global/skin/throbber/Throbber-small.gif") !important;
}
#menubar-items {
	margin-top: 0px !important;
}
#menubar-items > #main-menubar > menu {
	margin-bottom: 0px !important;
}
#commonDialog {
	padding: 10px !important;
	background: none #181818 !important;
}
dialog#commonDialog image#info\.icon.question-icon {
	display: -moz-box !important;
}
vbox#info\.box description {
	/* -moz-box-pack: center;
this has no effect */
	text-align: start !important;
}
/* background image for pages while they are being opened */
tabbrowser tabpanels {
	background: none black !important;
}
prefwindow#BrowserPreferences {
	background: None #2f2f2f !important;
	width: 39em!important;	
}

prefwindow#BrowserPreferences radiogroup.paneSelector {
	background: None #2f2f2f !important;
	margin: 0px 0px 0px 0px !important;
	padding: 0px 20px 0px 10px !important;
}

prefwindow#BrowserPreferences hbox.paneDeckContainer {
	margin-left: 10px !important
}
prefwindow#BrowserPreferences .prefWindow-dlgbuttons {
	border-top: 10px solid #2f2f2f !important;
}
.viewSelector {
	background: None #2f2f2f !important;
	padding-right: 0px !important;
}
#progressBox {
	padding: 10px 10px 20px 0px!important;
}
vbox#container>hbox.small-indent {
        background: None black !important;
}
