css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("joyreactor.cc"), domain("joy.reactor.cc"), domain("joyreactor.com"), url-prefix("http://reactor.cc") {
  
/*Замена заднего фона
------------------------------------------------------------------------------*/
  #container,
  #container::before, 
  #container::after {
    background: url("https://img-fotki.yandex.ru/get/165720/103004821.0/0_16a20c_a316c209_orig") repeat-x;
    background-color: #FAB728;  
  }
}

@-moz-document domain("joyreactor.cc"), domain("reactor.cc"), domain("joyreactor.com"), domain("pornreactor.cc") {
  body {
    background-color: #FAB728; 
  }

  
  
/*Изменение стиля поста и формы 'новый пост'
------------------------------------------------------------------------------*/
  .postContainer,
  #contentinner form#add_post {
	  background: #fff !important;
	  box-shadow: 0 1px 3px 0 rgba(0,0,0,0.12),0 1px 2px 0 rgba(0,0,0,0.24);    
    padding: 8px;
    margin-bottom: 16px;
  }
  
  form.sfSignin,
  form.post_comment_form {
    background: #fff !important;
    box-shadow: 0 1px 3px 0 rgba(0,0,0,0.12),0 1px 2px 0 rgba(0,0,0,0.24);    
    padding: 8px;
  }

  
  #showCreatePost {
    display: block;
    text-align: center;
    width: 100%;
    margin-bottom: 16px;
  }
  
  .add_post_button,
  #add_post_holder,
  #tagArticle {
    width: 100%;
  }
     
  .article,
  .add_post_footer {
     margin-bottom: 0;  
  }

  
  
/*Изменение цвета контейнера 
------------------------------------------------------------------------------*/
  #pageinner {
 	  background: #eee !important;
    padding: 16px;
  }
  
  #content{
 	  background: #eee !important;
  }

  .week_top,
  .user.week_top .userposition,
  .user.week_top a,
  .user .weekrating {
    background-image: none !important; 
    background: none !important; 
  }
  
  #tagList {
    padding-top: 0px;
  }
  
  .post_poll_holder {
    width: 100%;
  }

  
  
/*Изменение стиля кнопок навигации
------------------------------------------------------------------------------*/
  .pagination .current,
  .pagination .next,
  .pagination .prev {
    box-shadow: 0 1px 3px 0 rgba(0,0,0,0.12),0 1px 2px 0 rgba(0,0,0,0.24);
  }
  
  .sidebarContent p.login_link a {
    background: #fdb201;
    width: 100%;
    text-align: center;
  }
  
  input[value="Поддержать проект"] { 
    width: 100%;
    text-align: center;
  }

  
  
/*Компактизация содержимого
------------------------------------------------------------------------------*/  
  #navcontainer {
    padding: 13px 16px 0px;
  } 
  #navcontainer ul li {
    margin: 0px 0px 0px 0px;
  }
  
  .post_random a {
    border-radius: 12px;
    padding: 0px 12px;
  }
  
  #sidebar {
    width: 300px;
    float: left;
    margin-left: -300px;
  }
  
  #page {
    min-width: 1260px;
    width: 80%;  
  }
  
  #content {
    width: 100%;
    min-width: 911px;
    padding-right: 316px;
    margin-bottom: 0px;
  }
  
  .pagination_main {
    padding-top: 16px;
    padding-bottom: 11px;
  }
  
  .topbar_inner {
    width: 78%;
    min-width: 1220px;
    align-self: center;
  }
 
  #add_post > div > div.add_post_footer {
    margin: 0px 0px 0px;  
  }
  
  .article .ufoot span.comments {
    margin-right: 64px;
  }
  
  #contentinner .comment .addcomment form.post_comment_form {
    padding: 8px;  
  }
  
  .comment .addcomment {
    margin-top: 15px;
    margin-bottom: 8px;
  }

  .comments_bottom .date {
    margin: 0px 5px;
  }
  
  .commentnum {
    border-radius: 12px;
    line-height: 24px !important;
    font-size: 12px;
  }
  
  .commentnumDelta {
    height: 26px;
    line-height: 25px !important;
    top: 0px;
    border-radius: 12px;
    font-size: 12px;
  }
  
  .comment {
    padding-left: 10px;
    padding-right: 10px;
    padding-top: 8px;
    padding-bottom: 4px;
  }
  
  .comments_bottom{
    margin-top: 0px;
  }

  .comment_date {
    margin: 0px 16px;
  }

  .article .ufoot span a.response {
    font-size: 12px;
    font-weight: bold;
    margin: 0px 20px;
    padding: 0px 12px;
    border-radius: 12px;
  }
  
  a img,
  #contentInnerHeader {
    width: 100%;
    height: 100%;
  }
  
  .article .post_content_expand, #tagArticle .post_content_expand {
    height: 30px;
    margin-bottom: 5px;
  }
  
  .article .post_content_expand span, #tagArticle .post_content_expand span {
    text-align: center;
    line-height: 30px;
  }
  
  .article .post_content {
    margin-bottom: 0px;
  }
  
  .user.week_top {
    margin: 0px 0px 0px;
  }
  
  .addcomment {
    margin-bottom: 0px;
    padding: 0px;
  }
  
  .manage_post_wr,
  div.vote-minus,
  div.vote-plus {
    margin-top: 0px;
    vertical-align: middle;
  }
  
  span.comments {
    top: 5px;
  }  
  
  div.ufoot {
    margin-top: 5px;
  }  
  
  
  
}
