css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("anitubebr.xpg.uol.com.br") {

  body {
    background-color: #fff; font-family: helvetica;
  }

  #resbarrauol-header {
    display: none;
  }
  .barra-xpg {
    display: none;
  }

  #wrapper {
    width: 1089px;
  }
  #page {
    width: 100%;
  }

  #header {
    background-image: none;
    width: 100%;
    height: 90px;
  }
  #logo {
    margin: 0;
    position: absolute;
    top: 0;
    left: 25%;
  }
  div#logo a img {
    height: 60px;
    width: 202px;
  }

  #search {
    height: 55px;
    position: absolute;
    top: 2px;
    right: 25%;
  }
  #searchBox {
    margin-top: 10px;
    background: none;
    float: right;
    width: 392px;
  }
  .searchBox2 input {
    background-color: #eee;
  }
  #searchBox button {
    background: #8C152B;
    margin-top: 0;
    color: #fff;
    margin-left: 0;
    height: 27px;
  }
  #searchBox span {
    display: block;
  }

  #mainNavigation {
    display: none;
  }
  #smallNavigation {
    width: 100%;
    height: 30px;
    position: absolute;
    left: 0;
    top: 60px;
    background: #8c152b;
  }
  #bottomNavigation {
    float: none !important;
  }
  #bottomNavigation li {
    display: inline-block;
    float: none !important;
  }
  #smallNavigation a {
    font-size: 12px;
    text-decoration: none;
    color: #fff;
  }
  ul#bottomNavigation li {
    background-image: none;
  }

  #viewBox {
    border: none;
    padding-top: 0;
    width: 100%;
    margin-top: 0;
  }
  .tabs-container {
    background: none;
    border: none;
  }
  #mainContent {
    width: 100%;
  }
  #viewBox ul,
  #mainContent ul,
  #textBoxInfo {
    padding: 0;
  }
  
  .mainBox {
    width: 100%;
    border: none;
    margin-top: 0;
  }
  .mainBoxTitle {
    border: none;
    background: #8c152b;
    position: absolute;
    left: 0;
    text-align: center;
  }

  .midLink a {
    color: #000;
  }
  
  li.mainList {
    height: 150px;
    width: 270px;
    position: relative;
    margin-right: 0;
  }
  .mainBox li:nth-child(9) {
    display: none;
  }
  .videoThumb {
    border: none;
  }
  .videoThumb,
  .videoThumb img {
    width: 270px;
    height: 150px;
  }
  .videoThumb:hover {
    border: none;
  }
  .videoTitle {
    position: absolute;
    left: 0px;
    bottom: 0px;
    color: #fff;
    width: 100%;
    height: 25%;
    background: linear-gradient(to bottom, rgba(0,0,0,0) 0%, rgba(0,0,0,0.25) 100%);
  }
  .videoTitle a {
    position: absolute;
    width: 260px;
    bottom: 5px;
    left: 5px;
  }
  .videoTitle a,
  .videoTitle a:hover {
    color: #fff;
  }
  .videoInfo {
    display: none;
  }
  #mainContent ul,
  #textBoxInfo,
  #viewBox ul.tabs-nav {
    background: none;
  }
  #viewBox ul.tabs-nav {
    height: 35px;
  }
  #header, ul, li, .mainBoxTitle, #page {
    min-height: 0;
  }
  h1.mainBoxHeader {
    float: none !important;
  }
  h2.mainBoxHeader {
    font-weight: normal;
    color: #fff;
    float: none !important;
  }
  .mainBox ul {
    padding-top: 30px !important;
  }
  .moreLink {
    display: none;
  }
  .midLink {
    display: none;
  }
  .tabs-nav .tabs-selected a {
    background-image: none;
  }

  #videoUnder {
    display: none;
  }
  #container-3 {
    margin-top: 10px;
  }
  #viewBox #container-3 ul.tabs-nav {
    display: none;
  }
  #viewBox #container-3 #fragment-1 ul li.viewVideoInfo:nth-child(3),
  #viewBox #container-3 #fragment-1 ul li.viewVideoDetail:nth-child(4) {
    display: none;
  }

  #viewBox #container-3 #fragment-1 ul li p:nth-child(1),
  #viewBox #container-3 #fragment-1 ul li p:nth-child(4),
  #viewBox #container-3 #fragment-1 ul li p:nth-child(5) {
    display: none;
  }
  p.viewVideoText {
    height: 20px;
    line-height: 20px;
  }

  li#videoPlayer,
  li#videoPlayer > div {
    width: 1089px !important;
    height: 612px !important;
  }

  .fb-comments {
    display: none !important;
  }

  #sidebar {
    display: none;
  }

  #footer {
    display: none;
  }

  body > center {
    display: none;
  }

  #pagination-flickr li {
    font-size: 12px;
  }

  ul#pagination-flickr {
    background: none repeat scroll 0 0 #8c152b !important;
    height: 30px;
    left: 0;
    line-height: 30px;
    margin-top: 30px;
    padding: 0 !important;
    position: absolute;
    width: 100%;
    text-align: center;
  }
  ul#pagination-flickr li,
  ul#pagination-flickr li a,
  ul#pagination-flickr li a:hover,
  ul#pagination-flickr li.active,
  #pagination-flickr a:link,
  #pagination-flickr a:visited {
    border: none;
    color: #fff;
  }
  ul#pagination-flickr li a:hover {
    font-weight: bold;
  }
  #pagination-flickr a:link,
  #pagination-flickr a:visited,
  #pagination-flickr .active{
    padding: 0 5px;
  }
  #pagination-flickr .active,
  #pagination-flickr li {
    float: none !important;
    display: inline-block;
  }
  
}
