  @media screen and (min-width: 900px) {
    html, body {
          font-family: -apple-system,BlinkMacSystemFont,Roboto,"Helvetica Neue","Apple LiGothic Medium", "Microsoft YaHei",Sans-Serif !important;
          line-height: 1.5em;
      }
      .widemode #footer,
      .widemode #story,
      .widemode #titlebar,
      .widemode .read_content_container {
          max-width: 100% !important;
      }
      .main {
          padding: 6.25rem 0 0 200px;
      }
      .container-fluid {
          max-width: 90% !important;
      }
      .container {
          width: 90% !important;
      }
      div#story.story {
          font-family: -apple-system,BlinkMacSystemFont,Roboto,"Helvetica Neue","Apple LiGothic Medium", "Microsoft YaHei",Sans-Serif !important;
          line-height: 1.5em;
          font-weight: 300 !important;
      }
      .story,
      dd,
      dt {
          line-height: 2.0em !important;
      }
      .darkmode .story {
          color: #CCC
      }
      p,
      .story p {
          margin: 0em !important
      }
      blockquote {
          padding: 0px 20px;
          margin: 10px 0;
          border-left: 1px dotted;
      }
      h1,
      h2,
      h3,
      h4,
      dl dt {
          font-family: -apple-system,BlinkMacSystemFont,Roboto,"Helvetica Neue","Apple LiGothic Medium", "Microsoft YaHei",Sans-Serif !important;
          font-weight: 500 !important;
          margin-top: 1.0em !important;
          margin-bottom: 0.5em !important;
      }
      pre,
      code {
          white-space: pre-wrap;
          /* CSS 3 */
          
          white-space: -moz-pre-wrap;
          /* Mozilla, since 1999 */
          
          white-space: -pre-wrap;
          /* Opera 4-6 */
          
          white-space: -o-pre-wrap;
          /* Opera 7 */
          
          word-wrap: break-word;
          /* Internet Explorer 5.5+ */
          
          font-family: 'Roboto Mono', "Apple LiGothic Medium", "Microsoft YaHei", monospace !important;
          font-weight: 300 !important;
          font-size: 0.9em !important;
          overflow: visible !important;
      }
      .highlight_item .article_preview .lightmode a:hover,
      .lightmode .highlight,
      .lightmode .highlight_item .article_preview a:hover,
      .lightmode .highlight_label {
          color: #000;
          background-color: rgba(255, 245, 51, .6) !important;
      }
      .article_title {
          font-family: -apple-system,BlinkMacSystemFont,Roboto,"Helvetica Neue","Apple LiGothic Medium", "Microsoft YaHei",Sans-Serif !important;
          font-weight: 500 !important;
      }
      .article_preview {
        font-family: -apple-system,BlinkMacSystemFont,Roboto,"Helvetica Neue","Apple LiGothic Medium", "Microsoft YaHei",Sans-Serif !important;
        font-weight: 300 !important;
    }
    .title_meta {
        font-family: -apple-system,BlinkMacSystemFont,Roboto,"Helvetica Neue","Apple LiGothic Medium", "Microsoft YaHei",Sans-Serif !important;
        font-weight: 300 !important;
        font-size: 0.9em !important;
    }
      h2 a span {
          display: none;
      }
      .ipnote-popover .header {
          font-family: -apple-system,BlinkMacSystemFont,Roboto,"Helvetica Neue","Apple LiGothic Medium", "Microsoft YaHei",Sans-Serif !important;
          font-size: 1.0em !important;
          font-weight: 300 !important;
      }
      .ipnote-popover textarea {
          font-family: 'Roboto Mono', "Apple LiGothic Medium", "Microsoft YaHei", monospace !important;
          font-size: 1.0em !important;
          font-weight: 300 !important;
      }
  }
