css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 

domain("metacritic.com") {

.genre_nav .gn_wrap
{
background-image:none !important;
}

#nav_obar, .gn_wrap, .login_page #login
{
background-color:#1E8CD8 !important;
}

#mantle_skin
{
background-color:#222222 !important;
}

.form_area .input_wrapper
{
background-color:#000000 !important;
}

.content_section, .slot_wrap,
.new_releases_wrap, .tabs
{
background:#000000 !important;
}

.login_page .border_box
{
background:#222222 !important;
}

.border_box
{
border-color: #1E8CD8 !important;
}

.hpbanner_title, .hpb_item_title, .title, .header
{
color:#1E8CD8 !important;
}

.genre_nav_label, .nav_item_text a
{
color:#000000 !important;
}

.content_section, input
{
color:#CCCCCC !important;
}

#nav_obar td a
{
font-weight:bold !important;
}

}
