css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://k-tai.impress.co.jp/docs/column/stapablog/") {
 
/* 謎猫語とその画像　非表示
div.image-wrap div.inner div.row:last-child div.column:last-child{display:none !important;} */

/* 謎猫語のみ非表示 div.image-wrap div.inner div.row:last-child div.column:last-child span.caption:last-child */
  
    /* 関連記事　非表示 */
.outbrain-wrap {display:none !important;}
  
  body{
     background: #f5f5f5 !important;
  font-family : "Meiryo" !important;
  line-height:1.6em !important;
  letter-spacing:0.8pt !important;
  }
  
  .main{
  width:780px !important;
  margin: 0 auto  !important;
  background: #fff  !important;
  padding: 20px 20px 0 !important;
  box-shadow: 0 1px 3px 2px #eee  !important;
}
  
div[class="main-contents mainContents column stapablog column-stapablog"] {
  padding: 0 10px !important;
margin-bottom:0 !important;
}
  
  .inner {margin-bottom: 0 !important;}
  
  div[class="sub nocontent"]{
    padding: 2px 2px 0px 0px !important;
    background: white !important;
}
  
  .caption {
    padding: 4px !important;
  margin-bottom: 8px !important;
  margin-top: 6px !important;}
  

  
div.extra,.footer,.mv10,.logo,div[class="pt10 adsense"],div[class="extra nocontent"],div[class="extension social ktw"],.ad_contents_sideshuffle,p.corner-title,.null,.author-detail,.ad_backnumber_sideshuffle{display: none !important;}
  
  /* ピンク背景無効化 */
  .watch-color{display:none !important;}   
}

@-moz-document url-prefix("http://k-tai.impress.co.jp/img/ktw/docs/"){   /* 画像ページ */

  .extra,div[class="ad zoom-footer"],.footer {display:none !important;}
  
    body{
     background: #f5f5f5 !important;
  }
  
  .main{margin:0 !important;}


}

