css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.choke-point.com") {
  #wrapper { background-color: #f4f4f4 !important;}
  #sidebar,#footer {display: none !important;}
  
  #container {
  width: 750px !important;
  margin: 0px auto !important;
  margin-bottom: 10px !important;
  padding: 4px !important;
  box-shadow: 0 2px 4px 2px #eee;
  position: relative;}

  div[style*="height"]{height: auto !important;}
  
  /* ソーシャル */
  .category li{display: none !important;}
    .category br[clear="all"]{display: none !important;}
  
  /* AD */
  ins[class="adsbygoogle"] {display: none !important;}
  embed[id="amzn_widget"]{display: none !important;}
  .ad_left,.ad_right{display: none !important;}
  div[id*="amzn_assoc_ad"]{display: none !important;}
  
    script+br+br{display: none !important;}
  .pagination{display: none !important;}



  
  /* ヘッダー追尾 */
  #header {
    position: fixed;
  background: white;
    width: 100%;
    z-index: 1;
    top: 0;
  }
    body {
  margin-top: 30px;
}

}
