css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.tumblr.com") {
  
  /***********************
            FONTS
  ***********************/
  @font-face {
    font-family: "Open Sans";
    font-style: normal;
    font-weight: 400;
    src: local("Open Sans"), local("OpenSans"), url("https://fonts.gstatic.com/s/opensans/v13/cJZKeOuBrn4kERxqtaUH3VtXRa8TVwTICgirnJhmVJw.woff2") format("woff2");
  }
  
  @font-face {
    font-family: "Raleway";
    font-style: normal;
    font-weight: 400;
    src: local("Raleway"), url("https://fonts.gstatic.com/s/raleway/v9/0dTEPzkLWceF7z0koJaX1A.woff2") format("woff2");
  }
  
  html, body,
  .post-content-text, .reblog-content, .post .post_body,
  .notification, .flag--dashboard-icon-revolution .post_info,
  .post .post_info,
  .radar .radar_avatar,
  .editor-richtext,
  button,
  .post-form--header .tumblelog-select .caption, .post-form--header .tumblelog-select .reblog_source
  {
    font-family: "Open Sans", "Helvetica Neue","HelveticaNeue",Helvetica,Arial,sans-serif;
  }
  
  .post.is_conversation .post_content .post_title, .post.is_regular .post_content .post_title, .post .post-content-text .title-field *,
  h1, h2, h3, h4,
  .reblog-title
  {
    font-family: "Raleway", "Gibson","Helvetica Neue","HelveticaNeue",Helvetica,Arial,sans-serif;
    font-weight: 400;
  }
  
  /***********************
           COLORS
  ***********************/
  
  .identity,
  .l-container.l-container--flex .l-content,
  .search_results_container .tumblelog_mask_item,
  .showcase .dashboard-section,
  .l-container.l-container--two-column .l-content, .l-container.l-container--two-column-dashboard .l-content,
  .l-container.l-container--two-column-dashboard .left_column,
  .post_avatar, .blog-avatar,
  .l-header-container--refresh,
  .l-container.l-container--two-column .right_column, .l-container.l-container--two-column-dashboard .right_column,
  .identity .controls_section li:hover:not(.section_header):not(.selected_blog),
  .identity .controls_section li:active:not(.section_header):not(.selected_blog),
  .controls_section li:active:not(.section_header):not(.selected_blog),
  .identity .controls_section.user_list li:active .follow_list_item_blog::before
  {
    background-color: #2d2d2d;
    background-image: none;
    border-right: none;
  }
  
  .identity .controls_section.user_list li .follow_list_item_blog::before,
  .controls_section.user_list li .follow_list_item_blog::before,
  .user_list li .follow_list_item_blog::before,
  .identity .controls_section.user_list li:hover .follow_list_item_blog::before,
  .identity .controls_section.user_list li .follow_list_item_blog::before,
  .controls_section.user_list li .follow_list_item_blog::before
  {
    background-image: linear-gradient(to right,rgba(45,45,45,0),#2d2d2d);
    border-color: #2d2d2d;
  }
  
  .identity .right_column::after {
    background: linear-gradient(to bottom,#2d2d2d,rgba(45,45,45,0));
  }
  
  #search_actions_search .elevator, #discover_actions_discover .elevator
  {
    background-color: rgba(45, 45, 45, 0.85);
  }
  
  .ui_peepr_glass {
    background-color: rgba(45, 45, 45, 0.95);
  }
  
  .post_full .post_avatar .publish_info.day,
  .post_full .post_avatar .queue .publish_info.day
  {
    color: #E5DEDD;
  }
  
  .dashboard_options_form, .tag_psa
  {
    color: #E5DEDD;
    border-color: #595959;
  }
  
  .dashboard_options_form .dashboard_options_form_disclaimer,
  .dashboard_options_form .dashboard_options_form_disclaimer a 
  {
    color: #998B89;
  }
  
  .post_full .post_avatar .queue .publish_info.time,
  .post_full .post_avatar .publish_info.time
  {
    color: #998B89;
  }
  
  .post_avatar .post_avatar_link, .post_avatar .blog-avatar-link, .blog-avatar .post_avatar_link, .blog-avatar .blog-avatar-link
  {
    background-color:#2d2d2d;
  }
  .plus-follow-button,
  .tab-bar-container .tab_notice,
  .tab_notice .tab_notice_value,
  .logged-out-header .login-button:focus,
  .logged-out-header .login-button
  {
    color: #2d2d2d;
  }
  .tab-notice--outlined
  {
    border-color: #2d2d2d;
  }
  .post_full .post_permalink
  {
    border-color: #2d2d2d #2d2d2d transparent transparent;
  }
  .nav-redesign .selected .tab_notice,
  .nav-redesign .tab_notice,
  .binary_switch input[type="checkbox"]:checked ~ .binary_switch_track
  {
    background-color: #D64937;
  }
  .binary_switch input[type="checkbox"]:focus ~ .binary_switch_track
  {
	  box-shadow:0 0 5px #EBA49B;
  }
  
  .pop-menu .item-option:hover,
  .blog-sub-nav-details .blog-sub-nav-item:hover, .blog-sub-nav-details .blog-sub-nav-item.selected, .blog-sub-nav-details .blog-sub-nav-item.currentItem
  .blog-list-item.blog-list-item--on-light:hover,
  .blog-list-item.blog-list-item--on-light:hover,
  .popover_menu_item:hover, .popover_menu_item.keyboard_focus,
  .popover_menu_item:hover, .popover_menu_item.keyboard_focus,
  .popover--account-popover .popover .popover_menu_item:hover
  {
    background-color: #dedede;
  }
  
  .popover--account-popover .popover .popover_menu_item_blog.popover_menu_item_blog {
    background: none repeat scroll 0% 0% #FFF;
  }
  
  .blog-sub-nav-details .blog-sub-nav-item:hover, .blog-sub-nav-details .blog-sub-nav-item.selected, .blog-sub-nav-details .blog-sub-nav-item.currentItem
  {
    box-shadow: -4px 0px 0px #dedede, 10px 0px 0px #dedede;
  }
  
  .blog-sub-nav-details .blog-sub-nav-item:hover .blog-sub-nav-item-label, .blog-sub-nav-details .blog-sub-nav-item.selected .blog-sub-nav-item-label, .blog-sub-nav-details .blog-sub-nav-item.currentItem .blog-sub-nav-item-label
  {
    color: #2d2d2d;
  }
  
  .popover--account-popover .popover .popover_menu_item:hover .popover_menu_item_anchor,
  .blog-list-item.blog-list-item--on-light:hover .blog-list-item-info-name, .blog-list-item.blog-list-item--on-light:hover .blog-list-item-icon-private
  {
    color: #392c2b;
  }
  
  .blog-list-item.blog-list-item--on-light:hover .blog-list-item-info-title,
  .blog-sub-nav-details .blog-sub-nav-item:hover .blog-sub-nav-item-data, .blog-sub-nav-details .blog-sub-nav-item.selected .blog-sub-nav-item-data, .blog-sub-nav-details .blog-sub-nav-item.currentItem .blog-sub-nav-item-data,
  .popover--account-popover .popover .popover_menu_item:hover .popover_item_suffix
  {
    color: #777;
  }
  
  .compose-button:focus,
  .compose-button
  {
    fill: #D64937;
  }
  
  .flat_select select:focus ~ .icon
  {
    color:#D64937;
  }
  
  .identity .controls_section .item:hover .follow:hover,
  .controls_section .item:hover .follow:hover,
  .plus-follow-button:hover
  {
    background-color: #D64937;
    color: #2d2d2d;
  }
  .post-forms-glass,
  .ui_dialog_lock.opaque
  {
    background-color: rgba(45, 45, 45, 0.95);
  }
  .flat-button.blue,
  .flat-button.blue:active,
  .flat-button.blue:focus,
  .split .create_post_button:active,
  .flat-button.blue[disabled], .flat-button.blue.disable, .flat-button.blue[disabled]:active, .flat-button.blue.disabled.active,
  .chrome.blue,
  .chrome.blue:active,
  .logged-out-header .signup-button,
  .logged-out-header .signup-button:active,
  .ui_dialog .chrome.blue,
  .ui_dialog .chrome.blue:active,
  .identity .send_fan_mail
  {
    background-color: #D64937;
    border-color: #D64937;
  }
  
  .editor .over-bottom::after, .editor .over-top::before,
  .tab_notice, .selected .tab_notice
  {
    background-color: #D64937;
  }
  
  .rapid-recs-container .rapid-recs {
    background-image: linear-gradient(to bottom,rgba(45,45,45,0)0%,#2d2d2d 15%,#2d2d2d 85%,rgba(45,45,45,0)100%);
  }
  
  .tab_bar .tab.selected, .tab_bar .tab.selected .tab_anchor, .tab_bar .tab.active, .tab_bar .tab.active .tab_anchor
  {
    color: #dedede;
  }
  
  .post.post_full,
  .post .post-source-footer,
  .notifications_page .notification_pane,
  #user_graphs .axis-label,
  .ui_notes .date_header, .date_header,
  .ui_stats .unit .stage,
  .post-container,
  .flag--peepr-redux .peepr-body .indash_blog .post,
  .editor-slot,
  .radar .radar_controls
  {
    background-color: #f9f9f9;
  }
  .ui_stats.monochrome .unit.active.new_followers .button, .ui_stats.monochrome .unit.active.total_followers .button, .ui_stats.monochrome .unit.active.new_notes .button {
    background: transparent linear-gradient(to bottom, #CBCBCB 0px, #f9f9f9 100%) repeat scroll 0% 0%;
  }
  .ui_stats.monochrome .unit.active.new_followers .stage, .ui_stats.monochrome .unit.active.total_followers .stage, .ui_stats.monochrome .unit.active.new_notes .stage {
    background: transparent linear-gradient(to bottom, #F5F5F5 0px, #F9F9F9 100%) repeat scroll 0% 0%;
  }
  .ui_stats .plop::after
  {
    border-top: 8px solid #f9f9f9;
  }
  .radar .radar_footer .radar_avatar::before {
    background-image: linear-gradient(to right,rgba(54,70,93,0),#2d2d2d);
  }
  
  #toaster .toast .toast_border
  {
    background-color: #444;
    border-radius: 3px;
  }

  .post_controls .post_control.reblog.reblogged::after
  {
    background-color: #f9f9f9;
  }
}
