css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("lynda.com") {

body, .learn-more-banner, #header_search_field
{
background: #000000 !important;
}
.section, #primary, .course-container,
#nmb_banner_container, .panel-collapse 
{
background-color: #000000 !important;
}
.main_subjects a:hover, .subject_details, 
.course-info, .well, .main_subjects li a:active,
.tabs .active #mostPopular, .carousel-content,
.course-search, #course-toc-outer, .tab-content,
.course-tabs li h2 a, .course-tabs,
.faq_subcategory_header, .faq_question_wrapper,
.searchTop li a, .category-header, dd span a
{
background: #222222 !important;
}
#ams3982 td, .category-more-details, .category-details h1,
.main_link, .main_subjects, .course-list-head,
#course-toc-outer li dl:hover, .course-tabs li h2 a:hover,
.faq_helpfulness_answer, .sub_menu li a:hover,
.searchTop li a:hover, .detail-type
{
background: #444444 !important;
}
.well 
{
box-shadow: none !important;
}
h1, h2, h3, h4, h5, #header_search_field, #topics li,
a, p, strong, em, dd, dt,
.count, .show-more, .learn-more-text, .grid_8,
.category-more-details, .course-list-count, .course-list-sort, .course-date, .breadcrumbs div a span, .course-description-topics, .detail-type,
.has-icon, .meta, .author_name, .category-header
{
color: #FFFFFF !important;
}
}
