css:
@-moz-document domain("mobile-review.com") {
/*android links*/
body{transition:all 0.3s ease 0s;}

a[href^="http://android"]{color: #4C9800 !important;} 



.cse-search-form{
    width:220px;}
  
.input .gsc-input{
    border-radius: 3px;
height: 16px;
width: 200px;
border: 1px solid rgba(91, 108, 144, 0.5);
}


/*sh!t*/
img[src="/image/banner/yota.png"],
#calendar,
a[href="http://www.liveinternet.ru/click"],
#hmenu,
#toptextblock,
#ad_ph_1,
#mainbanner,
#newsblock iframe,
#mainblock div.break,
#maincontent #mainblock #printbutton,
#maincontent div[align="right"],
#newsblock div.right_banner,
#mainblock img[width="1"],
.adv_vert, .adv_vert2,
center,
#counters,
#searchmod td:nth-child(1) img,
#searchmod td:nth-child(2),
#searchmod td:nth-child(3),
#searchmod td:nth-child(5),
#searchmod td:nth-child(7),
#searchmod td:nth-child(9),
#searchmod td:nth-child(11),
#searchmod td:nth-child(13),
#searchmod td:nth-child(15),
#searchmod td:nth-child(16),
#searchmod td:nth-child(18)
{display:none !important;}




/*header*/

#headblock {
  height: 80px;
  background: url('http://i.imgur.com/5KYCdCQ.png') no-repeat  left;
  background-position: 14px 24px;}
#topline{
  background-color:#fff;
  height:40px;
  padding-top: 21px;
  padding-right: 40px;}
#topline td{
  padding:0 2px;}
#topline td strong{
  font: 16px/20px open sans, sans-serif;
padding: 4px 10px;
color: #365476;
opacity: 0.8;
margin: 0 4px;}
#searchmod a[href="http://vk.com/mobilereviewcom"] img{
  width:0px;
  height:0px;
  padding: 12px;
  background:url(https://cdn4.iconfinder.com/data/icons/social-circle/512/vk-24.png);}
#searchmod a[href="https://www.facebook.com/mobilereviewcom"] img{
  width:0px;
  height:0px;
  padding: 12px;
  background:url(https://cdn3.iconfinder.com/data/icons/social-circle/512/social_3-24.png);}
#searchmod a[href="http://www.youtube.com/user/MobileReviewcom"] img{
  width:0px;
  height:0px;
  padding: 12px;
  background:url(https://cdn3.iconfinder.com/data/icons/social-circle/512/647397-youtube-24.png);}
#searchmod a[href="http://instagram.com/mobilereview"] img{
  width:0px;
  height:0px;
  padding: 12px;
  background:url(https://cdn3.iconfinder.com/data/icons/social-circle/512/social_6-24.png);}
#searchmod a[href="https://twitter.com/#!/mobilreview"] img{
  width:0px;
  height:0px;
  padding: 12px;
  background:url(https://cdn3.iconfinder.com/data/icons/social-circle/512/social_8-24.png);}
#searchmod a[href="https://plus.google.com/u/0/100981961533140434917/posts"] img{
  width:0px;
  height:0px;
  padding: 12px;
  background:url(https://cdn0.iconfinder.com/data/icons/social-circle/512/google-24.png);}




/*advblock*/

#advblock{
  background: #eeeeee;
  padding: 30px;
  margin:0;
  height:none;}
#advblock tr{
  display: block;}
#advblock div.anons a p {
  height: 100px;
  overflow: hidden;
  vertical-align: bottom;
  margin-left: 0;
  padding-left:0;
  margin-bottom: 8px;}
#advblock #mainmenu, #mainmenu{
  background: none;
  min-width:170px; 
  width:170px;}
#advblock td {
  padding: 0;
  margin: 0 0 0 0;
  background: none;
  width: 760px;
  vertical-align: none;}
#advblock a{
  font: bold 14px "open sans",sans-serif;}
#advblock div.anons a{
  font: bold 16px/24px "open sans",sans-serif;
  color: #365476;
  min-width: 174px;}
#advblock div.anons p{
  font: 13px/20px open sans,sans-serif;
  color: rgb(74, 74, 74);
  margin:0;
  min-width: 174px;}
#advblock div.anons img{
  float:none !important;}
#advblock div.anons {
  display: table-cell;
  padding-bottom: 40px;
  padding-left:20px;}




/*left menu*/

#menu{
  background: none;
  background-color: none;
  padding: 0px 0 27px 0;
  width:170px;}
#menu a, #newphones a, #newreviews a {
  display: block;
  list-style: none;
  margin:0 0 10px 0;
  font: 14px/16px "open sans",sans-serif;
  line-height:20px;
  color: #93A4BB;
  transition: all 0.1s ease 0s;
}
#menu a:hover, #newphones a:hover, #newreviews a:hover{
  color: #5C7083;}
#newphones{
  background:none;}
#newphones span{
  padding:0 0 10px 0px;
  display: block;
  font: 16px open sans,sans-serif;
  text-transform: none;
  color: #637A93;}
#maincontent #leftcolumn {width: 170px;padding-left: 30px;}





.mm_box {
margin-top: 0px;
  padding-top: 0px;
}

#mainmenu a{padding: 0px 0 2px 0;}





/*main*/

#maincontent{
  margin-top:40px;
  padding-left: 30px;
  border-collapse:collapse;}




/*news*/

#newsblock{
  width: 170px;}
td#newsblock{
  background:none;
  padding-top:0px;
  padding-right:30px;}
#newsblock a[href="/fullnews/main/index.shtml"]{
  border:none;
  padding:0;}
#newsblock p a{
  border-bottom:none !important;
  font:13px/18px open sans,sans-serif;
  padding:0 0 0 0;}
#newsblock p{
  padding: 0 0 0 0;
  font: 11px/18px open sans,sans-serif;
  color: #fff;}
#newsblock div:last-child{
  width:170px !important;}
#newsblock span{
  padding:0;
  display: block;
  font: 16px open sans,sans-serif;
  text-transform: none;
  color: #637A93}
#newsblock div.hit_small {
  padding: 13px 2px 0px 0px;
  margin: 2px 0px 0px -20px;}
div.hit{
  padding: 13px 2px 0px 0px;
  margin: 2px 0px 0px -20px;}
#newsblock div.hit_small img{
  width: 15px;
  height: 11px;
  position: relative;
  top: 4px;}




/*article*/

#mainblock{
  text-align: center;
  padding:0 20px 0 0 !important}
#mainblock h1, h2, h3, h4, p{
  text-align:left;
  display:inline-block;
  width: 100%;
  max-width:860px;}
#mainblock[style="padding:0px;"]{
  font-size:0;}
#mainblock p{
  font-family: open sans,sans-serif;
  font-size:1.6em;
  line-height:1.8em;
  color: rgb(29, 29, 29);}
#mainblock h1{
  font:6em/1.3em open sans,sans-serif;
  font-size:46px;
  color:rgb(91, 108, 144);
  margin:0;
  text-align:center;}
#mainblock h3, #mainblock h2 {
  margin: 40px 0 0 0;
  font: 3em open sans,sans-serif;
  color:rgbrgb(49, 54, 65);}
#mainblock li{
  font: 1.5em/1.7em open sans,sans-serif;
  max-width: 860px;
width: 100%;
  text-align: left;
padding:0;}

#mainblock ul{max-width: 860px;
width: 100%;
display: inline-block;
list-style-type: disc;
margin: 10px 0;}
#mainblock ol{
max-width: 860px;
width: 100%;
  display: inline-block;
margin: 10px 0;}




/*topics*/


#mainblock div.topic12,
#mainblock div.topic {
  display:inline-table;
  width:25%;
  clear: none;
  float: none;}
#mainblock .topic12 img,
#mainblock .topic img{
  float:none;
  margin:0;
  padding-bottom:10px;}
#mainblock .topic12  a,
#mainblock .topic  a{
  font:16px/20px open sans,sans-serif !important;
  margin:4px 0;
  padding: 0 0 0 20px;
  text-align:left;}
#mainblock .topic12  p,
#mainblock .topic  p {
  font: 13px/20px open sans,sans-serif;
  padding: 0 0 0 20px;
  width: auto;}
#mainblock .topic12  a p,
#mainblock .topic a p{
  padding-left:0;
  width:100%;}

}
