css:
@-moz-document url-prefix("https://www.formula1.com/en/f1-live-legacy.html") {
footer, header, .dashboard-header, .site-nav, #sp_notification {
    display: none;
}
[class^='jun-'], [class*=' jun-'] {
    display: none;
}
body {
    margin-top: 0;
}
.SP_Table_Row_Hl {
    background-color: transparent !important;
    border-bottom: 1px #444 solid;
    font-size: 36px !important;
    font-weight: normal !important;
    height: 53px !important;
    line-height: 34px !important;
}
.SP_Table_Row_Hl * {
    font-family:"Monaco", "Ubuntu Mono" !important;
}
#sp_grid {
    padding: 20px 0 0 20px;
}
.SP_sidebyside {
    padding-bottom: 10px;
}
.SP_sidebyside > .left {
    display: none;
}
.SP_sidebyside > .right {
    margin-left: 0;
    padding-left: 30px;
}
.SP_racebanner {
    max-width: 5000px;
    min-height: 0;
    margin: 0 0 -20px 0;
}
.SP_racebanner *, #sp_temperatures {
    display: none;
}
#lapcounter {
    display: block;
    right: 0;
}
#sp_timingleaderboard>div:first-child {
    display: none;
}
.template {
    padding-top: 0;
}
.SP_greyFlec {
    width: 70px;
}
.DriverParallelogram {
    height: 36px;
}
}
