css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("wunderground.com"){
    
[id*="home-news1"] {
    display: none!important;
}
[id*="home-news2"] {
    display: none!important;
}
[id*="home-maps3"] {
    display: none!important;
}
[id*="home-maps4"] {
    display: none!important;
}
[id*="home-infographics"] {
    display: none!important;
}
[id*="home-posters"] {
    display: none!important;
}
[id*="almanac-history"] {
    display: none!important;
}
[id*="astronomy-mod"] {
    display: none!important;
}
p {
    font-family: inherit;
    font-weight: normal;
    font-size: 0.875rem;
    line-height: 1.3125rem;
    margin-bottom: 0.625rem;
    text-rendering: optimizelegibility;
    color: #010106;
}

.cat-six-article[_ngcontent-c8] h2[_ngcontent-c8],
.cat-six-article[_ngcontent-c8] h3[_ngcontent-c8] {
    margin-bottom: 20px;
    color: #CBBCED;
    font-weight: bold;
}

label {
    font-size: 0.875rem;
    color: #CBBCED;
    cursor: pointer;
    display: block;
    font-weight: normal;
    line-height: 1.5;
    margin-bottom: 0px;
}
#conditions-card {
    position: relative;
    color: #FFF;
    background-color: #6F43C3;
    width: 800px;
    min-height: 200px;
    margin: auto;
}

/*  header */
NAV.topbar #global-header .logo {
    position: absolute;
    top: 0px;
    left: 40px;
    width: 171px;
    height: 38px;
    background-image: url('http://img.photobucket.com/albums/v256/Dynasmon/WUlogoUSA2.png_zps0xsqmrty.gif');
    background-position: left;
    background-repeat: no-repeat;
}
.block-list .block-item {
    background: rgba(162, 173, 208, .2) !important;
}
.block-list .block-item:nth-child(2n + 1) {
    background: rgba(162, 173, 208, .2) !important;
}
.panel.category {
    font-size: 0.9375rem;
    background-color: #843EDB;
}
#tropicNotice IMG,
/* footer*/
#footer,
#hpSearch #title,
#tripIcons,
.adBox.br5,
#socialWeather,
#pwsTable > TFOOT tr td,

/* social BS >*/
.titlePromo,
#optShare,
.sep,
#plusOne,
#likeButton,
#socialBar > TBODY > TR > TD,
#postShare,
#locationBarBottom > TBODY > TR > TD:nth-child(6) {
    display: none !important;
}
table tr.even,
table tr.alt,
table tr:nth-of-type(2n) {
    background: none repeat scroll 0% 0% #522d80;
    color: #fffafa;
}
.readTerms,
#locationName,
.primeHeader .setAlerts,
.photoUp,
.photoBy,
.cmSource.cmFWIX,
#fct_details .fct_hourlytable TABLE {
    background-image: none !important;
}

/* font */
a,
.keyText {
    color: #ccccff !important;
}
a:hover,
#optionBar a:hover,
#trip_panel_0 select option:hover,
SPAN[style="color: rgb(0, 170, 0);"] {
    color: #e6e6fa !important;
}
#optionBar a,
#grouptitle,
div,
.rfGreen {
    color: black !important;
}
h1 {
    font-size: 16px !important;
}
.green {
    color: #FCD116 !important;
    text-shadow: 1px 2px 1px #000 !important;
}
.admin-label {
    color: #FCB514 !important;
    text-shadow: 1px 2px 1px #000 !important;
}
.comment.admin {
    background-color: #FF8C00;
}
SPAN.subtle {
    color: #660000;
}

/*  header */
#pageHeader {
    height: 120px !important;
}
#feature-menu .primary-menu LI:hover > LABEL.maps,
#feature-menu .primary-menu LI.active > LABEL.maps {
    border-top-color: #D5202A;
    color: #666666;
}
.sidebar .sidebar-wrapper .side-main-nav A.maps {
    border-left-color: #D5202A;
}
#feature-menu .primary-menu LI:hover > LABEL.severe,
#feature-menu .primary-menu LI.active > LABEL.severe {
    border-top-color: #FFA500;
    color: #666666;
}
.sidebar .sidebar-wrapper .side-main-nav A.severe {
    border-left-color: #FFA500;
}
#feature-menu .primary-menu LI:hover > LABEL.news,
#feature-menu .primary-menu LI.active > LABEL.news {
    border-top-color: #FFFF00;
    color: #666666;
}
.sidebar .sidebar-wrapper .side-main-nav A.news {
    border-left-color: #FFFF00;
}
#feature-menu .primary-menu LI:hover > LABEL.photos,
#feature-menu .primary-menu LI.active > LABEL.photos {
    border-top-color: #228B22;
    color: #666666;
}
.sidebar .sidebar-wrapper .side-main-nav A.photos {
    border-left-color: #228B22;
}
#feature-menu .primary-menu LI:hover > LABEL.activities,
#feature-menu .primary-menu LI.active > LABEL.activities {
    border-top-color: #191970;
    color: #666666;
}
.sidebar .sidebar-wrapper .side-main-nav A.activities {
    border-left-color: #191970;
}
#feature-menu .primary-menu LI:hover > LABEL.more,
#feature-menu .primary-menu LI.active > LABEL.more {
    border-top-color: #4B0082;
    color: #666666;
}
.sidebar .sidebar-wrapper .side-main-nav A.browse {
    border-left-color: #4B0082;
}
.maps-radar .subnav LI A:link,
.maps-radar .subnav LI A:visited,
.maps-radar .subnav LI LABEL:link,
.maps-radar .subnav LI LABEL:visited {
    border-color: #D5202A;
}
.maps-radar .subnav LI A.selected,
.maps-radar .subnav LI A.selected:hover,
.maps-radar .subnav LI LABEL.selected,
.maps-radar .subnav LI LABEL.selected:hover {
    color: #1E2023;
    border-color: #FF6347;
}
.news-blogs .subnav LI A:link,
.news-blogs .subnav LI A:visited,
.news-blogs .subnav LI LABEL:link,
.news-blogs .subnav LI LABEL:visited {
    border-color: #FFFF00;
}
.news-blogs .subnav LI A.selected,
.news-blogs .subnav LI A.selected:hover,
.news-blogs .subnav LI LABEL.selected,
.news-blogs .subnav LI LABEL.selected:hover {
    color: #1E2023;
    border-color: #FFFFFF;
}
.severe-weather .subnav LI A:link,
.severe-weather .subnav LI A:visited,
.severe-weather .subnav LI LABEL:link,
.severe-weather .subnav LI LABEL:visited {
    border-color: #FFA500;
}
.severe-weather .subnav LI A.selected,
.severe-weather .subnav LI A.selected:hover,
.severe-weather .subnav LI LABEL.selected,
.severe-weather .subnav LI LABEL.selected:hover {
    color: #1E2023;
    border-color: #FFD700;
}
.photos-videos .subnav LI A:link,
.photos-videos .subnav LI A:visited,
.photos-videos .subnav LI LABEL:link,
.photos-videos .subnav LI LABEL:visited,
.webcams .subnav LI A:link,
.webcams .subnav LI A:visited,
.webcams .subnav LI LABEL:link,
.webcams .subnav LI LABEL:visited {
    border-color: #228B22;
}
.photos-videos .subnav LI A.selected,
.photos-videos .subnav LI A.selected:hover,
.photos-videos .subnav LI LABEL.selected,
.photos-videos .subnav LI LABEL.selected:hover,
.webcams .subnav LI A.selected,
.webcams .subnav LI A.selected:hover,
.webcams .subnav LI LABEL.selected,
.webcams .subnav LI LABEL.selected:hover {
    color: #1E2023;
    border-color: #7CFC00;
}

/* ========================= */
html,
body {
    background: #522d80 linear-gradient(to top, rgba(205, 193, 197, 0.1) 5%, rgba(205, 193, 197, 0.3) 75%, rgba(205, 193, 197, 0.6)) fixed !important;
}
#locationName {
    margin-left: -20px !important;
}
HTML > BODY > CENTER:nth-child(2) {
    margin-top: 20px !important;
}
#feature-menu .menu-wrapper.active .menu-list LI A:hover {
    color: #A9A9A9;
    background-color: #4C4C4C;
    border-left-color: #380061;
}
#wuSettings-user LI {
    background-color: #4C4C4C;
    border-bottom: 1px solid #D7D7D7;
}

/* error popup */
.wui-log-body,
.wui-log-time,
.wui-log-close {
    color: red !important;
}

/* search autocomplete popup */
.ui-autocomplete.ui-menu.ui-widget.ui-widget-content.ui-corner-all,
.ui-autocomplete-group {
    background: rgb(169, 169, 169) !important;
    border: 1px solid black !important;
    color: grey !important;
}

/* other pages */
/* comments */
.comment .text {
    margin-left: 0px;
}
.pic,
.text,
.subG,
#PostComment,
#PostComment > DIV {
    background: rgba(155, 89, 182, .2) !important;
    color: #666666;
    font-family: "Meiryo UI", Meiryo UI;
    font-size: 18px;
    border: 4px solid rgba(0, 0, 0, .2) !important;
}
.comment.gone {
    background-color: #DC143C;
    font-family: "Arial Black", Arial Black;
}
.comment.owner {
    background-color: rgba(108, 52, 131, .2) !important;
    font-family: "Meiryo UI", Meiryo UI;
}
.comment .subheading {
    color: #fffafa;
    font-size: 12px;
}
BLOCKQUOTE {
    background: none repeat scroll 0% 0% rgba(195, 155, 211, .4);
    color: #fffafa;
}
#blog-entry p {
    line-height: 1.5;
    background: rgba(165, 105, 189, .2) !important;
    border: 4px solid rgba(0, 0, 0, .2) !important;
    font-family: "Arial", Arial;
}
#favorites .favorites-bar {
    height: 39px;
    background: rgba(162, 173, 208, .2) !important;
    border: 4px solid rgba(0, 0, 0, .2) !important;
}
FOOTER.primary .navigation LI {
    line-height: normal;
    margin-bottom: 10px;
    background: rgba(59, 83, 35, .2) !important;
    border: 4px solid rgba(0, 0, 0, .2) !important;
}
.wx-content .wx-col,
.wx-content .wx-col-1,
.wx-content .wx-col-2,
.wx-content .wx-col-3 {
    padding-left: 0.625rem;
    padding-right: 0.625rem;
    float: none;
    width: auto;
    background: rgba(59, 83, 35, .2) !important;
    border: 4px solid rgba(0, 0, 0, .2) !important;
}

#feature-menu .menu-wrapper.active .menu-list {
    border-bottom-left-radius: 5px;
    border-bottom-right-radius: 5px;
    border: 1px solid #1E2023;
    background-color: #766291;
}
FOOTER.primary .navigation {
    display: none;
    border-top: 1px solid #D7D7D7;
    background-color: #816b9d;
}
FOOTER.primary .company {
    display: none;
    border-top: 1px solid #D7D7D7;
    background-color: #816b9d;
}
FOOTER.primary .floor {
    display: none;
    border-top: 1px solid #D7D7D7;
    background-color: #816b9d;
}
BODY [type="button"],
BODY [type="submit"],
BODY .button,
BODY button {
    background-color: #149AC6;
    background-image: linear-gradient(#3A0A62, #927FE1);
}
BODY [type="button"]:hover,
BODY [type="submit"]:hover,
BODY .button:hover,
BODY button:hover {
    background-color: #120E0B;
    background-image: linear-gradient(#110A06, #1A140F);
}
.plus-button .plus {
    border-radius: 0px 5px 5px 0px;
    background-color: #149AC6;
}
.plus-button:hover .plus:hover {
    background-color: #120E0B;
    border: 1px solid #F83;
}

/* Profile Menu */
#wuSettings-anon P:first-child {
    cursor: default;
    background-color: #e8000d;
    color: #202020;
    font-weight: bold;
    text-align: center;
    height: 44px;
    line-height: 44px;
    margin: 0;
}
#wuSettings-anon FORM LABEL {
    font-size: 0.75rem;
    background-color: #777696;
    color: #4C4C4C;
}
#wuSettings-anon FORM A {
    height: 34px;
    line-height: 34px;
    background-color: #777696;
    font-size: 0.75rem;
    display: inline-block;
    float: right;
}

#facebook-sign-in {
    border-top: 1px solid #d7d7d7;
    background-color: #777696;
    position: relative;
    padding: 25px 0;
    text-align: center;
}
#wuSettings-anon FORM {
    width: 100%;
    background-color: #777696;
    padding: 0 10px;
    margin-bottom: 20px;
}
#wuSettings-user LI:first-child {
    border-top-left-radius: 5px;
    border-top-right-radius: 5px;
    cursor: default;
    background-color: #FF6347;
    color: #202020;
    font-weight: bold;
    text-align: center;
    height: 35px;
    line-height: 35px;
    border-left-color: #380061;
    border-bottom: medium none;
}
#wuSettings-user LI A:hover {
    color: #A9A9A9;
    background-color: #4C4C4C;
    border-left-color: #380061;
}
#wuSettings-user LI.no-link {
    line-height: 30px;
    height: 30px;
    background-color: #D5202A;
    font-weight: bold;
    text-align: center;
    color: #202020;
    cursor: default;
}

/* Other Settings */
#wuSettings-quick .button-group {
    margin: 13px 10px;
    background-color: #78AB46;
}
#wuSettings-more {
    display: block;
    height: 40px;
    background-color: #D5202A;
    line-height: 40px;
    padding-left: 10px;
    font-size: 0.9375rem;
    border-top: 1px solid #D7D7D7;
    text-decoration: none;
    color: #202020;
}
#weather-snippet #hi-low .low {
    color: #6082b6;
}
.postContent tr td {
    font-size: 0.875rem;
    padding: 0px;
    font-weight: normal;
    color: #1E2023;
    line-height: 1.5;
}
.comment .pic {
    float: none;
    width: 815px;
    height: 80px;
    padding: 8px 3px 8px 8px;
    min-height: 1px;
}
.alert-bar.defcon-basic .alert-items {
    border-bottom-right-radius: 5px;
    border-top-right-radius: 5px;
    font-weight: bold;
    background-color: #a2add0;
}
#blog-tabs a {
    font-size: 13px;
    background: none repeat scroll 0% 0% #202020;
    padding: 0px 0.8rem;
}
table tr th,
table tr td {
    padding: 0.5625rem 0.625rem;
    font-size: 0.75rem;
    color: #fffafa;
    text-align: left;
}
.wui-tabs.tabs {
    margin: 0px;
    height: 35px;
    overflow: hidden;
    background: none repeat scroll 0% 0% #202020;
    box-sizing: border-box;
    border-bottom: 1px solid #D7D7D7;
    position: relative;
}
.wui-tabs.tabs > DD A {
    border-top: 3px solid #15AADC;
    background: none repeat scroll 0% 0% #202020;
    color: #0074A2;
}
.accordion.wui-accordion DD {
    background-color: #149AC6;
    background-image: linear-gradient(#3A0A62, #927FE1);
}
.accordion .accordion-navigation > .content.active,
.accordion dd > .content.active {
    display: block;
    background: none repeat scroll 0% 0% rgba(98, 0, 158, 0.9);
}
.wui-tabs.ui-tabs > .ui-tabs-nav {
    margin: 0px;
    height: 35px;
    background: none repeat scroll 0% 0% #180202;
    border-bottom: 1px solid #D7D7D7;
    box-sizing: border-box;
}
.wui-tabs.ui-tabs > .ui-tabs-nav .ui-state-active A {
    border-top-color: #8ED1E9;
    color: #4C4C4C;
    border-bottom: 1px solid #FFF;
    background-color: #4A3C6F;
    font-weight: bold;
    height: 35px;
    cursor: default;
}

/* Forecast Temps */
#favorites .favorites-bar UL LI.defcon-5 .fi-info {
    color: #CF4F4F;
}

#curFeel .wx-label {
    font-size: 0.75rem;
    color: #9584B3;
}

#todaySummary .low {
    color: #8BBAEF;
    font-weight: bold;
}

.day-row .temps .low {
    color: #8BBAEF;
    font-weight: bold;
}

.wx-na {
    font-style: italic;
    color: #9584B3;
}

#current H2 {
    border-bottom: none;
    color: #9584B3;
    font-size: 0.75rem;
    margin: 8px 0;
    line-height: 1.4;
}

.day-row .night {
    background-color: #7A6D87;
}
#extented-forecat .day-row .summary .temps .high {
    color: #F64E58;
    font-weight: bold;
}
#extented-forecat .day-row .summary .temps .low {
    color: #00AE9C;
    font-weight: bold;
}
.wx-module > .title {
    border-top-left-radius: 5px;
    border-top-right-radius: 5px;
    padding: 10px 7px;
    background-color: #976AC8;
    border-bottom: 1px solid #D7D7D7;
    font-size: 0.75rem;
    line-height: 1.5;
}

.alert-bar.defcon-basic .alert-items {
    border-bottom-right-radius: 5px;
    border-top-right-radius: 5px;
    font-weight: bold;
    background-color: #398f2d;
}

.alert-bar.defcon-2 {
    background-color: #8c210f;
}

/* Calendar History */
table.calendar-history-table td {
    background-color: #540A90;
}

/* Email */
#wu-mail #inbox table td.read {
    background-color: #6646AE;
}
#wu-mail #inbox table td {
    border: 1px solid #240B0B;
}
#wu-mail #sent-list table td.read {
    background-color: #6646AE;
}
#wu-mail #sent-list table td {
    border: 1px solid #240B0B;
}
#wu-mail #inbox table td.unread {
    background-color: #F50F0F;
}

/*   Weather History */
table.airport-history-summary-table .history-table-grey-header {
    background-color: #744CAE;
    font-weight: bold;
}
table.airport-history-summary-table td {
    background-color: rgb(90, 45, 146);
    font-size: 0.875rem;
}

/*   Hurricane/Typhoon Labels */
.active-hurr-label {
    background-color: #4A3F93;
    padding: 10px;
}

/*    Precipitation Forecast */
.precip-message P {
    margin: 0px 0px 0px 40px;
    padding: 8px;
    background-color: #0F6384;
}

/*  More Map Menu */
.wui-tabs.tabs > DD.active A {
    border-top-color: #8ED1E9;
    color: #4C4C4C;
    border-bottom: 1px solid #FFF;
    background-color: #531C96;
}

.callout {
    margin: 0 0 1rem;
    padding: 1rem;
    border: 1px solid rgba(30, 32, 35, .25);
    border-radius: 3px;
    position: relative;
    color: #1e2023;
    background-color: #5f0f8f99;
}

.city-header {
    background: rgba(112, 128, 144, .2) !important;
    margin-bottom: 10px;
    padding: 20px 0 44px;
    border-top: 1px solid #D7D7D7;
    border-bottom: 2px solid #D7D7D7;
}

.data-module {
    background: rgba(112, 128, 144, .2) !important;
    margin-bottom: 20px;
}

#hourly-forecast-table thead,
#hourly-forecast-table tbody,
#hourly-forecast-table tr,
#hourly-forecast-table td {
    border: none;
    background: rgba(112, 128, 144, .2) !important;
}

.tab-layout[_ngcontent-c21] .subnav-contain[_ngcontent-c21] .subnav[_ngcontent-c21] a.selected[_ngcontent-c21] {
    background-color: #000000;
}

a,
.keyText {
    color: #FFF !important;
}

.accordion.wui-accordion DD.active > A {
    color: #1E2023;
    height: 34px;
    line-height: 34px;
    padding: 0 1rem;
    background-color: #531C96;
    background-position: right center;
    background-repeat: no-repeat;
}

#current .cc2 TABLE TR TD,
#current .ccDetails TABLE TR TD {
    color: #202020;
    font-size: 0.75rem;
    line-height: 1.5;
    vertical-align: top;
    padding: 2px;
    background: rgb(84, 37, 152) !important;
}

#optionBar a,
#grouptitle,
div,
.rfGreen {
    color: #171717 !important;
}

.wx-module > TABLE THEAD TH {
    color: #090909;
}

.left-shift {
    position: relative;
    left: 0%;
}

#extremes-mod UL {
    font-size: 0.75rem;
    background: rgb(84, 37, 152) !important;
}

.column,
.columns {
    padding-left: 0.625rem;
    padding-right: 0.625rem;
    float: left;
    background: rgba(83, 28, 150, 1.8) !important;
    border: 4px solid rgba(0, 0, 0, .2) !important;
}

.data-module .module-header,
.data-module h4 {
    position: relative;
    margin: 0;
    height: 37px;
    line-height: 37px;
    color: #4c4c4c;
    background-color: #976AC8;
    font-size: .9375rem;
    font-weight: 400;
    text-transform: uppercase;
    overflow: hidden;
    padding: 0 60px 0 15px;

}.accordion .accordion-title {
        background-color: #6F43C3;
        border-top: 1px solid #8a8a8a;
        border-right: 1px solid #8a8a8a;
        border-left: 1px solid #8a8a8a;
        padding-right: 45px;
    }

    .accordion ul.accordion-content li a {
        display: block;
        padding: .65rem 1rem;
        background-color: #320760;
        
}table[_ngcontent-c27] tbody[_ngcontent-c27] th[_ngcontent-c27], table[_ngcontent-c27] tbody[_ngcontent-c27] td[_ngcontent-c27] {
    padding: 0.2rem 0.625rem;
    font-size: 0.75rem;
    color: #0E0D0D;
    text-align: left;
}
   table tr th, table tr td {
    padding: 0.5625rem 0.625rem;
    font-size: 0.75rem;
    color: #b3a6a6;
    text-align: left;
    background-color: rebeccapurple;
        
}.cat-six-about-author[_ngcontent-c5] {
    margin-bottom: 20px;
    padding: 25px 0 20px 0;
    display: block; 
    background-color: #522d80;
    
}stationselector_table[_ngcontent-c22] TR[_ngcontent-c22] TD[_ngcontent-c22] {
    background-color: #603CA5 !important;
    padding: 3px 5px  
        
    }
}
