css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.shoprbc.com") {
body {
  background:#e9e9e9 url(http://i.imgur.com/lNoKuKf.png) repeat-x;
}

.productHeader {
  background-image:none;
  padding-top:0;
}
  div.promoLiquidZone {
    width: 100% !important;
  }
  div.promoLiquidItem {
    width: 49%;
    text-align: center;
  }
.promoLiquidItem div div div p {
  padding: 4px 10px 10px 10px !important;
}
  div.promoLiquidItem div {
    width: 100% !important;
  }
  div.promoLiquidItem div div {
    width: 100% !important;
  }

.promoLiquidItem {
  background-image:none;
}

.panelRightOuter {
  background:none!important;
  display:none;
  padding:0;
}

td.panelCenter {
  margin-top:50px!important;
  background:#FFF!important;
  border:1px solid #cacaca;
  padding:10px;
}

#productCategoryMenu a,#productCategoryMenu h2 {
  font:normal 11px/16px arial,helvetica,sans-serif!important;
  color:#FFF!important;
  display:block!important;
  border:none!important;
  border-left:4px solid #1F7D8E!important;
  background:#2E489C!important;
  margin:0!important;
  padding:5px!important;
}
  
  .expand::after {
    font-size: 70%;
    color: #1F7D8E;
    float: right;
    content: 'â–º';
  }

#productCategoryMenu a:hover {
  background:#1F7D8E!important;
}

body,h1,h2,h3,p,font,table,td,th,ul,ol,textarea,input,form,blockquote,select {
  font-family:'Open Sans', sans-serif;
  font-size:11px;
  font-weight:400;
  color:#000;
  text-decoration:none;
}

.cells-inner-td-advanced a {
  text-decoration:none!important;
}

#ShopRbcModulesProductsSimpleSearchForm_keywords {
  width:300px!important;
  font-size:14px;
  font-weight:700;
  border:none!important;
  border-bottom:solid 4px #1F7D8E!important;
  transition:border .5s;
  padding:10px;
}

#ShopRbcModulesProductsSimpleSearchForm_keywords:focus,#ShopRbcModulesProductsSimpleSearchForm_keywords.focus {
  border-bottom:solid 4px #6E82C2 !important;
}

textarea:focus,input:focus {
  outline:0;
}

.navHeader-inner {
  top:3px!important;
  left:-110px!important;
}

#access-area-visitor > div > div > table > tbody > tr > td.pageOutline > table:nth-child(4) > tbody > tr:nth-child(2) > td {
  height:15px;
  background:none!important;
}

#shopRbcProductsSearchResultsOrder {
  background:#FFF!important;
  margin:-2px!important;
}

.listingOutline {
  background:#fff!important;
  margin:0!important;
  padding:0!important;
}

.sechead table {
  border:none;
  padding:4px!important;
}

td.sechead {
  background:#1F7D8E;
  
  font-family:Oswald, sans-serif!important;
  font-size:15px;
  border:none!important;
  font-variant:small-caps;
}

#shopRbcProductsSearchResultsOrder > table:nth-child(4) {
  margin-top:-10px!important;
  line-height:2em;
  padding:0 5px 5px!important;
}

td.listingData {
  vertical-align:middle!important;
  font-size:140%;
  background:#FFF!important;
  padding:15px!important;
}

.listingData a {
  padding:3px;
}

.listingData a:link,.listingData a:visited {
  color:#000;
  text-decoration:none;
}

#access-area-visitor > div > div > table > tbody > tr > td.pageOutline > table.print-doHide > tbody {
  background:#2E489C;
}

.cells-inner-td {
  background:none!important;
  margin:0!important;
  padding:0!important;
}

.promoLiquidZone {
  width:620px!important;
  margin:auto!important;
}

div.promoLiquidItem div div div.productHeader {
  background:#1F7D8E!important;
}

#access-area-visitor > div > div > table > tbody > tr > td.pageOutline > table:nth-child(5) > tbody > tr > td.panelCenter > table:nth-child(4) > tbody > tr:nth-child(1) > td > table > tbody > tr:nth-child(2) > td > form > div > table > tbody > tr > td {
  background:#0D6677!important;
  padding:6px!important;
}

.profileFrame {
  width:100%!important;
}

.profileHeading {
  background:#1F7D8E!important;
  color:#FFF!important;
  padding:5px!important;
}

.profileData {
  font-size:14px!important;
}

#ShopRbcModulesProductsSimpleSearchForm > fieldset > table {
  margin-right:8px;
}

.pageBorderLeft,.pageBorderRight,#access-area-visitor > div > div > table > tbody > tr > td.pageOutline > table:nth-child(5) > tbody > tr > td.panelCenter > div > table > tbody > tr:nth-child(1),td.panelCenter h1 img,.stockLocationsNotice,.panelLeftOuter .bannerWrapper,.panelRightOuter .bannerWrapper,#access-area-visitor > div > div > table > tbody > tr > td.pageOutline > table:nth-child(4) > tbody > tr:nth-child(2) > td > img,td.cells-inner-td.cells-inner-td-continue,img.print-doHide,#shopRbcProductsSearchResultsOrder > table:nth-child(4) > tbody > tr:nth-child(1),#shopRbcProductsSearchResultsOrder > table:nth-child(n + 5) > tbody > tr:nth-child(2) > td > table > tbody > tr > td > table > tbody > tr:nth-child(1),#shopRbcProductsSearchResultsOrder_compareBottom,.listingData br,.listingData input,.cells-inner-td-advanced, table.print-doHide > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(5) > img:nth-child(1), table.print-doHide > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > img:nth-child(1), table.print-doHide > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(4) > img:nth-child(1) {
  display:none;
}
  
table.print-doHide > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(2) > a:nth-child(1) > img:nth-child(1) {
  /* Logo pt1*/
  margin-left: -55px;
  width:0 !important;
  height: 0 !important;
  padding: 77px 209px 0 0;
  background: url(http://i.imgur.com/xmKkAN1.png);
}
  table.print-doHide > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(3) > img:nth-child(1) {
    /* logo pt2 */
  margin-left: -55px;
    width:0 !important;
    height: 0 !important;
    padding: 77px 63px 0 0;
    background: url(http://i.imgur.com/UnE89jM.png);
  }
  
#access-area-visitor > div > div > table > tbody > tr > td.pageOutline > table.print-doHide > tbody > tr:nth-child(1) > td:nth-child(3) {
  width: 370px !important;
}

.panelLeftInner,.panelLeftOuter,.panelRightInner {
  background:none!important;
  padding:0;
}

table.print-doHide [style],#access-area-visitor > div > div > table > tbody > tr > td.pageOutline > table:nth-child(6) > tbody > tr > td {
  background:none!important;
}

.navHeader-inner a,.navHeader-inner a:hover {
  color:#FFF;
  font-weight:400;
  text-decoration:none;
}

#shopRbcProductsSearchResultsOrder td,.listingFrame {
  margin:0!important;
  padding:0!important;
}

#access-area-visitor > div > div > table > tbody > tr > td.pageOutline > table:nth-child(4) > tbody > tr:nth-child(1) > td > table > tbody > tr,#access-area-visitor > div > div > table > tbody > tr > td.pageOutline > table:nth-child(4) > tbody > tr:nth-child(1) > td {
  background:#0D6677!important;
}

#notImportant,.tabbedContent {
  width:100%;
}
}
