css:
@-moz-document domain("vk.com") {
.wall_module .post_info {
	margin-left: 0;
	margin-right: 60px;
	direction: rtl;
	text-align: right;
}

.wall_module div.post_image {
	right: 0;
}

.wall_text_name {
	
}

.page_fronly {
	margin-right: 4px;
}

.wall_module .reply_info {
	margin-left: 0;
	margin-right: 39px;
}

.wide_wall_module .reply_info {
	margin-left: 0;
	margin-right: 60px;
}

.post_full_like_wrap {
	float: left;
	direction: ltr;
}

.post_full_like {
	right: auto;
	left: 0;
}

.post_like, .post_share {
	float: left;
}

.wall_module .reply_link_wrap {
	direction: ltr;
}

.wall_module .media_desc .checkin {
	background-position: 4px -165px;
	float: right;
}

.wall_module .reply .like_wrap {
	float: left;
}

.wall_module .reply_box_open .reply_form {
	margin-left: 0;
	margin-right: 38px;
}

.wide_wall_module .reply_box_open .reply_form {
	margin-left: 0;
	margin-right: 62px;
}

.emoji_smile {
	right: auto;
	left: 0;
}

.wide_wall_module .reply_field_tail {
	display: none;
}

.wall_module .post_actions_wrap, .wall_module .reply_actions_wrap {
	float: left;
}

.wall_module .info_footer {
	direction: ltr;
}

.wall_module .bp_post .like_link, 
.wall_module .reply_info .like_link, 
.wall_module .pv_comment .like_link, 
.wall_module .mv_comment .like_link {
	float: right;
}
}
