css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bbc.co.uk") {
/* Notes
    std "blue" colour rgb(237, 237, 237) is close to Firefox background
*/

/* Recolors */
div#range-top-stories.top-stories-range-module,
div#features.feature-generic,
div#most-popular,
div.top-index-stories,
div#hypertab.hypertabs div.hypertab-container ul,
ul#sub-nav.nav,
div.dna-comments_module.preset_opinion {
    background-color: #ccccff !important;
    border-color: #ccccff !important;
    }

/* changes consequential on changing background colour */
p.dna-moderation-type.secondary_body {
    color: #333333 !important;
    }

/* Asides */
div.story-feature.narrow,
div.story-feature.wide,
div.story-feature.related.narrow,
div.embedded-hyper div.hyperpuff {
    background-color: #ccccff !important;
    padding: 5px;
    margin: 0px 5px 5px 5px;
    }

/* captions - distinguish from text */
div.caption span, p.caption {
    font-size: smaller !important;
    color: #333399 !important;
    }

/* Removing unwanted clutter */
div#bbccookies, 
div#top-share-toolbar,
div#bottom-share-toolbar,
div#page-bookmark-links-foot,
div#news-services,
div#blq-foot-blocks img {
    display: none !important;
    }
    
/* Hide log-in name */
#blq-idstatus-text {  /* news site */
    display: none !important;
    }
div.orb-nav-section:nth-child(3) { /* radio & TV */
    display: none !important;
    }

/* Allow Enlargement of comments box */
  .cmts-post-box {resize: vertical; line-height: 120%; height: 150px;}

/* allow wordwrap in comments */
  .cmts-list li {word-break:  unset !important;}    
/* increase stand-out of elements */
  h2.story-body__crosshead {color: #CC0101;}
    
}
