css:
@-moz-document domain("d2l.arizona.edu") {
*{
 -webkit-transition: all 0.5s; /* Safari */
    transition: all 0.5s;   
}

.d2l-background-left, .d2l-background-right{
 	background-image:none !important;
  	background-color:white;
  
}
  .d2l-navbar-container.d_nb_cFull{
	background-image:none !important;
    background-color : #C80000 !important;
	border:none !important;
    color : #202020;
    box-shadow: 1px 2px 4px #808080;
    padding: 10px 10px 2px 10px;
    
}

.d2l-navbar-container{
	background-image:none !important;
    background-color : #C80000 !important;
	border:none !important;
    color : #202020;
    padding: 10px 10px 2px 10px;
    
}
.d2l-page-title{
   font-weight:bolder;
    font-variant:small-caps;
    font-size : 34px;
}

#d2l_navbar .d2l-navbar-title {
	color: #FFFFFF; 
    font-family:sans-serif;
    box-shadow: 1px 2px 4px #808080;
    padding : 2px 2px 2px 30px;
  	
}  
    
.d2l-heading{
	color: #202020; 
    font-family: sans-serif;
    
}

.d2l-htmlblock{
  	color: #202020 !important;
}

.d2l-widget-header{
   	background-image:none !important;
    background-color : #000099 !important;
    color : #FFFFFF;
    border : none;
    box-shadow: 1px 2px 4px #808080;
    
} 
    

 .d2l-menuflyout-group-container{
    background-color:rgba(0,0,0,0);
}


        
.d2l-minibar{
  background-color : #000066;
    color : #FFFFFF !important;
    border : none !important;
    box-shadow: 1px 2px 4px #404040 !important;
  }  
  
.d2l-datalist-item-content, .d2l-twopanelselector-padding{
    color : #202020;
    border : none;
    box-shadow: 1px 2px 4px #808080;
    padding:10px;
}


  
.d2l-datalist-item-content:hover {
    color : #202020;
    border : none;
    box-shadow: 1px 2px 10px #404040;
    background-color:white !important;
    
}


.d2l-widget-content{
    color : #202020;
    border : none;
    background-color:white !important;
	    box-shadow: 1px 2px 4px #808080;

}

.d2l-widget-content:hover {
    color : #202020;
    border : none;
    box-shadow: 1px 2px 10px #404040;
    background-color:white !important;
    
}

.d2l-page-main, .d2l-homepage, .d2l-widget{
    color : #202020;
    border : none;
    background-color:white !important;
	z-index:99;
    padding:none !important;

}

.d2l-page-main-padding, .d2l-background-homepage, .d2l-twopanelselector-wrapper{
	border : none !important;
    background-color : white !important;
	background-image : none !important;
    padding : 0px 0px 0px 0px !important;
    margin-top : 20px;
}

.d2l-navbar-link span,
#d2l_navbar .d2l-menuflyout span,
.d2l-datalist-item-actioncontrol{
	background-color : none !important;
	border : none !important;
	color : darkblue !important;
    
}
.d2l-navbar-link,
.d2l-navbar-item a,
.d2l-menuflyout a{
	background-color : none !important;
	border:none !important;
    color:white;
}
.d2l-navbar-link span:hover,
.d2l-navbar-item a:hover,
.d2l-menuflyout span:hover,
.d2l-menuflyout a:hover{
	background-color : lightgrey !important;
	border : none !important;
	color : darkblue !important;
}

 #d2l_navbar .d2l-navbar-sep{
	background-image:none !important;
    background-color : none !important;
    border : none !important;
}
}
