css:
@-moz-document domain("steamcommunity.com"), domain("steampowered.com") {
body,
textarea,
#global_header,
input[name=text],
.blotter_group_announcement_content
{ font-family: Motiva Sans, Meiryo !important; }

#header_wallet_balance{
  padding: 2px 4px !important;
  font-family: monospace;
  background: black; border: 1px solid rgba(255,255,255,0.5);
}

.game_area_details_specs > a[href*="2=22"],
.game_area_details_specs > a[href*="2=29"]
{
  color: white;
  background: linear-gradient(to bottom, #b377cd, #8f47b3);
}

.game_area_details_specs > a[href*="2=22"]:hover,
.game_area_details_specs > a[href*="2=29"]:hover
{
  color: white;
  background: linear-gradient(to bottom, #c7a3d9, #8f47b3);
}

.chart-stat .num{ font-family: "Roboto Condensed", Arial Narrow; }

.profile_ban_status{
    padding: 4px 0 4px 8px; line-height: 1.25;
    color: #faa !important; font-size: 11px;
    background: #141414; border-radius: 4px;
}
.profile_ban_info{ color: inherit !important; }

.profile_ban:before{
    margin: 8px 2em; padding: 2px 6px 0 6px; display: inline-block;
    font: bold 30px/1 "Courier New", Courier, monospace;
    color: red; border: 3px solid red; border-radius: 4px;
    content: 'CHEATER'; -webkit-transform: rotate(-5deg);
}
}
