css:
@-moz-document domain("xqwh.org") {
html,body,tr{
  font-family: "微软雅黑", Helvetica, Arial, sans-serif;
  color: #00000;
  background-color: #C1E6EA;
  font-size: 13px;
  margin: 0;
  WIDTH: 100%;
}
#wrapcentre {
  padding: 0 20px;
  min-width: 640px;
  width: 960px;
  margin: 0 auto;
}
#logodesc {
  background-color: #F9CCDE;
  background-repeat: repeat-x;
  background-position: center bottom;
}

/* background */
#pagecontent td{background-color: #84CAD4}
tr.nav, .cat {
  background-color: #87CDD7!important;
}

.tablebg {
  background-color: #7CC9D3;
}
.post{BORDER-BOTTOM: #7CC9D3 1px solid;}
h2 {background-color: #84CAD4;padding:10px;}
.panel table,.panel table div,.leftpanel,.rightpanel {background-color: #C1E6EA;padding:10px;}
.panel table{BORDER: #7CC9D3 1px solid!important; }
.row1,.row2,.row3 { 
  background-color: #C1E6EA!important;
}
.cat {
  background-color: #89D1DF;
}
th {
  color: #000000;
  background-color: #9CDBE2;

}
.bg2,.postbody,.content,.genmed{background-color: #C1E6EA;}
.gensmall {
  text-align: left;
  margin: 1px 1px;
  font-size: 1.2em;
}
a.topictitle {
  margin: 1px 0;
  font-family: "微软雅黑", Helvetica, Arial, sans-serif;
  font-size: 16px;
  color: #000;
}
.row2 span { color: #57b6a1!important;}
/* Links */

a:link {
	color: #000;
	text-decoration: none;
}

a:active,
a:visited {
	color: #333;
	text-decoration: none;
}

a:hover {
	color: #57b6a1;
	text-decoration: none;
}

a.prefixlink {
	color: #069;
	font-size: 1.3em;
}

a.forumlink {
	color: #069;
	font-weight: bold;
	font-size: 1.3em;
}
a.topictitle {
	color: #000;
}

a.topictitle:visited {
	color: #666;
	text-decoration: none;
}

a.topictitle:hover {
	color: #57b6a1;
	text-decoration: none;
}
.topicrcmd {color:#ff0000!important;}
th a,
th a:visited {
	color: #333 !important;
	text-decoration: none;
}

th a:hover {
	text-decoration: none;
	color: #fc158c;
}
}
