@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.animeid.tv") {
/*---------------LOGO---------------*/
#logo
{
    background-image:url("http://k46.kn3.net/59E6DE121.png");
}
   
 /*---------------凸(-_-)凸 ADS---------------*/
#anime aside,.status-right #adl,aside#sky,div.a728x90
{
    display:none;
}

/*---------------MAIN---------------*/
*
{
    text-shadow:none!important;
    box-shadow:none!important;
    border-radius:0px!important;
}
/* Share */
#share ul li a
{
    background-color:#202020;
}
/*---------------SEARCH---------------*/
ul#jSearch
{
    background-color:#202020;
}
ul#jSearch li.itemBox a.ui-state-hover
{
    background-color:#151515;
}
ul#jSearch li.odd
{
    background-color:#14171A;
}
ul#jSearch li.even
{
    background-color:#181D20;
}
ul#jSearch li.even,ul#jSearch li.odd
{
    border-top:1px solid #181818;
    border-bottom:1px solid #181818;
}
aside#share
{
    background-color:#202020;
}
html
{
    color:#6A6F74;
}
/*---------------Busqueda por genero---------------*/
.list article.item:nth-child(2n)
{
    background-color:#181818;
}
.list article.item
{
    border-bottom:1px solid #151515;
    border-top:1px solid #151515;
}
/*---------------HEADER-background---------------*/
header.main,html
{
    background:#151515;
    background-image:none;
}
/*---------------MAIN---------------*/
div.main
{
    background:none repeat scroll 0% 0% #202020;
}
/* Letras - Generos */
ul#letras,ul#generos
{
    background-color:#191919;
}

#letras li, #generos li
{
    background:none repeat scroll 0% 0% transparent;
    border:1px solid #606060;
}
#letras li a, #generos li a
{
    color:#ccc;
}
    #letras li a:hover, #generos li a:hover
{
    background:none repeat scroll 0% 0% #FEE557;
    color:#000;
}
a.swShowPage
{
    background-image:url("http://k30.kn3.net/00512E7E6.png")
}
    /* Caps más vistos, en emisión, agregados recientemente */
.top12 ol, .series ol
{
    border:1px solid #606060;
}
.top12 ol li, .series ol li    
{
    background-color:#191919;
}
.top12 ol li:nth-child(2n), .series ol li:nth-child(2n)
{
    background-color:#202020;
}
.top12 ol a:hover, .series li a:hover
{
    background-color:#2C2C2C;
}
    
.series header.tabs .tab
{
    border:1px solid #606060;
}    
.series header.tabs .tab.active
{
    background-color:#181818;
}
.series header.tabs .tab:hover
{
    background-color:#232323;
}
.lastcap article a .mask
{
    background:url("http://k46.kn3.net/C56BD52B5.png") no-repeat scroll left top transparent;
}   
/* TOP 5 */
.topfive article
{
    background-color:#181818;
    border:1px solid #606060;
}
.topfive article header
{
    background-color:#2C2C2C;
    color:#606060;
}
/*---------------Ancho total---------------*/
section.main
{
    width:940px!important;;
}
#letras, #generos
{
    width:380px!important;;
}
.lastcap article
{
    width:250px!important;;
    margin:1px 30px 2px!important;;
}
.topfive article 
{
    width:225px!important;;
    margin:5px!important;;
}
/*---------------ANIME---------------*/
#anime figure .mask
{
    background-image:url("http://k46.kn3.net/C56BD52B5.png");
}
#anime hgroup
{
    color:#5A5F64;
}
#anime section .sinopsis
{
    color:#7A7F84;
}
.tag
{
    background:none repeat scroll 0% 0% #151515;
    border:1px solid #333333;
    color:#ccc;
}
.tag:hover
{
    color:#FEE557;
}
/* CAPS */
#infoanime figure .mask,#related .rel .mask,article.item .mask
{
    background-image:url("http://k33.kn3.net/C7C048359.png");
}
#infoanime a
{
    color:#FEE557;
}
#infoanime a:hover
{
    color:#545C65;
}
#infoanime p
{
    color:#6A6F74;
}
.buttons .b a.n
{
    background:none repeat scroll 0% 0% #181818;
    border-color:#252C33;
}
.buttons .b a
{
    background:none repeat scroll 0% 0% #181818;
    border-color:#252C33;
    color:#00AB4E;
}
.buttons .b a:hover
{
    background:none repeat scroll 0% 0% #151515;
    border-color:#2c2c2c;
    color:#FEE557;
}
/* MIRRORS */
#mirrors li.tab
{
    background:none repeat scroll 0% 0% #282D30;
    border-color:#505050;
}
#mirrors li.tab:hover
{
    background:none repeat scroll 0% 0% #232323;
}
#mirrors li.tab.active
{
    background:none repeat scroll 0% 0% #151515;
}
/* PARTES */
#partes .container
{
    background:none repeat scroll 0% 0% transparent;
}
#partes .subtab .parte
{
    background:none repeat scroll 0% 0% #1C394C;
}
/* DESCARGAR*/
#adl a
{
    background:none repeat scroll 0% 0% #151515;
    color:#818587;
}
/* ----Relacionados---- */
.cuerpo li.reli
{
    height:26px;
}
.cuerpo li.reli:nth-child(2n+1)
{
    background:none repeat scroll 0% 0% #191919;
    border:1px solid #6e5500;
}
.cuerpo li.reli a
{
    color:#FEE557;
    
}
/* DATOS */
#capitulos hr, .main hr.cap,.main hr
{
    border-color:#606060 -moz-use-text-color -moz-use-text-color;
}
.cuerpo div:nth-child(2n+1)
{
    background:none repeat scroll 0% 0% #191919;
}
/* ----Relacionados---- */
#procap
{
    background-color:#191919;
    color:#E01818;
    border:1px solid #E01818;
}
/* LISTA DE CAPITULOS */
#paginas ul.pagination li a, #paginas ul.pagination li.dot, #capitulos header .right
{
    background:none repeat scroll 0% 0% #151515;
}
#capitulos header .right:hover
{
    background:none repeat scroll 0% 0% #21262A;
}
#listado li
{
    border-bottom:1px solid #252C33;
    border-top:1px solid #252C33;
}
#listado li a:hover
{
    color:#12A9FA;
}
#listado li:nth-child(2n)
{
    background:none repeat scroll 0% 0% #191919;
}
#listado li:hover
{
    background:none repeat scroll 0% 0% #2C2C2C;
}
#listado li a:visited
{
    color:#70BF53
}
#listado li a::before
{
    box-shadow:none;
    border-radius:0px;
}
/*---------------FOOTER---------------*/
footer.main
{
    background:none repeat scroll 0% 0% #151515;
}
}
